		];
		var iconNum = (' 4' == "5; 4; 3; 2; 1; 0") ? 5 : ' 4';
		var globalLinksHeader = "", globalLinks = "";

		for(var i = 0; i < iconNum; i++) {
			var linkCheck = (menuGlobalIcons[i][1] == "[SITECALENDARLINK]") ? "/Page/2" : menuGlobalIcons[i][1];
			globalLinks += "1, " + menuGlobalIcons[i][0] + ", " + linkCheck + ", _self;";
		}

		if(iconNum > 0) {
			globalLinksHeader = "Popular Links";
		}

		$("body").csRsMenu({
			"breakPoint"		: 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			"slideDirection"	: "left-to-right", // OPTIONS - left-to-right, right-to-left
			"menuButtonParent"	: "#gb-global-icons-outer",
			"showMenuBtnText"	: "yes",
			"menuBtnText"		: "MENU",
			"showSchools"		: "no",
			"showTranslate"		: useTranslate,
			"translateId"		: "none",
             "advancedTranlate": {
             	"useAdvancedList": "yes"
                },
			"showAccount"		: "yes",
			"extraMenuOptions"	: globalLinksHeader, // EXTRA MENU HEADING NAMES SEPARATED BY A ;
			"extraMenuItems"	: globalLinks, // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
			"siteID"		: "1"
		});

		$("body").csRsPagelistMenu({
		        "breakPoint"            : 640, // SYSTEM BREAK POINTS - 768, 640, 480, 320
		        "slideDirection"        : "left-to-right", // OPTIONS - left-to-right, right-to-left
		        "menuButtonParent"      : "#sp-content",
		        "showMenuBtnText"       : "yes",
		        "menuBtnText"           : "PAGES",
		        "siteID"                : "1"
		});

        //VIEW FULL SITE BUTTON ON MOBILE
		if('false' == "true") {
			$("#gb-page").addClass("using-full-site-btn");
			$("body").viewFullSite({
				"breakPoint"		: 639, // SYSTEM BREAK POINTS - 1023, 767, 639, 479
				"buttonContainer"	: "body",
				"useDefaultCSS"		: "yes",
				"fixedPosition"		: "bottom" // IF USING DEFAULT STYLES - TOP, BOTTOM
			});
		}

		// GLOBAL ICONS
		$("#gb-global-icons").creativeIcons({
		        "iconNum"       : iconNum, // NUMBER OF ICONS
		        "icons"         : [
		                ["Student Program/16.png", "Menus", "https://www.slps.org/Page/709", "_self"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
		                ["/cms/lib/MO01001157/Centricity/Template/GlobalAssets/icons/find-your-school-32X32.png", "Find Your School", "https://www.stlouis-mo.gov/live-work/school-search-by-address.cfm", "_blank"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
		                ["Information Technology/1.png", "Parent Portal", "https://sis.slps.org/", "_blank"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
		                ["Site Utility/1.png", "Calendar", "https://www.slps.org/calendar", "_self"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
		                ["Audio  Visual/12.png", "Videos", "https://www.slps.org/site/Default.aspx?PageID=23241", "_self"] // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
		        ],
		        "siteID"        : "1",
		        "siteAlias"     : "slps",
		        "calendarLink"  : "/Page/2",
		        "contactEmail"  : "tom.booth@slps.org",
		        "allLoaded"     : function(){
		        	$(".global-icon").first().addClass("first");
				$(".global-icon").last().addClass("last");
		        }
		});
		if(iconNum == "0") {
			$("#gb-global-icons").hide();
		} else {
			$("#gb-global-icons").addClass("items-" + $.trim(iconNum));
		}

		pgScreenSize();
		useChannelForSection();
		dynamicContent();

		if(!$("html").hasClass("ie8")) {
			if(jaggedEdges == "true"){
				bgRaggedInit("#333333");
			}
			if(globalIconsBG == "true") {
				gbIconsBgInit();
			}
		}		
	});	

	function pgScreenSize(){
		switch(csGetBreakPoint()) {
			case "desktop": 
				photoGalleryOne();
				photoGalleryTwo();
				photoGalleryThree();
				setLogoW();
				//DOC move passkeys button
				if($("#sw-mystart-mypasskey").length) {
					$("#sw-mystart-right").prepend($("#sw-mystart-mypasskey"));
				}
			break;
			case "768":
				photoGalleryOne();
				setLogoW();
				appView768();
				//DOC move passkeys button
				if($("#sw-mystart-mypasskey").length && !$("#sw-mystart-left #sw-mystart-mypasskey").length) {
					$("#sw-mystart-left").append($("#sw-mystart-mypasskey"));
				}
			break;
			case "640":
				photoGalleryOne();
				setLogoW();
				appView768();
				//DOC move passkeys button
				if($("#sw-mystart-mypasskey").length && !$("#sw-mystart-left #sw-mystart-mypasskey").length) {
					$("#sw-mystart-left").append($("#sw-mystart-mypasskey"));
				}
			break;
			case "480":
				photoGalleryOne();
				setLogoW();
				appView480();
				//DOC move passkeys button
				if($("#sw-mystart-mypasskey").length && !$("#sw-mystart-left #sw-mystart-mypasskey").length) {
					$("#sw-mystart-left").append($("#sw-mystart-mypasskey"));
				}
			break;
			case "320":
				photoGalleryOne();
				setLogoW();
				appView480();
				//DOC move passkeys button
				if($("#sw-mystart-mypasskey").length && !$("#sw-mystart-left #sw-mystart-mypasskey").length) {
					$("#sw-mystart-left").append($("#sw-mystart-mypasskey"));
				}
			break;
		}
	}

