    function jsMediaQueries() {
		switch(csGetBreakPoint()) {
        	case "desktop":
            	pagelistAccordionReset();
            break;
            case "768":
            	pagelistAccordionReset();
            break;
			case "640":
				responsiveNav();
                pagelistAccordionSet();
                adjustMystart();
			break;
			case "480":
				responsiveNav();
                pagelistAccordionSet();
                adjustMystart();
			break;
			case "320":
				responsiveNav();
                pagelistAccordionSet();
                adjustMystart();
			break;
		}
	}

	function photoGallery() {
		head.ready("photoGallery", function(){
			if(!$("#sw-content-container10.region.ui-hp .rs-photo-gallery-container").length){
            	// PHOTO GALLERY PLACEHOLDER
                if(!$("#sw-content-container10.ui-hp div.ui-widget.app.gallery.json").length) {
                    $("#sw-content-container10.ui-hp").html("


