                    "contMoreLink"              : "no",
                    "contMoreLinkText"          : "Read More",
                    "descMoreLink"              : "no",
                    "descMoreLinkText"          : "Description Read More",
                    "bullets"                   : "no",
                    "bulletControls"            : "no",
                    "thumbnails"                : "no",
                    "thumbnailWidth"            : 122,
                    "thumbnailHeight"           : 53,
                    "thumbnailSlideDirection"   : "horizontal", // horizontal or vertical
                    "hoverPause"                : "yes",
                    "fullScreenRotator"         : "no", // image dimensions work well at 1500x915
                    "fullScreenBreakpoints"     : ["960", "768", "640", "480", "320"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
                    "autoRotate"                : "yes",
                    "transitionType"            : "fade", // fade or slide
                    "transitionSpeed"           : 2,
                    "onImageLoad"               : function() { },
                    "allImagesLoaded"           : function() { },
                    "onThumbnailLoad"           : function() { },
                    "allThumbnailsLoaded"       : function() { },
                    "onTransitionStart"         : function() { },
                    "onTransitionEnd"           : function() { },
                    "allLoaded"                 : function() { }
				});
			}
		});
	}

	function dynamicContent() {
    	// ********** MYSTART ********** //
        if(false == true) $(".sw-mystart-button.home").show();

		// ********** HEADER ********** //
		// ADD LOGO
		var logoCheck = true;
		var logoSrc = $.trim("/cms/lib/MT01001600/Centricity/Template/GlobalAssets/images///logos/logo1.png");
		if(logoCheck) {
			var srcSplit = logoSrc.split("/");
			var srcSplitLen = srcSplit.length;
			if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
				$("#gb-logo").append('').removeClass("hidden");
			} else {
            	$("#gb-logo").append('').removeClass("hidden");
            }
		}

        // ADD TAGLINE - ADD WITH JQUERY SO THAT THERE IS NOT AN EMPTY H2
        if(true == true) {
        	$("#gb-sitename").append('
Achieving Excellence');
        }

		// ********** BODY ********** //
		// AUTO FOCUS SIGN IN FIELD
		$("#swsignin-txt-username").focus();

        // RENAME FIRST BREADCRUMB
        $("li.ui-breadcrumb-first > a > span").text("Home");

		// CENTER SIGNED OUT MESSAGE AND SIGNIN BUTTON
        if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
            $("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
            //DOC add signed out breadcrumb
            $(".ui-breadcrumbs").append("
Signed Out");
        }

        // ADJUST SIGN IN PAGE
        if($("#swlogin").length){
            $("#sw-content-layout-wrapper .ui-widget.app").addClass("signin");
            $(".ui-breadcrumbs").append("
Sign In");
        }

        // ADJUST REGISTER PAGE
		if($("#swageprompt-txt-birthmonth").length && $("#swageprompt-txt-birthday").length && $("#swageprompt-txt-birthyear").length) {
			$("#sw-content-layout-wrapper .ui-widget.app").addClass("register");
			$(".ui-breadcrumbs").append("
Register");
		}

		// ADJUST SEARCH RESULTS PAGE
		if($("#sw-content-layout-wrapper.ui-spn #swsearch-pnl-main").length) {
			$("#sw-content-layout-wrapper").children().wrapAll('

');
			$(".ui-breadcrumbs").append("
Search Results");
		}

        // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
        $("div.ui-widget.app .ui-widget-detail img")
            .not($("div.ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail img"))
            .not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
            .each(function() {
                if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                    $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                }
        });
	}

    function adjustMystart() {
    	if(($(".sw-mystart-button.manage").length || $(".sw-mystart-button.pw").length) && (!$("#sw-mystart-account").hasClass("extra-items-loaded"))) {
        	var myViewBtn = ($(".sw-mystart-button.pw").length) ? '
' + $(".sw-mystart-button.pw").clone().html() + '': ''
            var siteManagerBtn = ($(".sw-mystart-button.manage").length) ? '
' + $(".sw-mystart-button.manage").clone().html() + '' : '';
            $("#sw-myaccount-list").prepend(myViewBtn + siteManagerBtn);
            $("#sw-mystart-account").addClass("extra-items-loaded");
        }
    }

    function responsiveNav() {
    	if(!csRsNavExists) {
        	// START BUILDING THE MENU STRUCTURE
        	var rsMenuItems = "";
        	rsMenuItems += '
';
            rsMenuItems +=	'Menu';
            rsMenuItems +=	'';

            // LOOP THROUGH THE CHANNEL ITEMS AND BUILD THE LIST ITEMS
            $(".sw-channel-item").each(function() {
                rsMenuItems += '
 a", this).attr("href") + '">' + $("> a span", this).text() + '';

                // IF A SECTION DROPDOWN EXISTS
                if($(".sw-channel-dropdown", this).length) {
                    // LOOP THROUGH THE SECTION ITEMS AND BUILD THE LIST ITEMS
                    $(".sw-channel-dropdown li", this).each(function() {
                        rsMenuItems += '
 a", this).attr("href") + '">' + $("> a span", this).text() + '';
                    });
                }
            });

            // FINISH BUILDING THE MENU STRUCTURE
            rsMenuItems +=	'';
            rsMenuItems += '';

            // ADD THE MENU TO THE DOM
        	$("#gb-channel-list").prepend(rsMenuItems);

            // BIND MENU EVENTS
            // @param (menu container, menu button, menu dropdown)
            setMenuEvents("#rs-menu-container", "#rs-menu-button", "#rs-menu-dropdown");

            // MENU NOW EXISTS
            csRsNavExists = true;
        }
    }

    function setMenuEvents(parent, button, dropdown) {
    	// BUTTON CLICK EVENT
        $(button).click(function(e) {
            e.preventDefault();

            $(parent).toggleClass("open");
            $(dropdown).toggle().attr("aria-hidden", ($("~ ul", this).attr("aria-hidden") == "true") ? "false" : "true");
        });

        // BUTTON KEYDOWN EVENTS
        $(button).keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME SPACE AND ENTER KEYS
                case csKeyCodes.space:
                case csKeyCodes.enter:
                    e.preventDefault();

                    // TOGGLE THE MENU
                    $(parent).toggleClass("open");
                    $(dropdown).toggle().attr("aria-hidden", ($(dropdown).attr("aria-hidden") == "true") ? "false" : "true");

                    if($(parent).hasClass("open")) {
                        // FOCUS THE FIRST LINK
                        $(dropdown + " > li:first-child > a").focus();

                        // ADJUST ARIA ATTRIBUTES
                        $(dropdown).attr("aria-hidden", "false");
                    } else {
                        // ADJUST ARIA ATTRIBUTES
                        $(dropdown).attr("aria-hidden", "true");
                    }
                break;

                // CONSUME DOWN ARROW KEY
                case csKeyCodes.down:
                    e.preventDefault();

                    // SHOW THE MENU
                    $(parent).addClass("open");
                    $(dropdown).show().attr("aria-hidden", "false");

                    // FOCUS THE FIRST LINK
                    $(dropdown + " > li:first-child > a").focus();
                break;

                // CONSUME ESC KEY
                case csKeyCodes.esc:
                    e.preventDefault();

                    // HIDE THE MENU
                    $(parent).removeClass("open");
                    $(dropdown).hide().attr("aria-hidden", "true");
                break;
            }
        });

        // DROPDOWN LINK KEYDOWN EVENTS
        $(dropdown + " > li > a").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().is(":first-child")) {
                        // FOCUS DROPDOWN BUTTON
                        $(button).focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).parent().prev("li").find("> a").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    e.preventDefault();

                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $(dropdown + " > li:first-child > a").focus();
                    } else {
                        // FOCUS NEXT ITEM
                        $(this).parent().next("li").find("> a").focus();
                    }
                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $(dropdown + " > li:first-child > a").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    e.preventDefault();

                    // FOCUS LAST ITEM
                    $(dropdown + " > li:last-child > a").focus();
                break;

                // CONSUME ESC KEY
                case csKeyCodes.esc:
                    e.preventDefault();

                    $(button).focus();
                    $(parent).removeClass("open");
                    $(dropdown).hide().attr("aria-hidden", "true");
                break;
            }
        });
    }

    function pagelistAccordionSet() {
        if(!pagelistAccordionIsActive) {
            // SET ROLES AND ATTRIBUTES
            $(".sp.column.one .ui-widget.app .ui-widget-header").attr("role", "button").attr("tabindex", "0");
            $(".sp.column.one .ui-widget.app .ui-widget-detail, .sp.column.one .ui-widget.app .ui-widget-footer").hide().attr("aria-hidden", "true");

            // ACCORDION HEADER CLICK
            $(".sp.column.one .ui-widget.app .ui-widget-header").click(function(){
                $(this).parent().toggleClass("open");
                $("~ .ui-widget-detail", this).toggle().attr("aria-hidden", ($("~ .ui-widget-detail", this).attr("aria-hidden") == "true") ? "false" : "true");
                $("~ .ui-widget-footer", this).toggle().attr("aria-hidden", ($("~ .ui-widget-footer", this).attr("aria-hidden") == "true") ? "false" : "true");
            });

            // ACCORDION HEADER FOCUS
            $(".sp.column.one .ui-widget.app .ui-widget-header").keydown(function(e){
                switch(e.keyCode) {
                    // CONSUME SPACE AND ENTER KEYS
                    case csKeyCodes.space:
                    case csKeyCodes.enter:
                        e.preventDefault();

                        $(this).parent().toggleClass("open");
                        $("~ .ui-widget-detail", this).toggle().attr("aria-hidden", ($("~ .ui-widget-detail", this).attr("aria-hidden") == "true") ? "false" : "true");
                        $("~ .ui-widget-footer", this).toggle().attr("aria-hidden", ($("~ .ui-widget-footer", this).attr("aria-hidden") == "true") ? "false" : "true");
                    break;
                }
            });

            pagelistAccordionIsActive = true;
        }
    }

    function pagelistAccordionReset() {
        if(pagelistAccordionIsActive) {
            // REMOVE ROLES AND ATTRIBUTES
            $(".sp.column.one .ui-widget.app .ui-widget-header").removeAttr("role").removeAttr("tabindex");
            $(".sp.column.one .ui-widget.app .ui-widget-detail, .sp.column.one .ui-widget.app .ui-widget-footer").removeAttr("style").removeAttr("aria-hidden");

            // UNBIND EVENTS
            $(".sp.column.one .ui-widget.app .ui-widget-header").unbind();

            pagelistAccordionIsActive = false;
        }
    }

	function useChannelForSection() {
		// USE CHANNEL NAME FOR PAGELIST NAV HEADER IF ONE IS NOT PRESENT
		if(!$("div.sp.column.one .ui-widget-header h1").length) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
    }

	function calendarListView() {
		// AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
		if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
			$("a.ui-btn-toggle.list").click();
			$("a.ui-btn-toggle.month").click();
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
		}

		switch(csGetBreakPoint()) {
			case "desktop": case "768": case "640":
				$(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
				$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
			break;
			case "480": case "320":
				$(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
				$(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
				$(".ui-btn-toggle.month").removeClass("active");
				$(".ui-btn-toggle.list").addClass("active");
			break;
		}
	}

	function csGetBreakPoint() {
		return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
	}

