   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=2bVrFqD8%2fQ9hhBj4ANtn1w%3d%3d&SessionID=f8838ff2-18f3-4dc5-8f76-f88f30e85813&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f24%2f2020+8%3a12%3a42+AM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.74.64';
