;(function($) {
  'use strict';
  $(function() {
    // Wait until all of the images in the features are loaded
    $('.socs-feature-img').imagesLoaded(function() {
      $('.socs-feature.two').each(function() {
        var $this = $(this);

        // Add a border on the side of whichever box is the tallest - .two or .three
        if ($this.height() >= $this.next().height()) {
          $this.addClass('bordered');
        } else {
          $this.next().addClass('bordered');
        }
      });

      // If the last feature is the left-side of the "split" - undo the split
      var $lastFeature = $('.socs-feature:last');
      if ($lastFeature.hasClass('two')) {
        $lastFeature.css({
          'float': 'none',
          'padding-right': '0',
          'width': 'auto'
        }).removeClass('bordered');
      }
    });

  });
})(jQuery);

;(function($) {
  // Loop through all the li tags looking for banner content
  var $li = $('.socs-banner.rightBanner .nav > li');
  if ($li.find('> div').length) {
    $li.each(function() {
      if ($(this).find('img').length === 0 && $(this).find('> div').text() === '') $(this).addClass('hidden');
    });
    // If all the li tags are hidden - hide the entire socs-banner div
    if ($('.socs-banner.rightBanner .nav > li.hidden').length === $li.length) $li.closest('div').addClass('hidden');
  } else {
    $li.closest('div').addClass('hidden');
  }
})(jQuery);

