						"autoRotate" 			: "no",
			        	"allLoaded"				: function() { 
                        	photoGallerySlider(); 
                            $(".hide-shadow").show(); 
                            $("#hp-slideshow-slider-outer").show(); 
                        }
			});
			if($("#hp-slideshow-slider .rs-photo-gallery-images .rs-photo-gallery-images-list li").size() > 3) {

