if ((/Android|iPhone|iPad|iPod|BlackBerry|Windows Phone/i).test(navigator.userAgent || navigator.vendor || window.opera)) {
  $('body').removeClass('desktop');
}
$(document).ready(function () {

  /* ---	Equal height function resizeable on window resize ---- */
  if ($(window).width() > 880) {
    delay(function () {
      $('.main .col-item.same-height').css('height', 'auto'); //solve for all you browser stretchers out there!
      setEqualHeight($('.main .col-item.same-height'));
    }, 500);
  }
  $(window).resize(function () {
    if ($(window).width() > 880) {
      delay(function () {
        $('.main .col-item.same-height').css('height', 'auto'); //solve for all you browser stretchers out there!
        setEqualHeight($('.main .col-item.same-height'));
      }, 500);

    } else {
      $(".main .col-item.same-height").css('height', 'auto');
    }
  });

  if ($(window).width() > 1040) {
    var sliderheight = $('#slider').height();
    var headerheight = $('#header').height();
    var windowheight = $(window).height();
    var imgheight = windowheight - headerheight;
    $('.right-slider img, .right-slider  .bx-wrapper .bx-viewport').css("height", imgheight);
    //$('#left-nav .menu-wrap').css("min-height", imgheight);

  } else {
    $('.right-slider img,.right-slider .bx-wrapper .bx-viewport').css("height", "auto");
    $('.right-slider img, .bx-wrapper .bx-viewport').css("min-height", 90);
    //$('#left-nav .menu-wrap').css("height", "auto");
  }
  $('.right-slider').bxSlider({
    mode: 'fade',
    auto: true,
    autoHover: true,
    adaptiveHeight: true,
    onSliderLoad: function () {
      if ($(window).width() > 1040) {
        var sliderheight = $('#slider').height();
        var headerheight = $('#header').height();
        var windowheight = $(window).height();
        var imgheight = windowheight - headerheight;
        $('.right-slider img, .right-slider  .bx-wrapper .bx-viewport').css("height", imgheight);
        //$('#left-nav .menu-wrap').css("min-height", imgheight);

      } else {
        $('.right-slider img,.right-slider .bx-wrapper .bx-viewport').css("height", "auto");
        $('.right-slider img, .bx-wrapper .bx-viewport').css("min-height", 90);
        //$('#left-nav .menu-wrap').css("height", "auto");
      }
      $(window).resize(function () {
        if ($(window).width() > 1040) {
          var sliderheight = $('#slider').height();
          var headerheight = $('#header').height();
          var windowheight = $(window).height();
          var imgheight = windowheight - headerheight;
          $('.right-slider img, .right-slider  .bx-wrapper .bx-viewport').css("height", imgheight);
          //$('#left-nav .menu-wrap').css("min-height", imgheight);

        } else {
          $('.right-slider img,.right-slider .bx-wrapper .bx-viewport').css("height", "auto");
          $('.right-slider img, .bx-wrapper .bx-viewport').css("min-height", 90);
          //$('#left-nav .menu-wrap').css("height", "auto");
        }
      });
    }

  });

});

var slider = $('.news-slider').bxSlider({
  auto: true,
  autoHover: true,
  adaptiveHeight: true,
  minSlides: 2,
  maxSlides: 2,
  slideWidth: 877,
  slideMargin: 22
});
if ($(window).width() <= 1000) {
  slider.reloadSlider({mode: 'fade', auto: true});
}

$(document).ready(function () {
  document.getElementById("cboxNext").setAttribute("aria-label", "Next Photo");
  document.getElementById("cboxPrevious").setAttribute("aria-label", "Previous Photo");
  document.getElementById("cboxSlideshow").setAttribute("aria-label", "Slide Show");
  var label = document.createElement('label');
  label.setAttribute("for", "filter_id");
  label.setAttribute("style", "display: none;");
  label.innerText = "Change Filter form";
  document.getElementById("filter_id").parentNode.appendChild(label);
  $(".class-link a[href='']").remove();
  $(".folder a[href='#']").remove();
});
