	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			multimediaRotator();
			popularLinks('standard');

				//removes click event for standard view
			$('#footer-global-links > div > h2').off("click");
				//fixes issues when going between accordion and standard.
			$('#footer-global-links > div > ul').each(function(k, v){
				if($(this).css('display') == "none"){
					$(this).css('display', 'block');
				}
			});
			if(announcement[0]){
				$('#popular-links-overlay #popular-links').css('margin-top', '-127.5px');
			}

		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			multimediaRotator();
			popularLinks('mobile');
			mobileMenuImage();
			if($('.sp').length > 0 || $('.spn').length > 0){
				popularLinks('spMobile');
			}
				//fixes issues when going between accordion and standard.
			$('#footer-global-links > div > ul').each(function(k, v){
				if($(this).css('display') == "block"){
					$(this).css('display', 'none');
				}
			});
				//sets click event for accordions
			$('#footer-global-links > div > h2').off("click").click(footerGlobalAccordions);
			if(announcement[0]){
				$('#popular-links-overlay #popular-links').css('margin-top', announcement[1] - 20);
			}

		} if (getWidth >= 640 && getWidth < 768) { // 640+
			multimediaRotator();
			popularLinks('mobile');
			mobileMenuImage();
			if($('.sp').length > 0 || $('.spn').length > 0){
				popularLinks('spMobile');
			}
			$('#footer-global-links > div > ul').each(function(k, v){
				if($(this).css('display') == "block"){
					$(this).css('display', 'none');
				}
			});
			$('#footer-global-links > div > h2').off("click").click(footerGlobalAccordions);
			if(announcement[0]){
				$('#popular-links-overlay #popular-links').css('margin-top', announcement[1] - 20);
			}

		} if (getWidth >= 480 && getWidth < 640) { // 480+
			multimediaRotator();
			appView();
			popularLinks('mobile');
			mobileMenuImage();
			if($('.sp').length > 0 || $('.spn').length > 0){
				popularLinks('spMobile');
			}
			$('#footer-global-links > div > ul').each(function(k, v){
				if($(this).css('display') == "block"){
					$(this).css('display', 'none');
				}
			});
			$('#footer-global-links > div > h2').off("click").click(footerGlobalAccordions);
			if(announcement[0]){
				$('#popular-links-overlay #popular-links').css('margin-top', announcement[1] - 20);
			}

		} if (getWidth >= 320 && getWidth < 480) { // 320+
			appView();
			popularLinks('mobile');
			mobileMenuImage();
			if($('.sp').length > 0 || $('.spn').length > 0){
				popularLinks('spMobile');
			}
			$('#footer-global-links > div > ul').each(function(k, v){
				if($(this).css('display') == "block"){
					$(this).css('display', 'none');
				}
			});
			$('#footer-global-links > div > h2').off("click").click(footerGlobalAccordions);
			if(announcement[0]){
				$('#allow-overlay-all #popular-links-overlay').css('padding-top', announcement[1] + 10);
			}
		}
	}

	function multimediaRotator(){
		if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length){
			// MULTIMEDIA ROTATOR
			$("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
				"imageWidth"              : 1500,
				"imageHeight"             : 915,
				"appHeader"               : "no",
				"titles"                  : "yes",
				"captions"                : "yes",
				"galleryOverlay"		  : "yes",
				"linkedElement"		  : ["title"], // ["image", "title", "overlay", "all"]
				"playPause"               : "no",
				"mainControls"            : "no",
				"contMoreLink"            : "no",
				"contMoreLinkText"        : "Read More",
				"descMoreLink"            : "no",
				"descMoreLinkText"        : "Read More",
				"bullets"                 : "no",
				"bulletControls"          : "no",
		 		"thumbnails"              : "no",
				"thumbnailWidth"          : 122,
				"thumbnailHeight"         : 53,
				"thumbnailSlideDirection" : "horizontal", // horizontal or vertical
				"hoverPause"              : "yes",
		                "fullScreenRotator"       : "yes", // image dimensions work well at 1500x915
				"fullScreenVideo"         : "no",
				"transitionType"          : "fade", // fade or slide
				"transitionSpeed"         : 3,
				"transitionDelay"         : 4,
				"onImageLoad"             : function() { },
				"allImagesLoaded"         : function() { },
				"onThumbnailLoad"         : function() { },
				"allThumbnailsLoaded"     : function() { },
				"onTransitionStart"       : function() { 
					if($('.rs-gallery-desc .rs-gallery-title .rs-title.next').hasClass("hidden")){
						$('.rs-gallery-desc').fadeOut(500);
					}else{
						$('.rs-gallery-desc').fadeIn(500);
					}
				},
				"onTransitionEnd"         : function() { },
				"watchVidClick"           : function() { },
				"closeVidClick"           : function() { },
				"allLoaded"               : function() { 
					if($('.rs-gallery-desc .rs-gallery-title .rs-title.active').hasClass("hidden")){
						$('.rs-gallery-desc').hide();
					}
					galleryDesc();
				}
			});
		}
	}

	function modEvents(){
		//Remove all of the year values from the dates
	    $('.ui-article-title.sw-calendar-block-date').each(function(k, v){
	    	var date = $(this).html();
	    	if(date.indexOf(',') >= 0){ //make sure it doesnt say a day of the week.
	    		date = date.substring(0, date.indexOf(','));
	    	}
	    	$(this).html(date);
	    });
	    	//Remove all articles over 5. Designer wanted this hard coded in.
	    $('.ui-widget.upcomingevents ul.ui-articles li').each(function(k, v){
	    	if(k > 4){
	    		$(this).remove();
	    	}else{
	    		$(this).addClass('custom-red-bg-grad');
	    	}
	    });

	    	//Add our own view calendar link.
	   $('.ui-widget.upcomingevents ul.ui-articles').append('
All Events');
	   $('#view-calendar-events a').attr('href', $('.ui-widget.upcomingevents .view-calendar-link').attr('href'));
	}

	function eventsByDay(container) {
	    var countLi = 0;
	    $(".ui-article", container).each(function(){
	        if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
	            var moveArticle = $(this).html();
	            $(this).parent().prev().children().children().next().append(moveArticle);
	            $(this).parent().remove();
	        };
	    });
	}

	function appView(){
			//They add the + / - to the accordion headers.
		var headerIcons = {
			'.hp.content.one': '+',
			'.hp.content.two': '+',
			'.hp.content.three': '+'
		};

		$.each(headerIcons, function(k, v){
			if($(k + " .ui-widget-header h1").length){
				if(!$(k + " .ui-widget-header h1 span").length){
					$(k + " .ui-widget-header h1").append(v);
				}else{
					return false;
				}
			}
		});


		//DOC dont add app into accordion if it doesnt have a header
		$(".hp.content div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}
		});

		//DOC accordion header click
		$(".hp.content div.ui-widget-header").off('click').click(function(){
			$(".hp.content div.ui-widget.app.active .ui-widget-header h1 span").html("+");

			if( $(this).parent().hasClass("active") ){
				$(this).parent().removeClass("active");
			}else{
				var clickedApp = $(this).parent().parent().attr("id");

				$(".hp.content div.ui-widget.app.active").removeClass("active");

				$(this).parent().addClass("active");

				$("#" + clickedApp + " .ui-widget-header h1 span").html("-");

				smoothScroll("#" + clickedApp, 3);
			}
		});
	}	

	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
        }

	function dynamicContent(){
		// ********** CUSTOM MYSTART ********** //

		$('.custom-mystart-menu').click(function(e){
			if( $(this).hasClass("openMenu") ){
				$(this).removeClass("openMenu");
				$(this).children('p').children('span').removeClass('icon-fontarrow-up2').addClass('icon-fontarrow-down2');
			}else{
				$('.custom-mystart-menu.openMenu').removeClass('openMenu').children('p').children('span').removeClass('icon-fontarrow-up2').addClass('icon-fontarrow-down2');
				$(this).addClass("openMenu");
				$(this).children('p').children('span').removeClass('icon-fontarrow-down2').addClass('icon-fontarrow-up2');
			}
		});

		$('.custom-mystart-menu').mouseleave(function(e){
			$(this).removeClass("openMenu");
			$(this).children('p').children('span').removeClass('icon-fontarrow-up2').addClass('icon-fontarrow-down2');	
		});

		$('#custom-schoollist ul').append($('.sw-mystart-dropdown.schoollist ul.sw-dropdown-list').html());

		// Changed the logic in checking for signed in users Jeremy - Case #00104349 



		if($('.sw-mystart-button.signin').length){
			$('#custom-mystart-bar ul li#custom-mystart-signin').html($('.sw-mystart-button.signin').html());
		}
		if($('.sw-mystart-button.register').length){ //we check for this seperate from sign in because it can be turned on or off.
			$('#custom-mystart-bar ul li#custom-mystart-register').html($('.sw-mystart-button.register').html()).children('a').children('span').html('Subscribe');
		}

		addFramesetTranslate(); //makes sure the translator is last in line

			//this is done so css can move that menu item to the right some so it doesnt display off of the page.
		$('#custom-mystart-bar div.hide768 > ul > li').each(function(k, v){
			if( $('#custom-mystart-bar div.hide768 > ul > li').length - 1 == k){
				$(v).addClass("last-menu-item");
			}
		});

		// ********** HEADER ********** //

			//adds the custom italic 'the' on the sitenames that have it.
		$('#gb-sitename-top h1').html($('#gb-sitename-top h1').html().replace(/the/i, 'The'));


		// ********** CHANNEL BAR ********** //
		//DOC add first class to first channel item
		$("li.sw-channel-item").first().addClass("first");
		//DOC add last class to last channel item
		$("li.sw-channel-item").last().addClass("last");

			//Super kluge to fix channel bar wrapping into site tagline.....
		var textWidth = $('#gb-sitename-bottom h2').text().length * 13; //assuming each character is 9px wide
		textWidth += 185; //adds on the width of the banner and the parent containers padding.
		$('#gb-channel-list-outer').css('padding-left', textWidth + "px");

			//Changes Home to District.
		if( $('.sw-channel-item.first a span').length > 0 ){
			if( $('.sw-channel-item.first a span').html().toLowerCase() === "home"){
				$('.sw-channel-item.first a span').html('District');
			} 
		}

		// ********** BODY ********** //
		//DOC rename first breadcrumb
		$("li.ui-breadcrumb-first > a > span").addClass("icon-font icon-fonthome custom-red").html("");

		//DOC replace more-link text
		$("a.more-link span").text("More ");
		//DOC auto-focus username signin field
		$("#swsignin-txt-username").focus();
		//DOC center signed out message and signin button
        	if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
        		$("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
        		//DOC add signed out breadcrumb
        		$(".ui-breadcrumbs").append("
Signed Out");
        	}
        	//DOC add sign in breadcrumb
        	if($("#swlogin").length){
        		$(".ui-breadcrumbs").append("
Sign In");
        	}
        	//DOC add read full story button to each headline
        	$(".ui-widget.app.headlines").each(function(){
        		$(".ui-article", this).each(function(k, v){
        			var headlineURL = $("h1 a", this).attr("href");
        			$(this).after('');
        			if( k > 1 ){
        				$(this).parent('li').css('border-top', 'solid 2px #FFF');
        			}
        		});
        	});

        		//sets up our own custom "tabbed" area
        	if($('#sw-content-container1 .ui-widget-header h1').length){
        		$('#hp-content-menu').append('
' + $('#sw-content-container1 .ui-widget-header h1').hide().html() + '');
        	}
        	if($('#sw-content-container2 .ui-widget-header h1').length){
        		$('#hp-content-menu').append('
' + $('#sw-content-container2 .ui-widget-header h1').hide().html() + '');
        	}
        	if($('#sw-content-container3 .ui-widget-header h1').length){
        		$('#hp-content-menu').append('
' + $('#sw-content-container3 .ui-widget-header h1').hide().html() + '');
        	}
        		//Create click event for the accordion
        	$('#hp-content-menu').on('click', 'h1', function(e){ //adds functionality to it.
        		smoothScroll("#" + $(this).attr('data-scroll'), 5);
        	});
        		//adds a special class to the last h1 in the tabbed area for border reasons.
        	$('#hp-content-menu h1').last().addClass('lastItem');

		$('.rs-gallery-desc').append('');

		drawCurve("canvas-arrow-right"); //creates the photo gallery right triangle

		$('.rs-gallery-desc').hover(function(e){

		}, function(e){
			changeCanvasColor("canvas-arrow-right");
		});	

			//hide all empty content regions. since the css padding applied to them makes them show even if empty.
		if($('.hp.content').length > 0){
			$('.hp.content').each(function(k, v){
				if($(this).children('div').children().length === 0){
					$(this).hide();
				}
			});
		}
			//helps to create the sub-page left nav bullets.
		if( $('.ui-widget.pagenavigation').length > 0){
			$('ul.page-navigation li').each(function(){
				$(this).addClass('icon-font');
			});
		}
		// ********** FOOTER ********** //
		//DOC get Schoolwires footer links
		$("a#feedback").attr("href", $("div#sw-footer-links li:eq(0) a").attr("href"));
		$("a#terms").attr("href", $("div#sw-footer-links li:eq(2) a").attr("href"));
		$("a#policy").attr("href", $("div#sw-footer-links li:eq(1) a").attr("href"));		

		$("p#copyright").html($('#sw-footer-copyright').html());

			//This checks to see if they put a label in any of the footer global elements 
				//It will hide any that dont have a label.
		$('#footer-global-links div').each(function(k, v){
			$(v).children('ul').each(function(k1, v1){
				$(v1).children('li').each(function(k2, v2){
					var stringTest = $(this).html().split(''); //the tag will be like Label
					if(stringTest[1] === ""){	//if we only have a closing  then their wasnt any other text so hide it.
						$(v2).hide();
					}

				});
			});
		});

			//Hide Fax if it is empty
		var faxText = $("#fax-number").html();
		faxText = faxText.split("");
		if( faxText[1] === "" ){
			$('#fax-number').hide();
		}
	}

	function searchText() {
		//DOC custom mystart search placeholder
	        //SEARCH TEXT
	        $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
		$("div#sw-mystart-search input#sw-search-input").attr("value", "Search...");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "Search...") {
				$(this).val("");
			}
		});
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("Search...");
			}
		});
	}

	function calendarListView(){
		//DOC auto click function for schoolwires calendar list view for small mobile devices
		if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
			$("a.ui-btn-toggle.list").click();
			$("a.ui-btn-toggle.month").click();
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
		}

		var getWidth = $(window).width();
		if(getWidth >= 640){ // 640+
			$(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
		} if(getWidth < 640){ // 480-
			$(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
			$(".ui-btn-toggle.month").removeClass("active");
			$(".ui-btn-toggle.list").addClass("active");
		}
	}

	/*
	** smoothScroll
	** Use: This will smoothly scroll to the section thats id was passed to the function.
	** Return: N/A
	** Parameter(s): String (jQuery "#id") The id of the section to be scolled to.
	*/
	function smoothScroll( to, offset ){
		if( to.indexOf("#") < 0 ){ to = "#" + to; } //makes sure we have a valid id accessor string
		var target = $(to);

		if (target.length) {
			$('html,body').animate({
				scrollTop: target.offset().top - offset //this offset accounts for the fixed menu on the top
			}, 1000, function(){
				$("html, body").stop(true, false);
			});
			return false;
		}
	}

	/*popularLinks
		Use: sets up the actions and some of the styling for the popular links container and overlay.
		Parameters: actions (String) - will control the switch statement within the function.
	*/
	function popularLinks( actions ){
		switch(actions){
			case "init": //initializes the popular links (click events, hover, search)
					//setup the click events
				$('.popular-links-switch, #popular-links-overlay').click(function(e){
					if(!$(e.target).is('input')){
						var theSwitch;
						if($('#popular-links-overlay').hasClass('showOver')){
							$('#popular-links-overlay').fadeOut(500, function(e){$(this).removeClass('showOver')});

							if($(this).attr('id') === "popular-links-overlay" || $(this).hasClass('stopChange')){
								theSwitch = "#popular-links-container .popular-links-switch";
							}else{
								theSwitch = this;
							}

							$(theSwitch)
							.removeClass("custom-red-bg-grad on")
							.addClass("custom-orange-bg")
							.children('p')
							.children('span').html("Popular Links");

							$(theSwitch).children('span').hide();
						}else{
							$('#popular-links-overlay').hide().addClass('showOver').fadeIn(500);

							if($(this).attr('id') === "popular-links-overlay" || $(this).hasClass('stopChange')){
								theSwitch = "#popular-links-container .popular-links-switch";
							}else{
								theSwitch = this;
							}

							$(theSwitch)
							.removeClass("custom-orange-bg")
							.addClass("custom-red-bg-grad on")
							.children('p')
							.children('span').html("Close");

							$(theSwitch).children('span').show();
						}
					}
				});

					//Inverse color change of popular links on hover
				var linkColor, bgColor;
				$('.hp #popular-links ul li').hover(function(e){ //In
					linkColor = $(this).children('a').css("background-color");
					bgColor = $(this).children('a').css("color");
					$(this).children('a').animate({
						backgroundColor: bgColor,
						color: linkColor,
					}, 0);	
				}, function(e){ //Out
					linkColor = $(this).children('a').css("color");
					bgColor = $(this).children('a').css("background-color");
					$(this).children('a').animate({
						"color": bgColor,
						"backgroundColor": linkColor
					}, 0);
				});	

					//Popular links custom search
				var defaultText = 'Search';	
					//search text changing
				$("div#popular-links-search input").attr('placeholder', defaultText);

				$("div#popular-links-search input").focus(function(e) {
					if($(this).val() == defaultText) {
						$(this).val("");
					}
				})
				.blur(function() {
					if($(this).val() == "") {
						$(this).val(defaultText);
					}
				});

				$("div#popular-links-search input").val(defaultText);

				//$('div#popular-links-search span').click(customSearch);
				break;
			case "mobile": //moves them to their mobile state
				$('#popular-links-container').appendTo('#custom-mystart-bar');
				$('#popular-links-overlay').prependTo('#allow-overlay-all');
				break;
			case "standard": //moves them to their standard state
				$('#popular-links-container').prependTo('#hp-slideshow');
				$('#popular-links-overlay').appendTo('#popular-links-container');
				break;
			case "spMobile": //moves them to the menu for sp and spn

				//check to make sure the menu option doesnt exist already
				if( $('#rs-menu .popular-links').length <= 0 ){
					var sitemap;
					if("https://www.elyriaschools.org/Page/754" == ""){
						sitemap = sitemapUrl;
					}else{
						sitemap = "https://www.elyriaschools.org/Page/754";
					}
						//setup the string of things to be added
					var additionalMenuItems = {
						"Pioneer TV" : ["https://www.youtube.com/user/TheElyriaSchools  ", "_blank"],
						"PowerSchool" : ["http://PowerParent.Elyriaschools.org", "_blank"],
						"Food" : ["/Page/176 ", "_blank"],
						"Performing Arts Schedule" : ["/Page/378", "_blank"],
						"Athletics Schedule" : ["https://elyriaathletics.org", "_blank"],
						"Schoology" : ["https://Classlink.ElyriaSchools.org", "_blank"],
						"Job Postings" : ["http://www.applitrack.com/elyria/onlineapp/default.aspx", "_blank"],
						"Site Map" : [sitemap, "_self"],
						"Twitter" : ["https://twitter.com/elyriaschools", "_blank"],
						"Facebook" : ["https://www.facebook.com/elyriacityschools", "_blank"]
					}

					var newOptions = '
\

\

a\

Popular Links\

\
						\

\
						 ';

					$.each(additionalMenuItems, function(k, v){
						newOptions += '
' + k + '';
					});

					newOptions += '';

					$('#rs-menu-header').after(newOptions);

					$('#rs-menu').on('click', '.popular-links .rs-option-header', function(e){
						var clickedHeader = $(this);
						var menuBody = $(this).next($("div.rs-option-body"));
						if(!$(this).hasClass("open")){
							var emptyTest = $(menuBody).is(":empty");
							if(emptyTest == false){
								$("div.rs-option-header").removeClass("open").addClass("closed");
								$("div.rs-option-body").slideUp(function(){
									clickedHeader.removeClass("closed").addClass("open");
								});
								menuBody.slideDown();
							}
						} else {
							clickedHeader.removeClass("open").addClass("closed");
							menuBody.slideUp();	
						}
					});
				}
				break;
			default:
				//pass
				break;
		}

	}
	function customSearch() {
		window.location.href="//jkatlic.schoolwires.net/site/Default.aspx?PageType=6&SiteID=26&SearchString=" + $('div#popular-links-search input').val();
	}
	function scrollManager(){
		var rect = document.body.getBoundingClientRect();
		if(rect.top < -250){
			$('#goto-top').fadeIn(500);
		}else{
			$('#goto-top').fadeOut(400);
		}
	}

	function galleryDesc(){
		var startColor = "rgba(255, 255, 255, 0.20)";
			//hook in a hover
		$('.rs-gallery-desc').hover(function(e){ //in
			if($('.rs-title.active h1').children('a').length > 0){
				$(this).css("background-color", "#2C5E7D");
				changeCanvasColor("canvas-arrow-right", "#2C5E7D");
			}
		}, function(e){ //out
			$(this).css("background-color", startColor);
			changeCanvasColor("canvas-arrow-right");
		});	
	}
