	function footerGlobalAccordions(){
		if($(this).siblings('ul').hasClass('openMenu')){
			$('#footer-global-links > div > ul.openMenu').fadeOut(200).removeClass('openMenu');
			$(this).children('span').html("+");			
		}else{
			$('#footer-global-links > div > ul.openMenu').fadeOut(200).removeClass('openMenu');
			$(this).siblings('ul').hide().addClass('openMenu').fadeIn(400);
			$(this).children('span').html("-");
		}	
	}

	function mobileMenuImage(){
		//  /cms/lib/OH01914441/Centricity/Template/8/images/menu-handle.png
		//console.log(!$('#rs-mystart-menu-btn span image').length);
		if($('#rs-mystart-menu-btn').length && !$('#rs-mystart-menu-btn > span > img').length){
			$('#rs-mystart-menu-btn span').append('');
		}

		if($('#rs-menu-header').length && !$('#rs-menu-btn > img').length){
			$('#rs-menu-btn').append('');
		}

		// Move menu items around Jeremy - Case #00104284
