	});

	$(function(){
		// SET IE CLASSES TO BODY
		if(isMSIE){ $("body").addClass("msie"); }
		if(isMSIE8){ $("body").addClass("msie8"); }
		if(isMSIE9){ $("body").addClass("msie9"); }
		if(isMSIE10){ $("body").addClass("msie10"); }

		$(window).bind("resize", function(){ pgScreenSize(); if(logoCheck == "true") { padSiteName(); } headerGlobalH(); slideshowPad(); });
		if($(".hp").length && slideshowCheck == "true") {
			$(window).scroll(adjustChannelDropdowns);
		}

		adjustChannelDropdowns();

		// CHECK FOR SCHOOLLIST
		var useSchoolList = ($(".sw-mystart-dropdown.schoollist").length) ? "yes" : "no";

		// CHECK FOR TRANSLATOR
		var translator = 'false';
		if (translator == "false"){
			$(".sw-mystart-dropdown.translate").hide();
			var useTranslate = "no";
		}
		else {
			var useTranslate = "yes";
		}

		var districtHome = 'true';
		if (districtHome == "false"){
			$(".sw-mystart-button.home").hide();
		}
		else {
			$(".sw-mystart-button.home").show();
		}

		// RESPONSIVE MAIN MENU
		var globalIcons = [
			["Staff", "https://www.fhevs.org/domain/47"],
			["Infinite Campus", "https://lgca.infinitecampus.org/campus/fairport.jsp"],
			["Harding High School", "https://www.fhevs.org/domain/29"],
			["McKinley Elementary School", "https://www.fhevs.org/domain/27"],
			["Athletics", "https://www.fhevs.org/domain/18"],
			["Directions", "#"]
		];
		var globalLinksHeader = "", globalLinks = "";

		for(var i = 0; i < iconNum; i++) {
			var linkCheck = (globalIcons[i][1] == "[SITECALENDARLINK]") ? "/Page/2" : globalIcons[i][1];
			globalLinks += "1, " + globalIcons[i][0] + ", " + linkCheck + ", _self;";
		}

		if(iconNum > 0) {
			globalLinksHeader = "Popular";
		}

		var globalLink1Target = 'false';
		var globalLink2Target = 'false';
		var globalLink3Target = 'false';

		if(globalLink1Target == 'true') {
			globalLink1Target = '_blank';
		} else {
			globalLink1Target = '_self';
		}
		if(globalLink2Target == 'true') {
			globalLink2Target = '_blank';
		} else {
			globalLink2Target = '_self';
		}
		if(globalLink3Target == 'true') {
			globalLink3Target = '_blank';
		} else {
			globalLink3Target = '_self';
		}

		var menuglobalLink1 = '2,PARENTS,https://www.fhevs.org/domain/104,'+ globalLink1Target +';';
		var menuglobalLink2 = '2,STUDENTS,https://www.fhevs.org/domain/105,'+ globalLink2Target +';';
		var menuglobalLink3 = '2,STAFF,https://www.fhevs.org/domain/47,'+ globalLink3Target + ';';

		$("body").csRsMenu({
			"breakPoint"		: 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			"slideDirection"	: "left-to-right", // OPTIONS - left-to-right, right-to-left
			"menuButtonParent"	: "#gb-channel-list",
			"showMenuBtnText"	: "yes",
			"menuBtnText"		: "MENU",
			"showSchools"		: useSchoolList,
			"showTranslate"		: useTranslate,
            "translateVersion"	: translateType,
			"translateId"		: "none",
             "advancedTranlate": {
             	"useAdvancedList": "yes"
                },
			"showAccount"		: "yes",
			"extraMenuOptions"	: globalLinksHeader + "; Global Links", // EXTRA MENU HEADING NAMES SEPARATED BY A ;
			"extraMenuItems"	: globalLinks + menuglobalLink1 + menuglobalLink2 + menuglobalLink3, // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
			"siteID"		: "4"
		});

		$(".rs-menu-option.global-links").addClass("link1-true link2-true link3-true");
		$(".rs-menu-option.global-links li:eq(2)").addClass('true');
		$(".rs-menu-option.global-links li:eq(3)").addClass('true');
		$(".rs-menu-option.global-links li:eq(4)").addClass('true');

		// RESPONSIVE PAGELIST MENU
		if($(".sp").length) {
			$("body").csRsPagelistMenu({
			        "breakPoint"            : 640, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			        "slideDirection"        : "left-to-right", // OPTIONS - left-to-right, right-to-left
			        "menuButtonParent"      : "#gb-breadcrumbs",
			        "showMenuBtnText"       : "yes",
			        "menuBtnText"           : "PAGES",
			        "siteID"                : "4"
			});
		}

		// GLOBAL ICONS
        var icon1Target = ("false" == "true") ? "_blank" : "_self";
        var icon2Target = ("false" == "true") ? "_blank" : "_self";
        var icon3Target = ("false" == "true") ? "_blank" : "_self";
        var icon4Target = ("false" == "true") ? "_blank" : "_self";
        var icon5Target = ("false" == "true") ? "_blank" : "_self";
        var icon6Target = ("false" == "true") ? "_blank" : "_self";
		$("#gb-mystart .gb-global-icons, #gb-mobile-mystart-outer .gb-global-icons").creativeIcons({
		        "iconNum"       : "5", // NUMBER OF ICONS
		        "icons"         : [
		                ["Student Program/8.png", "Staff", "https://www.fhevs.org/domain/47", icon1Target], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
		                ["Classroom/7.png", "Infinite Campus", "https://lgca.infinitecampus.org/campus/fairport.jsp", icon2Target], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
		                ["Classroom/15.png", "Harding High School", "https://www.fhevs.org/domain/29", icon3Target], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
		                ["Classroom/1.png", "McKinley Elementary School", "https://www.fhevs.org/domain/27", icon4Target], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
		                ["Athletics/2.png", "Athletics", "https://www.fhevs.org/domain/18", icon5Target], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
		                ["Communication/7.png", "Directions", "#", icon6Target] // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
		        ],
		        "siteID"        : "4",
		        "siteAlias"     : "Fairport",
		        "calendarLink"  : "/Page/2",
		        "contactEmail"  : "fh_webmaster@fhevs.org",
		        "allLoaded"     : function(){
                	$(".gb-global-icons .global-icon").each(function() {
                    	$(this).removeAttr("id");
                    });
		        	$("#gb-mystart .gb-global-icons .global-icon").first().addClass("first");
		        	$("#gb-mobile-mystart-outer .gb-global-icons .global-icon").first().addClass("first");
					$("#gb-mystart .gb-global-icons .global-icon").last().addClass("last");
					$("#gb-mobile-mystart-outer .gb-global-icons .global-icon").last().addClass("last");
		        	$(".gb-global-icons .global-icon").each(function() {
					if(!$(".global-icon-inner", this).length) {
						$(this).children().wrapAll('
');
					}
				});
		        }
		});
		if(iconNum == "0") {
			$(".sw-mystart-button.popular, .gb-global-icons").hide();
		} else {
			$(".gb-global-icons").addClass("items-" + $.trim(iconNum));
		}

		//VIEW FULL SITE BUTTON ON MOBILE
		if('true' == "true") {
			$("#gb-page").addClass("using-full-site-btn");
			$("body").viewFullSite({
				"breakPoint"		: 639, // SYSTEM BREAK POINTS - 1023, 767, 639, 479
				"buttonContainer"	: "body",
				"useDefaultCSS"		: "yes",
				"fixedPosition"		: "bottom" // IF USING DEFAULT STYLES - TOP, BOTTOM
			});
		}

		pgScreenSize();
		photoGallery();
		useChannelForSection();
		dynamicContent();
		buildStyleSheet();
		gbSearch();	
		headerGlobalH();

        csGlobalJs.OpenInNewWindowWarning();
	});	

	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			//DOC MOVE PASSKEYS BUTTON
			if($("#sw-mystart-mypasskey").length) {
				$("#gb-header .gb-header-global.passkey").append($("#sw-mystart-mypasskey"));
			}
			if(!$("#pw-body").length) {
				stickyChannelBar();
				$(window).scroll(stickyChannelBar);
			}
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			//DOC MOVE PASSKEYS BUTTON
			if($("#sw-mystart-mypasskey").length) {
				$("#gb-mobile-mystart .gb-header-global.passkey").append($("#sw-mystart-mypasskey"));
			}
			if(!$("#pw-body").length) {
				$(window).unbind("scroll", stickyChannelBar);
				stickyChannelBarReset();
			}
			appView();
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			//DOC MOVE PASSKEYS BUTTON
			if($("#sw-mystart-mypasskey").length) {
				$("#gb-mobile-mystart .gb-header-global.passkey").append($("#sw-mystart-mypasskey"));
			}
			appView();
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			//DOC MOVE PASSKEYS BUTTON
			if($("#sw-mystart-mypasskey").length) {
				$("#gb-mobile-mystart .gb-header-global.passkey").append($("#sw-mystart-mypasskey"));
			}
			appView();
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			//DOC MOVE PASSKEYS BUTTON
			if($("#sw-mystart-mypasskey").length) {
				$("#gb-mobile-mystart .gb-header-global.passkey").append($("#sw-mystart-mypasskey"));
			}
			appView();
		}
	}

	function photoGallery(){
		if(slideshowCheck == "true") {
			if(!$("#sw-content-container10.region.ui-hp .rs-photo-gallery-container").length){
				// PHOTO GALLERY PLACEHOLDER
                if(!$("#sw-content-container10.ui-hp div.ui-widget.app.gallery.json").length) {
                    $("#sw-content-container10.ui-hp").html("


var photoGallery0000={'Album':{'title': 'Photo Gallery','description': '','transitiondelay': '6','fadestyle': 'NO','gallerywidth': '1800','backgroundaudio': '','loopaudio': 'False','lgpath': '','tnpath': '','Images': [{'photoname': 'Photography Showcase','src': '//extend.schoolwires.com/creative/subscription_library/myway_premium/euphoria/rotate/euphoria.jpg','caption': '','pause': '','link': '','target': ''}]}}</" + "scr" + "ipt>");
                }

                if($("#sw-content-container10.ui-hp div.ui-widget.app.gallery").length) {
					var showPlayPause = 'false';
                    if(showPlayPause == 'true') {
                        showPlayPause = 'yes';
                    } else {
                        showPlayPause = 'no';
                    }

                    var fullScreenRotator = (true) ? "yes" : "no";

                    // PHOTO GALLERY ROTATOR
					$("#sw-content-container10.ui-hp div.ui-widget.app.gallery.json").creativeGalleryRotate({
						"imageWidth" 			: 1800,
				        	"imageHeight"			: 960,
				        	"appHeader"			: "no",
				        	"titles"			: "yes",
				        	"captions"			: "yes",
				        	"playPause"			: showPlayPause,
				        	"mainControls"			: "yes",
				        	"linkedElement"             	: ["image"], // ["image", "title", "overlay", "all"]
				        	"contMoreLink"			: "no",
				       		"contMoreLinkText"		: "Read More",
				        	"descMoreLink"			: "yes",
				        	"descMoreLinkText"		: "MORE",
				        	"bullets"			: "yes",
				        	"bulletControls"		: "no",
				        	"thumbnails"			: "no",
				        	"thumbnailWidth"		: 122,
				        	"thumbnailHeight"		: 53,
				        	"thumbnailSlideDirection"	: "horizontal", // horizontal or vertical
				        	"hoverPause"			: "yes",
				        	"fullScreenRotator"		: fullScreenRotator,
				        	"fullScreenBreakpoints"		: ["960"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
				        	"autoRotate"			: "yes",
				        	"transitionType"		: slideshowAnimation, // fade or slide
				        	"transitionSpeed"		: 2,
				        	"onImageLoad"			: function() { },
				        	"allImagesLoaded"		: function() { },
				        	"onThumbnailLoad"		: function() { },
				        	"allThumbnailsLoaded"		: function() { },
				        	"onTransitionStart"		: function() { },
				        	"onTransitionEnd"		: function() {
				        		// SET PREV AND NEXT CONTROL IMAGES
				        		var prevImgSrc = $("#hp-slideshow .rs-photo-gallery-images-list .image.prev img").attr("src");
				        		var prevImgTitle = $("#hp-slideshow .rs-photo-gallery-images-list .image.prev img").attr("title");
				      			var nextImgSrc = $("#hp-slideshow .rs-photo-gallery-images-list .image.next img").attr("src");
				      			var nextImgTitle = $("#hp-slideshow .rs-photo-gallery-images-list .image.next img").attr("title");
				        		$("#hp-slideshow .rs-photo-gallery-control.back .rs-photo-gallery-control-img img").attr("src", prevImgSrc).attr("title", prevImgTitle).attr("alt", prevImgTitle);
				        		$("#hp-slideshow .rs-photo-gallery-control.next .rs-photo-gallery-control-img img").attr("src", nextImgSrc).attr("title", nextImgTitle).attr("alt", nextImgTitle);
				        	},
				        	"allLoaded"			: function() {
                            	$(".rs-photo-gallery-control.play-pause").prependTo(".rs-photo-gallery-container");
				        		// WRAP THE SLIDESHOW DESCRIPTION CONTAINER
				        		$("#hp-slideshow .rs-photo-gallery-desc").wrap('
').children().wrapAll('
');
				        		$("#hp-slideshow .rs-photo-gallery-desc-inner").children().wrapAll('
');

				        		// MOVE THE SLIDESHOW BULLETS
				        		$("#hp-slideshow .rs-photo-gallery-images").prepend($("#hp-slideshow .rs-photo-gallery-bullets-outer"));

				        		// MOVE SLIDESHOW CONTROLS
				        		$("#hp-slideshow .rs-photo-gallery-images").prepend($("#hp-slideshow .rs-photo-gallery-control.next")).prepend($("#hp-slideshow .rs-photo-gallery-control.back"));

				        		// SET PREV AND NEXT CONTROL IMAGES
				        		var prevImgSrc = $("#hp-slideshow .rs-photo-gallery-images-list .image.prev .record-src").text();
				        		var prevImgTitle = $("#hp-slideshow .rs-photo-gallery-images-list .image.prev .record-title").text();
				      			var nextImgSrc = $("#hp-slideshow .rs-photo-gallery-images-list .image.next .record-src").text();
				      			var nextImgTitle = $("#hp-slideshow .rs-photo-gallery-images-list .image.next .record-title").text();
				        		$("#hp-slideshow .rs-photo-gallery-control.back").append('
');
				        		$("#hp-slideshow .rs-photo-gallery-control.next").append('
');

				        		// SET BOTTOM PADDING ON THE SLIDESHOW SO THAT EVERYTHING IS VISIBILE ABOVE THE CHANNEL BAR
				        		slideshowPad();

				        		// SET CONTROL HOVER
				        		$(".rs-photo-gallery-control").hover(function() {
				        			$(".rs-photo-gallery-control-img", this).fadeIn("fast");
				        		}, function () {
				        			$(".rs-photo-gallery-control-img", this).stop(true, true).fadeOut("fast");
				        		});
				        	}
					});
				}
			}
		}
	}

	function slideshowPad() {
		var channelBarH = $("#gb-channel-list-outer").outerHeight(true);

		$("#hp-slideshow .rs-photo-gallery-container, #hp-slideshow .rs-photo-gallery-images, #hp-slideshow .rs-photo-gallery-images-list, #hp-slideshow .rs-photo-gallery-images-list li, #hp-slideshow .rs-photo-gallery-desc-outer, #hp-slideshow .rs-photo-gallery-bullets-outer").css("padding-bottom", channelBarH + "px");
	}

	function headerGlobalH() {
		$("#gb-header-globals-outer").height("auto");

		var headerH = $("#gb-header").outerHeight(true);
		$("#gb-header-globals-outer").css("height", headerH + "px");
	}

	function appView(){
		//DOC dont add app into accordion if it doesnt have a header
		$("#hp-content-outer div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}
		});

		//DOC accordion header click
		$("#hp-content-outer div.ui-widget-header").click(function(){
			var clickedApp = $(this).parent().parent().attr("id");

			$("#hp-content-outer div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");

			$("html, body").animate({
				scrollTop: $("#" + clickedApp).offset().top
			}, 500, function(){
				$("html, body").stop(true, false);
			});

			// MANUALLY TRIGGER THE WINDOW RESIZE EVENT
			// TO MAKE SURE APP SLIDERS PROPERLY RESIZE
			$(window).trigger("resize");
		});
	}	

	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
        }

	function dynamicContent(){
		// ********** MYSTART ********** //
		//DOC fixes search box not allowing spaces
		$("#sw-mystart-search").unbind();

		//DOC replace mystart dropdown arrows
		$(".sw-dropdown-arrow").addClass("euphoria-icon").text("d");

		//DOC #swsignin-txt-username -- AUTO-FOCUS USERNAME SIGNIN FORM FIELD #swsignin-txt-username
		$("#swsignin-txt-username").focus();

        $("#gb-mobile-mystart").on("click keydown", ".sw-mystart-button.popular > a", function(e) {
        	if(e.type == "click" || e.type == "keydown" && e.keyCode == csKeyCodes.enter || e.type == "keydown" && e.keyCode == csKeyCodes.space) {
                e.preventDefault();
                if($("#gb-mobile-mystart-outer .gb-global-icons-outer").hasClass("active")) {
                    $("#gb-mobile-mystart-outer .gb-global-icons-outer").slideUp(function() {
                        $(this).removeClass("active");
                    }).attr("aria-hidden", "true");
                } else {
                    $("#gb-mobile-mystart-outer .gb-global-icons-outer").slideDown(function() {
                        $(this).addClass("active");
                        $("#gb-mobile-mystart-outer .gb-global-icons a:first-child").focus();
                    }).attr("aria-hidden", "false");
                }
            }
        });

        $("#gb-mobile-mystart-outer .gb-global-icons a").keydown(function(e) {
        	switch(e.keyCode) {
            	case csKeyCodes.up:
            		e.preventDefault();
                    $("#gb-mobile-mystart-outer .sw-mystart-button.popular > a").focus();
            	break;
                case csKeyCodes.right:
            		e.preventDefault();
                    if($(this).is(":last-child")) {
                    	$("#gb-mobile-mystart-outer .gb-global-icons a:first-child").focus();
                    } else {
                    	$(this).next("a").focus();
                    }
            	break;
                case csKeyCodes.left:
            		e.preventDefault();
                    if($(this).is(":first-child")) {
                    	$("#gb-mobile-mystart-outer .gb-global-icons a:last-child").focus();
                    } else {
                    	$(this).prev("a").focus();
                    }
            	break;
                case csKeyCodes.esc:
            		e.preventDefault();
                    $("#gb-mobile-mystart-outer .gb-global-icons-outer").slideUp(function() {
                        $(this).removeClass("active");
                        $("#gb-mobile-mystart-outer .sw-mystart-button.popular > a").focus();
                    }).attr("aria-hidden", "true");
            	break;
            }
        });

		$("#gb-mystart .sw-mystart-button.popular").hover(function() {
			$("#gb-mystart .gb-global-icons-outer").slideDown("fast").attr("aria-hidden", "false");
		}, function() {
			$("#gb-mystart .gb-global-icons-outer").stop(true, true).slideUp("fast").attr("aria-hidden", "true");
		});

        $("#gb-mystart").on("click keydown", ".sw-mystart-button.popular > a", function(e) {
        	if(e.type == "click" || e.type == "keydown" && e.keyCode == csKeyCodes.enter || e.type == "keydown" && e.keyCode == csKeyCodes.space) {
                e.preventDefault();
                if($("#gb-mystart .gb-global-icons-outer").hasClass("active")) {
                    $("#gb-mystart .gb-global-icons-outer").slideUp(function() {
                        $(this).removeClass("active");
                    }).attr("aria-hidden", "true");
                } else {
                    $("#gb-mystart .gb-global-icons-outer").slideDown(function() {
                        $(this).addClass("active");
                        $("#gb-mystart .sw-mystart-button.popular .gb-global-icons a:first-child").focus();
                    }).attr("aria-hidden", "false");
                }
            }
        });

        $("#gb-mystart .sw-mystart-button.popular").focusout(function() {
            var $this = $(this);

            setTimeout(function() {
                if(!$this.is(":focus") && !$this.find(":focus").length) {
                    $("#gb-mystart .gb-global-icons-outer").slideUp(function() {
                        $(this).removeClass("active");
                    }).attr("aria-hidden", "true");
                }
            }, 10);
        });

        $(".sw-mystart-button.popular .gb-global-icons a").keydown(function(e) {
        	switch(e.keyCode) {
            	case csKeyCodes.up:
            		e.preventDefault();
                    $(".sw-mystart-button.popular > a").focus();
            	break;
                case csKeyCodes.right:
            		e.preventDefault();
                    if($(this).is(":last-child")) {
                    	$(".sw-mystart-button.popular .gb-global-icons a:first-child").focus();
                    } else {
                    	$(this).next("a").focus();
                    }
            	break;
                case csKeyCodes.left:
            		e.preventDefault();
                    if($(this).is(":first-child")) {
                    	$(".sw-mystart-button.popular .gb-global-icons a:last-child").focus();
                    } else {
                    	$(this).prev("a").focus();
                    }
            	break;
                case csKeyCodes.esc:
            		e.preventDefault();
                    $("#gb-mystart .gb-global-icons-outer").slideUp(function() {
                        $(this).removeClass("active");
                        $("#gb-mystart .sw-mystart-button.popular > a").focus();
                    }).attr("aria-hidden", "true");
            	break;
            }
        });




		// ********** HEADER ********** //
		var taglineToggle = 'true';
		var sitenameToggle = 'true';
		if(taglineToggle == "false" && sitenameToggle == "false") {
			$("#gb-sitename-outer").hide();
		}
		//DOC add logo
		var logoSrc = jQuery.trim("/cms/lib/OH01914554/Centricity/Template/GlobalAssets/images///logos/Fairport_Insignia_Red.png");
		if(logoCheck == "true") {
			var srcSplit = logoSrc.split("/");
			var srcSplitLen = srcSplit.length;
			if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
				$('').one("load", function() {
					$(".gb-logo").append($(this)).children().wrap('');

					// APPLY PADDING TO THE SITE NAME AND TAGLINE
					padSiteName();

					// MANUALLY TRIGGER THE WINDOW RESIZE EVENT
					// TO MAKE SURE SLIDESHOW PROPERLY SIZES TO VIEWPORT
					$(window).trigger("resize");
				});
			} else {
				$('.gb-logo').append($('')).children().wrap('');
			}
		}

		// HEADER GLOBAL TARGETS
		var global1Target = "false";
		var global2Target = "false";
		var global3Target = "false";
		if(global1Target == "true") {
			$(".gb-header-global.global-one a").attr("target", "_blank");
		}
		if(global2Target == "true") {
			$(".gb-header-global.global-two a").attr("target", "_blank");
		}
		if(global3Target == "true") {
			$(".gb-header-global.global-three a").attr("target", "_blank");
		}

		// ********** CHANNEL BAR ********** //
		//DOC add first class to first channel item
		$("li.sw-channel-item").first().addClass("first");
		//DOC add last class to last channel item
		$("li.sw-channel-item").last().addClass("last");

		//DOC set icon for home channel
		var firstChannelText = $("li.sw-channel-item:first > a span").text().toLowerCase();
		if(firstChannelText == "home") {
			$("li.sw-channel-item:first > a").attr("aria-label", "Home");
            $("li.sw-channel-item:first > a span").addClass("euphoria-icon").text("h");
			$("li.sw-channel-item").first().addClass("home");
		}
		//DOC set icon for calendar channel
		var lastChannelText = $("li.sw-channel-item:last > a span").text().toLowerCase();
		if(lastChannelText == "calendar") {
        	$("li.sw-channel-item:last > a").attr("aria-label", "Calendar");
			$("li.sw-channel-item:last > a span").addClass("euphoria-icon").text("c");
			if($("li.sw-channel-item.home").length) {
				$("li.sw-channel-item").last().addClass("calendar").insertAfter($("li.sw-channel-item").first());
			} else {
				$("li.sw-channel-item").last().addClass("calendar").insertBefore($("li.sw-channel-item").first());
			}
		}
		// SET CLASS TO MAKE CHANNEL DROPDOWNS DROP UP IF SCROLL POSITION IS LESS THAN 40 PIXELS FROM TOP EDGE OF WINDOW
		if($(".hp").length && slideshowCheck == "true") {
			if ($(window).scrollTop() <= 60) {
				$("#gb-channel-list").addClass("channel-drop-up");
			} else {
				$("#gb-channel-list").removeClass("channel-drop-up");
			}
		}
		// CHECK IF CHANNEL HAS DROPDOWN
		$(".sw-channel-item").each(function() {
			if($(".sw-channel-dropdown", this).length) {
				$(this).addClass("has-dropdown");
			}
		});

		// ********** BODY ********** //
		//DOC rename first breadcrumb
		$("li.ui-breadcrumb-first > a > span").text("Home");
		//DOC replace more-link text
		$("a.more-link span, a.view-calendar-link span").text("VIEW ALL");
		//DOC auto-focus username signin field
		$("#swsignin-txt-username").focus();
		//DOC center signed out message and signin button
        	if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
        		$("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
        		//DOC add signed out breadcrumb
        		$(".ui-breadcrumbs").append("
Signed Out");
        	}
        	//DOC add sign in breadcrumb
        	if($("#swlogin").length){
        		$(".ui-breadcrumbs").append("
Sign In");
        	}
        	//DOC add register breadcrumb
        	if($(".spn").length && $("#swageprompt-txt-birthmonth").length && $("#swageprompt-txt-birthday").length && $("#swageprompt-txt-birthyear").length) {
        		$(".ui-breadcrumbs").append("
Register");
        	}

        	//DOC add read more button to each healine
        	$(".ui-widget.app.headlines").each(function(){
        		$(".ui-article", this).each(function(){
        			var headlineURL = $("h1 a", this).attr("href");
                    if(headlineURL !== undefined){ //-- So this does not get added to the Articles Sub page
        				$(this).append('READ MORE');
                    }
        		});
        	});


		// REMOVE BOTTOM CONTENT REGION IF NO APPS EXIST
		if(!$(".hp-content.bottom .ui-widget.app").length) {
			$(".hp-content.bottom").hide();
		}

		// MOVE VIEW ALL LINKS FOR HEADLINES AND UPCOMING EVENTS
		$("#sw-content-container1.ui-hp .ui-widget.app").each(function() {
			if($(this).hasClass("headlines")) {
				if($(".more-link", this).length) {
					if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || !$(".ui-widget-header h1", this).length || $(".ui-widget-header h1", this).text() == "") {
						$(".ui-widget-detail", this).prepend($(".more-link", this).addClass("cs-header-view-all no-header").text("VIEW ALL"));
					} else {
						$(".ui-widget-header h1", this).append($(".more-link", this).addClass("cs-header-view-all").text("VIEW ALL"));
					}
				}
			} else if($(this).hasClass("upcomingevents")) {
				if($(".view-calendar-link", this).length) {
					if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || !$(".ui-widget-header h1", this).length || $(".ui-widget-header h1", this).text() == "") {
						$(".ui-widget-detail", this).prepend($(".view-calendar-link", this).addClass("cs-header-view-all no-header").text("VIEW ALL"));
					} else {
						$(".ui-widget-header h1", this).append($(".view-calendar-link", this).addClass("cs-header-view-all").text("VIEW ALL"));
					}
				}
			}
		});

		// ADD LAST CLASS TO LAST EVENT AND LAST HEADLINE
		$(".upcomingevents li").last().addClass("last");
		$(".headlines li").last().addClass("last");

		// CHANGE ORDER OF UPCOMING EVENTS TITLE AND TIME
		$(".upcomingevents .ui-article-description").each(function() {
			$(this).prepend($(".sw-calendar-block-title", this));
		});

		// EVENTS SCROLLER
        	if($(".hp").length) {
        		$("#sw-content-container1.region.ui-hp div.ui-widget.app.upcomingevents").each(function() {
		    		$(".ui-widget-detail", this).wrap('

');
