			if($(".sw-mystart-button.register").length){
				$(".gb-header-global.manager").append($(".sw-mystart-button.register"));
			}

    }

	function buildStyleSheet() {
		var primaryColor75 = "rgba(" + hexToRgb('#FFFFFF') + ", 0.75)";
		var primaryColor80 = "rgba(" + hexToRgb('#FFFFFF') + ", 0.80)";
		var primaryColor97 = "rgba(" + hexToRgb('#FFFFFF') + ", 0.97)";
		var accentColor90 = "rgba(" + hexToRgb('#5C000A') + ", 0.90)";

		var dynStyleSheet = document.createElement('style');

		if(dynStyleSheet) {
			dynStyleSheet.setAttribute('type', 'text/css');
			var head = document.getElementsByTagName('head')[0];

			if(head) {
				head.insertBefore(dynStyleSheet, head.childNodes[0]);
			}

			var dynStyles = '.hp #gb-mystart-outer {' +
						'background: ' + primaryColor80 + ';' +
					'}' +
					'#gb-mystart .sw-mystart-dropdown .sw-dropdown {' +
						'background: ' + primaryColor80 + ';' +
					'}' +
					'#gb-mystart .gb-global-icons-outer {' +
						'background: ' + accentColor90 + ';' +
					'}' +
					'@media (max-width: 1023px) {' +
						'.gb-footer-outer.top, .gb-footer-outer.bottom {' +
							'background: ' + primaryColor97 + ' !important;' +
						'}' +
					'}';

			var rules = document.createTextNode(dynStyles);

			if(dynStyleSheet.styleSheet){ // IE
				dynStyleSheet.styleSheet.cssText = dynStyles;
			} else {
				dynStyleSheet.appendChild(rules);
			}
		}
	}

	function hexToRgb(h){
	        var r = parseInt((cutHex(h)).substring(0,2),16);
	        var g = parseInt((cutHex(h)).substring(2,4),16);
	        var b = parseInt((cutHex(h)).substring(4,6),16);
	        return r + ', ' + g + ', ' + b;
	}

	function cutHex(h) {
	        return (h.charAt(0)=="#") ? h.substring(1,7):h;
	}

	function padSiteName() {
		$(".gb-sitename").css("padding-left", "0px");

		var logoW = $("#gb-header .gb-logo img").width();
		var logoH = $("#gb-header .gb-logo img").height();
		$(".gb-sitename, .gb-footer-site-info").css("padding-left", (logoW + 20) + "px");

		if($(window).width() < 1024) { // 768-
			$(".gb-sitename-outer").css("height", logoH + "px");
		} else {
			$(".gb-sitename-outer").removeAttr("style");
		}
	}

	var adjustChannelDropdowns = function(){
		if ($(window).scrollTop() <= 40) {
			if($("#hp-slideshow").hasClass("false") || $(".sp").length || $(".spn").length) {
				$("#gb-channel-list").removeClass("channel-drop-up");
			} else {
				$("#gb-channel-list").addClass("channel-drop-up");
			}
		} else {
			$("#gb-channel-list").removeClass("channel-drop-up");
		}
	}

	var stickyChannelBar = function(){
		var channelBarOffset = $("#gb-channel-list-outer").offset().top;
		if ($(window).scrollTop() <= channelBarOffset) {
			if(isSticky) {
				$("#gb-page").removeClass("sticky-channel-bar");
				$("#gb-channel-list-outer").css("height", "auto");

				isSticky = 0;
			}
		} else {
			if(!isSticky) {
				$("#gb-page").addClass("sticky-channel-bar");
				$("#gb-channel-list-outer").css("height", $("#gb-channel-list").height() + "px");

				isSticky = 1;
			}
		}

        if($(".spn").length || $(".sp").length && $(".ui-widget.app.calendar").length) {
            if($(".wcm-controls").hasClass("wcm-stuck")) {
                $(".wcm-controls").css("margin-top", $("#gb-channel-list-outer").outerHeight(true));
            }
        }
	}

	function stickyChannelBarReset(){
		if(isSticky) {
			$("#gb-page").removeClass("sticky-channel-bar");
			$("#gb-channel-list-outer").css("height", "auto");


			isSticky = 0;
		}
	}

	function gbSearch() {
		$(".sw-mystart-button.search").on("click keydown", "a", function(e) {
        	if(e.type == "click" || e.type == "keydown" && e.keyCode == csKeyCodes.enter || e.type == "keydown" && e.keyCode == csKeyCodes.space) {
            	e.preventDefault();

                if(!$("#gb-search").hasClass("active")) {
                    $("#gb-search").slideDown(function() {
                        $(this).addClass("active");
                        $(".gb-search-input").focus();
                        $(".sw-mystart-button.search > a").attr("aria-label", "Close Search");
                    }).attr("aria-hidden", "false");
                } else {
                    $("#gb-search").slideUp(function() {
                        $(this).removeClass("active");
                        $(".sw-mystart-button.search > a").attr("aria-label", "Open Search");
                    }).attr("aria-hidden", "true");
                }
            }
        });

        $(document).keydown(function() {
        	setTimeout(function() {
            	if($("#gb-search").hasClass("active") && !$(".sw-mystart-button.search").find(":focus").length) {
                	$("#gb-search").slideUp(function() {
                        $(this).removeClass("active");
                    }).attr("aria-hidden", "true");
                }
            }, 100);
        });


		$(".gb-search-button").click(function(e){
		    e.preventDefault();
		    var searchForm = $(this).parent();
		    window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $(".gb-search-input", searchForm).val();
		});
		$(".gb-search-form").submit(function(e){
		    e.preventDefault();
		    var searchForm = $(this).parent();
		    window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $(".gb-search-input", searchForm).val();
		});
		$(".gb-search-input").focus(function() {
		    if($(this).val() == "Search...") {
		        $(this).val("");
		    }
		});
		$(".gb-search-input").blur(function() {
		    if($(this).val() == "") {
		        $(this).val("Search...");
		    }
		});
	}

	function calendarListView(){
		//DOC auto click function for schoolwires calendar list view for small mobile devices
		if(!$("a.ui-btn-toggle.list").hasClass("active")){
			$("a.ui-btn-toggle.list").click();
		}
	}

