    function LoadRating9533861() {
    var ModuleInstanceID = 953;
    var FlexDataID = 3861;

        GetContent("https://www.fhevs.org//site/UserControls/Rating/RatingListWrapper.aspx?ModuleInstanceID=" + ModuleInstanceID + "&FlexDataID=" + FlexDataID + "&IsDisabled=true", "swmoduleinstancerating-pnl-rating9533861", 2);
}

    function InsertRating9533861() {
    var ModuleInstanceID = 953;
    var FlexDataID = 3861;
    var Rating = $('#swmoduleinstancerating-hid-rating9533861').val();
    var data = "{ModuleInstanceID: " + ModuleInstanceID + ", " +
                "FlexDataID: " + FlexDataID + ", " +
                "Rating: " + Rating + "} ";  
    var success = 'LoadRating9533861();';
    var failure = "CallControllerFailure(result[0].errormessage);";

    if (Rating != '' && Rating !== undefined) {
        CallController("https://www.fhevs.org//site/UserControls/Rating/RatingController.aspx/InsertRating", data, success, failure);
    }
}

    $(document).ready(function() {
        CheckScript('Rating');
        CheckScript('Metadata');

        // initialize rating script
        $("input[name='swmoduleinstanceratinghidrating9533861']").rating({
            callback: function() {
                Getswmoduleinstanceratinghidrating9533861Values();
                InsertRating9533861($('#swmoduleinstancerating-hid-rating9533861').val());
            }
        });
    });
    
    // fill hidden input with value clicked
    function Getswmoduleinstanceratinghidrating9533861Values() {
        var selItem;

        $("input[name='swmoduleinstanceratinghidrating9533861']").each(function(i) {
            if ($(this).is(':checked')) {
                $('#swmoduleinstancerating-hid-rating9533861').val((i + 1) / 2);
            }
        });
    }

