    function LoadRating8103849() {
    var ModuleInstanceID = 810;
    var FlexDataID = 3849;

        GetContent("https://www.fhevs.org//site/UserControls/Rating/RatingListWrapper.aspx?ModuleInstanceID=" + ModuleInstanceID + "&FlexDataID=" + FlexDataID + "&IsDisabled=true", "swmoduleinstancerating-pnl-rating8103849", 2);
}

    function InsertRating8103849() {
    var ModuleInstanceID = 810;
    var FlexDataID = 3849;
    var Rating = $('#swmoduleinstancerating-hid-rating8103849').val();
    var data = "{ModuleInstanceID: " + ModuleInstanceID + ", " +
                "FlexDataID: " + FlexDataID + ", " +
                "Rating: " + Rating + "} ";  
    var success = 'LoadRating8103849();';
    var failure = "CallControllerFailure(result[0].errormessage);";

    if (Rating != '' && Rating !== undefined) {
        CallController("https://www.fhevs.org//site/UserControls/Rating/RatingController.aspx/InsertRating", data, success, failure);
    }
}

    $(document).ready(function() {
        CheckScript('Rating');
        CheckScript('Metadata');

        // initialize rating script
        $("input[name='swmoduleinstanceratinghidrating8103849']").rating({
            callback: function() {
                Getswmoduleinstanceratinghidrating8103849Values();
                InsertRating8103849($('#swmoduleinstancerating-hid-rating8103849').val());
            }
        });
    });
    
    // fill hidden input with value clicked
    function Getswmoduleinstanceratinghidrating8103849Values() {
        var selItem;

        $("input[name='swmoduleinstanceratinghidrating8103849']").each(function(i) {
            if ($(this).is(':checked')) {
                $('#swmoduleinstancerating-hid-rating8103849').val((i + 1) / 2);
            }
        });
    }

