    function loadCss(x) {
        try {
            var cssStr = '\
		#middle-nav ul li a, nav#top-nav li a {font-size:' + (12 + x) + 'pt;} h1, #content-right header {font-size:' + (24 + x) + 'px;} #news-list .article .news-summary {font-size:' + (11 + x) + 'px;} section#upcoming-events ul a{font-size:' + (11 + x) + 'px;} .aside-content li a{font-size:' + (11 + x) + 'px;} .foot2 li a{font-size:' + (11 + x) + 'pt;} .foot2 li ul li a{font-size:' + (10 + x) + 'pt;} #news-list .article a.news-title{font-size:' + (14 + x) + 'pt;} #school-map span{font-size:' + (9 + x) + 'pt;}.foot2 li ul li a, .sf-vertical li a, ul.nav.navbar-nav > li > a{font-size:' + (11 + x) + 'px !important;}  html, body, table, tr, td, div, .panel, .panel p, span, applet, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre, a, abbr, acronym, address, big, cite, code, del, dfn, img, ins, kbd, q, s, samp, small, strike, sub, sup, var, i, center, dl, dt, dd, ol, ul, li, fieldset, form, label, legend, article, aside, canvas, details, embed, figure, figcaption, footer, header, footer,hgroup, menu, nav, output, ruby, section, summary, time, mark, audio, video{font-size:' + (11 + x) + 'pt !Important;}\
		';
            if (cssNode.styleSheet) {
                cssNode.styleSheet.cssText = cssStr; // for IE
            } else {
                var cssText = document.createTextNode(cssStr);
                cssNode.appendChild(cssText); // breaks ie
                //cssNode.innerHTML = cssStr; // breaks saffari
            }
            if (!document.getElementById("resizingText")) headID.appendChild(cssNode);
        } catch (err) {
        }
    }

    function increaseFontSize() {
        if (incdec < 13) {
            incdec++;
            loadCss(incdec);
            createCookie('textsize', incdec, 1);
        }
    }

    function decreaseFontSize() {
        if (incdec > 0) {
            incdec--;
            loadCss(incdec);
            createCookie('textsize', incdec, 1);
        }
    }
    ////////////////////////////////////////////////
    function reverttextchanges() {
        eraseCookie('textsize');
        eraseCookie('colorchange');
        location.reload();
    }

    $(document).ready(function () {
        var x = readCookie('textsize');
        if (x && x != 0) {
            x = parseInt(x);
            incdec = x;
            loadCss(x);
        }

        var y = readCookie('colorchange');
        if (y && y != 0) {
            y = parseInt(y);
            incdec = y;
            loadCss2(y);
        }
    });

