    var CreativeTemplate = {
    	// PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD
        "IconList": "",
        "SLMList": "",

        // METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            this.SetTemplateProps();
            this.JsMediaQueries();
            this.MyStart();
            this.Header();
            this.GlobalIcons(_this);
            this.Dropdown();
            this.Body();
            this.Footer();
            this.Slideshow();
            this.SLM(_this);
            this.RsMenu(_this);
            this.AppAccordion();
            this.Search();




            $(document).ready(function(){
            	_this.ChannelBar();
                _this.WindowResize();
                _this.WindowScroll();

            	//$('#sw-mystart-outer').remove();

            	$('#translate-spanish').attr('href', $('#gb-translate-dropdown ul li a:contains("Spanish")').attr('href'));




            });

            $(window).scroll(function(){ _this.WindowScroll(); });
            $(window).load(function(){ _this.WindowLoad(); });
            $(window).resize(function(){ _this.WindowResize(); });


        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },

        "WindowLoad": function() {

            //this.CalendarListView();


            // MAKE NAVIGATION APPS ACCESSIBLE
            $(".ui-widget.app.navigation").csAccessibleNavigationApp();

            if($(".translated-ltr").length){
            	$("#gb-channel-list-outer").css("top","38px");
            } else {
            	$("#gb-channel-list-outer").css("top","0px");
            }



        },

        "WindowResize": function() {

            this.JsMediaQueries();
            //this.CalendarListView();

            $('.gb-mystart-dropdown:not(.findit) .gb-dropdown').width(($('#gb-channel-list').offset().left + 350) + 'px');


            $('#gb-findit-dropdown').css({
            		//'left' : '-' + ($('#gb-header').width() - 325) + 'px', //commented out by Matt B - case#3975381 - 09.25.18
                	'width' : $('#gb-header').width() + 'px'
                });

            var wW = $(window).width();

            //added by Matt B - case#3975381 - 09.25.18
            if(wW < 1380 && wW >= 1024) {
            	$('#gb-findit-dropdown').css({
                	'width' : ($('#gb-header').width() - 70) + 'px'
                });
            }
            //end addition

            if(wW < 1024) {

                $('#footer-tagline').removeClass('cs-fc').addClass('cs-fcc');

                $('#hp-content-upper .headlines .ui-articles > li:nth-child(4)').hide().attr('aria-hidden', 'true');

                $('#hp-content-upper .headlines .ui-articles > li:nth-child(3)').show().attr('aria-hidden', 'false');

				$('#gb-footer').addClass('cs-aifs').removeClass('cs-aic');

                $('.gb-info-wrapper').removeClass('cs-fw');

                if(wW < 768) {

                    $('#hp-content-upper .headlines .ui-articles > li:nth-child(3)').hide().attr('aria-hidden', 'true');

                    $('.gb-info-wrapper').addClass('cs-fw');

                    $('#gb-channel-list').append('');

                    if(wW < 640) {

                        $('.hp .ui-widget-header, .sp.one .ui-widget-header').append('');

                        $('#gb-schoolwires-footer-inner').removeClass('cs-fw cs-jc').addClass('cs-jfs');

                        if(wW < 480) {

                            $('#gb-schoolwires-footer-inner').addClass('cs-fw cs-jc').removeClass('cs-jfs');

                        }

                    }

                }

            } else {

                $('#footer-tagline').addClass('cs-fc').removeClass('cs-fcc');

                $('#hp-content-upper .headlines .ui-articles > li:nth-child(4)').show().attr('aria-hidden', 'false');

				$('#gb-footer').addClass('cs-aifs').removeClass('cs-aic');

            }

            if($(".translated-ltr").length){
            	$("#gb-channel-list-outer").css("top","38px");
            } else {
            	$("#gb-channel-list-outer").css("top","0px");
            }



        },

        "WindowScroll": function(){

            var wScroll = $(window).scrollTop();




            //--> To top button
            if(wScroll >= $('.engage-to-top').offset().top) {
                if($(window).width() >= 1024) {
                    $('#cj-to-top').fadeIn().addClass('fixed').attr('aria-hidden', 'false').attr('tabindex', '0');
                }
            } else {
                $('#cj-to-top').fadeOut().attr('aria-hidden', 'true').attr('tabindex', '-1');
            }

            if($(window).width() < 1024) {
            	$('#cj-to-top').hide().removeClass('fixed');
            }

            if($(".translated-ltr").length){
            	$("#gb-channel-list-outer").css("top","38px");
            } else {
            	$("#gb-channel-list-outer").css("top","0px");
            }

        },

        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 

                break;
                case "768":

                break;
                case "640":

                break;
                case "480":

                break;
                case "320":

                break;
            }
        },

        "CookieMonster": {
         	"createCookie": function(name, value, days) {
				var expires;

				if (days) {
					var date = new Date();
					date.setTime(date.getTime() + (days * 24 * 60 * 60 * 1000));
					expires = "; expires=" + date.toGMTString();
				} else {
					expires = "";
				}
				document.cookie = escape(name) + "=" + escape(value) + expires + "; path=schoolwires.net";
			},

			"readCookie": function(name) {
				var nameEQ = escape(name) + "=";
				var ca = document.cookie.split(';');
				for (var i = 0; i < ca.length; i++) {
					var c = ca[i];
					while (c.charAt(0) === ' ') c = c.substring(1, c.length);
					if (c.indexOf(nameEQ) === 0) return unescape(c.substring(nameEQ.length, c.length));
				}
				return null;
			},

			"eraseCookie": function(name) {
				CreativeTemplate.CookieMonster.createCookie(name, "", false);
			}
        },

        "MyStart": function() {

        	// ADD TRANSLATE
        	$("#gb-translate-dropdown").creativeTranslate({
                "type": 2,
                "advancedOptions": {
                    "addMethod": "append",
                    "dropdownHandleText": "Translate",
                    "customElement": { // ONLY FOR FRAMESET AND API VERSIONS
                        "useCustomElement": true,
                        "translateItemsList": true, // true = THE TRANSLATE ITEMS WILL BE AN UNORDERED LIST, false = THE TRANSLATE ITEMS WILL JUST BE A COLLECTION OF  TAGS
                        "customElementMarkup": "[$CreativeTranslateListItems$]" // CUSTOM HTML MARKUP THAT MAKES THE CUSTOM TRANSLATE ELEMENT/STRUCTURE - USE [$CreativeTranslateListItems$] ACTIVE BLOCK IN THE MARKUP WHERE THE TRANSLATE ITEMS SHOULD BE ADDED
                	}
                }
            });

            $("#translate-spanish").click(function(e) { //added by Matt - case#4021207 - 11.19.18
                  e.preventDefault();

            // GET THE NEW LANGUAGE CODE
            //CreativeTemplate.CookieMonster.eraseCookie("googtrans");
            CreativeTemplate.CookieMonster.createCookie("googtrans", $(this).attr('data-lang-code'));
            location.reload();
              }).keydown(function(e) {
                      // ENTER KEY
                      if(e.keyCode == 13) {
                            CreativeTemplate.CookieMonster.createCookie("googtrans", $(this).attr('data-lang-code'));
                            location.reload();
                      }
