        "SLM": function(_this) { /* added by Matt B - case#3180971 - 06.26.18 */
        	$(function() {
                $.schoolListMinibase({
                    'miniBaseDomainID': 4,
                    'miniBaseModuleInstanceID': 1184,
                    'cache': true,
                    'schoolListReady': function(schoolList) {
                        /*
                        The returned value is an array of objects and each object is a school record.
                        The fields are: schoolList.schoolName, schoolList.schoolType, schoolList.schoolUrl, schoolList.schoolAlias, schoolList.schoolVisible
                        */

                        var schools = '';

                        for(var i in schoolList) {
                            if(schoolList[i].schoolVisible.toLowerCase() == 'true') {
                                schools += '
' + schoolList[i].schoolName + '';
                            }
                        }

                        $('#gb-school-dropdown .gb-school-list').html(schools);

                    	$(".rs-menu-option.schools .rs-menu-option-sublist").html($("#gb-school-dropdown ul").html());
                    	$(".rs-menu-option.schools .rs-menu-option-sublist").prepend('
Back

Schools');

                    }
                });
            });



        },

