            function modEvents(){
                if (!$(".upcomingevents .joel-month").length) {
                    $(".upcomingevents").modEvents({
                        columns     : "yes",
                        monthLong   : "no",
                        dayWeek     : "yes"
                    });
                    eventsByDay(".upcomingevents .ui-articles");
                }
            } 

            function replaceAll(str, tar, rep) {
            	return str.split(tar).join(rep);
            }

            function eventsByDay(container) {
                var countLi = 0;
                $(".ui-article", container).each(function(num, article){
                    if (!$(article).find("h1.ui-article-title.sw-calendar-block-date").size()){
                        var moveArticle = $(article).html();
                        $(article).parent().prev().children().children().next().append(moveArticle);
                        $(article).parent().remove();


                    }

                    $('.sw-calendar-block-time', article).each(function(index, time){

                        var aTime = $(time).text();

                        aTime = replaceAll(aTime, ' AM', 'am');
                        aTime = replaceAll(aTime, ' PM', 'pm');

                        $(time).text(aTime);

                    });

                    //--> Flex it and move day
                    $('.upcoming-column.left', article).addClass('cs-fcc cs-aic').append($('.joel-day', article));

                    if($("#gb-page").hasClass("sp")) {

                        $(article).addClass('cs-fc');
                        $('.upcoming-column.right .ui-article-description', article).first().prepend($('.joel-day', article));

                    }

                });

            }

            //--> Only allow 5 days to be show on
            $('#hp-content-upper .upcomingevents .ui-articles > li').each(function(index, article){

            	if(index > 4) {

                	$(article).remove();

                }

            });

            //--> Check apps in middle region 
            $('#sw-content-container4').addClass('cs-fc cs-jfs cs-fw');

            if($('#sw-content-container4 > div').length == 1) {
            	$('#sw-content-container4').addClass('single');
            } else if($('#sw-content-container4 > div').length == 2) {
            	$('#sw-content-container4').addClass('double');
            } else {
            	$('#sw-content-container4').addClass('thirds');
            }

