            $('#sw-content-container5').addClass('cs-fc cs-jsb cs-fw');

            if($('#sw-content-container5 > div').length == 1) {
            	$('#sw-content-container5').addClass('single');
            } else if($('#sw-content-container5 > div').length == 2) {
            	$('#sw-content-container5').addClass('double');
            } else {
            	$('#sw-content-container5').addClass('thirds');
            }

        },

