	function dynamicContent(){
		// ********** MYSTART ********** //
		//DOC replace mystart dropdown arrows
		$(".sw-myaccount-chevron").text("");
		$(".sw-dropdown-arrow").text("");
		$(".sw-mystart-dropdown .selector, .sw-mystart-dropdown .sw-dropdown-selected").text("Choose School");

		// ********** HEADER ********** //

		var siteName = "Greater Latrobe School District";
		var siteAlias = "greaterlatrobe";
		siteName = siteName.split(" ");
		var siteNameLength = siteName.length;
		if(siteNameLength == 3){
			siteNameEnd = jQuery.trim(siteName.splice(-2, siteName.length).toString().replace(/,/g, " "));	
		} else if((siteNameLength > 3) && (siteAlias != "greaterlatrobe") && (siteAlias != "mountainviewes")) {
			siteNameEnd = jQuery.trim(siteName.splice(-3, siteName.length).toString().replace(/,/g, " "));
		} else if((siteNameLength > 3) && (siteAlias = "greaterlatrobe")) {
			siteNameEnd = jQuery.trim(siteName.splice(-2, siteName.length).toString().replace(/,/g, " "));
		} else if((siteNameLength > 3) && (siteAlias = "mountainviewes")) {
			siteNameEnd = jQuery.trim(siteName.splice(-2, siteName.length).toString().replace(/,/g, " "));
		} else {
			siteNameEnd = jQuery.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
		}
		siteNameBegin = jQuery.trim(siteName.toString().replace(/,/g, " "));
		$("#gb-sitename").prepend("
" + siteNameEnd + "");
		$("#gb-sitename").prepend("
" + siteNameBegin + "");
		$("#footer-school").prepend("
" + siteNameEnd + "");
		$("#footer-school").prepend("
" + siteNameBegin + "");

		// ********** CHANNEL BAR ********** //
		//DOC add first class to first channel item
		$("li.sw-channel-item").first().addClass("first");
		//DOC add last class to last channel item
		$("li.sw-channel-item").last().addClass("last");

		// ********** CHANNEL BAR ********** //
		//DOC add first class to first channel item
		$("li.sw-channel-item").first().addClass("first");
		//DOC add last class to last channel item
		$("li.sw-channel-item").last().addClass("last");

		// ********** BODY ********** //
		//DOC rename first breadcrumb
		$("li.ui-breadcrumb-first > a > span").text("Home");
		//DOC replace more-link text
		$("a.more-link span").text("View All >>");
		$("a.view-calendar-link").text("View All >>");
		//DOC auto-focus username signin field
		$("#swsignin-txt-username").focus();
		//DOC center signed out message and signin button
        	if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
        		$("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
        		//DOC add signed out breadcrumb
        		$(".ui-breadcrumbs").append("
Signed Out");
        	}
        	//DOC add read full story button to each healine
        	$(".ui-widget.app.headlines").each(function(){
        		$(".ui-article", this).each(function(){
        			var headlineURL = $("h1 a", this).attr("href");
        			$(".ui-article-description", this).append('Read more >>');
        		});
        	});
        	//DOC add sign in breadcrumb
        	if($("#swlogin").length){
        		$(".ui-breadcrumbs").append("
Sign In");
        	}
        	head.ready("modEvents", function() {
		        	//MOD EVENTS
		        	$(".upcomingevents").modEvents({
				columns:"yes",
				monthLong:"no",
				dayWeek:"no"
			});
			eventsByDay(".upcomingevents .ui-articles");
			if($("div.ui-widget.app.upcomingevents .ui-article-description").text() == "There are no upcoming events to display.") {
				$("div.ui-widget.app.upcomingevents .ui-article-description").css("padding","0px");
			}
		});


		// ********** FOOTER ********** //
		//DOC get Schoolwires footer links
		var feedbackURL = $("div#sw-footer-links li:eq(0) a").attr("href");
		var termsURL = $("div#sw-footer-links li:eq(2) a").attr("href");
		var policyURL = $("div#sw-footer-links li:eq(1) a").attr("href");

		$("a#feedback").attr("href", feedbackURL);
		$("a#terms").attr("href", termsURL);
		$("a#policy").attr("href", policyURL);

		var footerLinks = '' +
				  '
Questions or Feedback? | ' +
				  '
Terms of Use | ' +
				  '
Schoolwires Privacy Policy (Updated)' +
				  '';
		$("#sw-footer-links").html(footerLinks);

		//SOCIAL ICON TOGGLES
		var facebookToggle = 'true';
		if(facebookToggle == "true") {
			$(".social-icon.facebook").show();
		}
		var twitterToggle = 'true';
		if(twitterToggle == "true") {
			$(".social-icon.twitter").show();
		}	

        $("#swlogin .ui-widget-header").append('
Sign in is for staff only.');
	}

	function eventsByDay(container) {
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();
			};
		});
	}

	function searchText() {
		//DOC custom mystart search placeholder
	        //SEARCH TEXT
	        $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
		$("div#sw-mystart-search input#sw-search-input").attr("value", "Search");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "Search") {
				$(this).val("");
			}
		});
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("Search");
			}
		});
	}

	function calendarListView(){
		//DOC auto click function for schoolwires calendar list view for small mobile devices
		if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
			$("a.ui-btn-toggle.list").click();
			$("a.ui-btn-toggle.month").click();
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
		}

		var getWidth = $(window).width();
		if(getWidth >= 640){ // 640+
			$(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
		} if(getWidth < 640){ // 480-
			$(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
		}
	}

	function imgResize(){
		var imgHeight = $("#hp-slideshow .rotating-images img").height();
		$("#hp-slideshow, #hp-slideshow .rotating-images").height(imgHeight);
	}

