            $.schoolListMinibase({
                'miniBaseDomainID': 4,
                'miniBaseModuleInstanceID': 2069,
                'cache': false,
                'schoolListReady': function(schoolList) {
                    /*
                    The returned value is an array of objects and each object is a school record.
                    The fields are: schoolList.schoolName, schoolList.schoolType, schoolList.schoolUrl, schoolList.schoolAlias, schoolList.schoolVisible
                    */

                    var schools = '';

                    for(var i in schoolList) {
                        if(schoolList[i].schoolVisible.toLowerCase() == 'true') {
                            schools += '
' + schoolList[i].schoolName + '';
                        }
                    }

                    $('.sw-mystart-dropdown.schools .sw-dropdown ul').html(schools);

                    //DOC schoolname split
                    if($("#gb-sitename h1").text() == "") {
                        var siteName = "Everman Independent School District";
                        $('.sw-mystart-dropdown.schools .sw-dropdown ul a').each(function() {
                            if($(this).attr("data-site-alias") == "eisd") {
                                siteName = $(this).text();
                            }
                        });
                        if(siteName.indexOf("Independent School District") > 0) {
                            siteName = siteName.split("Independent School District");
                            $("#gb-sitename h1, .gb.footer.one h1").html($.trim(siteName[0]));    
                            $("#gb-sitename h2, .gb.footer.one h2").html("Independent School District");
                        } else if(siteName.indexOf("Elementary School") > 0) {
                            siteName = siteName.split("Elementary School");
                            $("#gb-sitename h1, .gb.footer.one h1").html($.trim(siteName[0]));    
                            $("#gb-sitename h2, .gb.footer.one h2").html("Elementary School");                          
                        } else if(siteName.indexOf("Junior High School") > 0) {
                            siteName = siteName.split("Junior High School");
                            $("#gb-sitename h1, .gb.footer.one h1").html($.trim(siteName[0]));    
                            $("#gb-sitename h2, .gb.footer.one h2").html("Junior High School");                          
                        } else if(siteName.indexOf("High School") > 0) {
                            siteName = siteName.split("High School");
                            $("#gb-sitename h1, .gb.footer.one h1").html($.trim(siteName[0]));    
                            $("#gb-sitename h2, .gb.footer.one h2").html("High School");                          
                        } else if(siteName.indexOf("Intermediate School") > 0) {
                            siteName = siteName.split("Intermediate School");
                            $("#gb-sitename h1, .gb.footer.one h1").html($.trim(siteName[0]));    
                            $("#gb-sitename h2, .gb.footer.one h2").html("Intermediate School");                          
                        } else if(siteName.indexOf("Grade Campus") > 0) {
                            siteName = siteName.split("Sixth Grade Campus");
                            $("#gb-sitename h1, .gb.footer.one h1").html($.trim(siteName[0]));    
                            $("#gb-sitename h2, .gb.footer.one h2").html("Sixth Grade Campus");                          
                        }
                    }
					$("#gb-sitename h3").html("One Goal...One Purpose...Student Success");
                    $("#gb-sitename h1:empty, #gb-sitename h2:empty, #gb-sitename h3:empty").remove();
                }
            });

            $(document).off("click", ".sw-mystart-dropdown");
            $(document).on("click", ".sw-mystart-dropdown", function(e) {
                $(".sw-dropdown", this).slideDown(300, "swing");
            });
            $(".sw-mystart-dropdown").hover(function(e) {
                e.preventDefault();
            }, function() {
                $(".sw-dropdown",this).slideUp(300, "swing").attr('aria-hidden', 'true');
            });

            $(document).off("focus", ".sw-mystart-dropdown");
            $(document).on("focus", ".sw-mystart-dropdown", function(e) {
                $(".sw-dropdown", this).slideDown(300, "swing").attr('aria-hidden', 'false');
            });

            $(document).off("keydown", ".sw-mystart-dropdown");
            $(document).on("keydown", ".sw-mystart-dropdown", function(e) {
                switch(e.keyCode) {
                    case _this.KeyCodes.tab:
                    	if($(this).hasClass("schools")) {
                        	e.preventDefault();
                            $(".sw-mystart-dropdown.translate").focus();
                        } else if($(this).hasClass("translate")) {
                        	e.preventDefault();
                            $(".sw-mystart-dropdown.users").focus();
                        } else if($(this).hasClass("users")) {
                        	e.preventDefault();
                            $(".sw-dropdown",this).slideUp(300, "swing").attr('aria-hidden', 'true');
                        	$("ul.sw-channel-list li.sw-channel-item:eq(0) > a").focus();
                        }
                    break;
                }
            });

            $(document).off("keydown", ".sw-mystart-dropdown ul a");
            $(document).on("keydown", ".sw-mystart-dropdown ul a", function(e) {
           		var lastIndex = $(this).parents(".sw-dropdown ul").children("li").length - 1;

                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case _this.KeyCodes.left:
                    case _this.KeyCodes.up:
                        e.preventDefault();
                        if($(this).parent().index() == 0) {
                        	$(this).parents(".sw-mystart-dropdown").focus();
						} else {
                        	$(this).parent().prev().find("a").focus();
                        }
                    break;             
                    // CONSUME RIGHT AND DOWN ARROWS
                    case _this.KeyCodes.right:
                    case _this.KeyCodes.down:
                        e.preventDefault();
                        if($(this).parent().index() == lastIndex) {
                        	$(this).parents(".sw-mystart-dropdown").focus();
						} else {
                        	$(this).parent().next().find("a").focus();
                        }
                    break;
                    case _this.KeyCodes.space:
                    case _this.KeyCodes.enter:
                        $(this)[0].click();
                    break;  
                    case _this.KeyCodes.tab:
                        if($(this).parent().index() == lastIndex && e.keyCode == _this.KeyCodes.tab) {
                            $(".sw-dropdown").slideUp(300, "swing").attr('aria-hidden', 'true');
                            $(".sw-dropdown").removeClass("open");
                        }
                    break;                
                }
            });
        },

        "Header": function() {
            // ADD LOGO
            var showLogo = true;
            var logoSrc = $.trim("/cms/lib/TX02216949/Centricity/Template/GlobalAssets/images///Logo/gb-logo.png");
            if(showLogo) {
                var srcSplit = logoSrc.split("/");
                var srcSplitLen = srcSplit.length;
                if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
                    $("#gb-logo").append('');
                } else {
                    $("#gb-logo").append('');
                }
            }
        },

