        "ModEvents": function() {
            // FOR SCOPE
            var _this = this;

            if (!$(".upcomingevents .joel-month").length) {
                $(".upcomingevents").modEvents({
                    columns     : "yes",
                    monthLong   : "no",
                    dayWeek     : "yes"
                });
                eventsByDay(".upcomingevents .ui-articles");
            }

            function eventsByDay(container) {
                var countLi = 0;
                $(".ui-article", container).each(function(){
                    if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
                        var moveArticle = $(this).html();
                        $(this).parent().prev().children().children().next().append(moveArticle);
                        $(this).parent().remove();
                    };
                });
            }

            $(".hp.row.one .upcoming-column.right .ui-article-description, .hp.row.two .upcoming-column.right .ui-article-description").each(function() {
            		if($(".sw-calendar-block-time", this).length === 0) {
                    	$(this).prepend('All Day');
                    }
            });

            $(".view-calendar-link span").text("VIEW FULL CALENDAR");

            $(".hp.row.one .upcomingevents, .hp.row.two .upcomingevents").each(function() {
            	var element = $(this);
            	$(".ui-widget-detail", this).prepend("
");
                $(".sw-calendar-block-date", this).each(function() {
                	$(".cs-events-container", element).append(''+$(this).html()+'');
                });
                $(".cs-events-btn", element).eq(0).addClass("active");
                $(".ui-articles li", element).eq(0).addClass("active");
                $(".ui-widget-detail", this).wrapInner('
');
                $(".view-calendar-link", this).clone().appendTo($(".upcoming-column-container", this));
            });

            $(document).on("click keydown", ".cs-events-btn", function(e) {
                if(_this.A11yClick(e)) {
                   	e.preventDefault();
                   	// REMOVE ACTIVE STATE
                    $(this).removeClass("active");
                    $(this).siblings().removeClass("active");
                    $(this).parent().siblings(".ui-articles").find("li").removeClass("active");
                    // ADD ACTIVE STATE
                    $(this).addClass("active");
                	$(this).parent().siblings(".ui-articles").find("li").eq($(this).index()).addClass("active");
                    $(this).parent().siblings(".ui-articles").find("li").eq($(this).index()).find("a").eq(0).focus();
                }
            });
        },

