$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=eU%2bQBHDeDJz50uV9AHFv%2fQ%3d%3d&SessionID=6fbe479a-d73e-4ce1-b35d-da8ff5ed779b&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f24%2f2020+11%3a12%3a47+AM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.20.6';
