        	jsMediaQueries(); 
            calendarListView();
            csVideoResize();
            checkSlideshow();
            var rule2 = sheet.add("ul.sw-channel-dropdown li.mega-columns");
            rule2 .update({"max-height": $(window).height() - 160+"px"});

            //DOC update dynamic stylesheet
            $("div.ui-widget.app div.ui-widget-header").each(function() {
            	if(!$("h1 span", this).length) {
            		$("h1", this).wrapInner("");
                }
                if($(this).parents(".sp.column.one").length) {
                    var rule = sheet.add(".sp.column.one div.ui-widget-header h1:before");
                    var newWidth = $(this).width()-$("h1 span", this).width();
                } else {
                    var rule = sheet.add("#"+$(this).parent().parent().parent().attr("id")+" #"+$(this).parent().parent().attr("id")+" div.ui-widget-header h1:before");
                    var newWidth = $(this).width()-$("h1 span", this).width();
                }
                if(newWidth-20 <= 0) {
                    rule.update({"width": newWidth+"px"});
                } else {
                    newWidth = newWidth-20;
                    rule.update({"width": newWidth+"px"});
                }
            });
        });

