    function stickyHeader() {
        if(!IsMyViewPage) {
 			if($(window).width() >= 1280) { 
            	if($(".hp").length) {
                	var channelBarOffSet = $("#hp-slideshow-outer").offset().top;
                } else {
                	var channelBarOffSet = 0;
                }
                if ($(window).scrollTop() <= channelBarOffSet) {
                    if(ChannelBarIsSticky) {
                        $("#gb-mystart-outer, #gb-channel-list-outer").removeClass("fixed-header");
                        $("body").css("padding", "0");

                        ChannelBarIsSticky = false;
                    }

                    if($(".sp").length && $(".ui-widget.app.calendar").length) {
                        $(".wcm-controls").css("margin-top", "0px");
                    }
                } else {
                    if(!ChannelBarIsSticky) {
                        $("#gb-mystart-outer, #gb-channel-list-outer").hide().addClass("fixed-header");
                        $("body").css("padding", $("#gb-channel-list").height()+$("#gb-mystart-outer").height()+"px 0 0 0");
                        $("#gb-mystart-outer, #gb-channel-list-outer").fadeIn(500);

                        ChannelBarIsSticky = true;
                    }

                    if($(".sp").length && $(".ui-widget.app.calendar").length) {
                        if($(".wcm-controls").hasClass("wcm-stuck")) {
                            $(".wcm-controls").css("margin-top", $("#gb-channel-list-outer").outerHeight(true));
                        }
                    }
                }

                $("#sw-maincontent").css({
                    "display": "block",
                    "position": "relative",
                    "top": "-" + $("#gb-channel-list-outer").outerHeight(true) + "px"
                });
           } else if($(window).width() <= 768) { 
                if(ChannelBarIsSticky) {
                    $("#gb-mystart-outer, #gb-channel-list-outer").removeClass("fixed-header");
                    $("body").css("padding", "0");

                    ChannelBarIsSticky = false;
                }

                if($(".sp").length && $(".ui-widget.app.calendar").length) {
                    $(".wcm-controls").css("margin-top", "0px");
                }
                $("#sw-maincontent").removeAttr("style");
            }
        }
    }

	function jsMediaQueries() {
		switch(csGetBreakPoint()) {
			case "desktop": 
				if($(".hp").length){
               		$("#hp-slideshow").height($(window).height() - $("#hp-slideshow-outer").position().top - $("#gb-fixed-mystart-outer").height() - 2);
					photoGallery();
                    $("#hp-slideshow .ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail").height($("#hp-slideshow").height());
				}
			break;
			case "768":
				if($(".hp").length){
					photoGallery();
				}
			break;
			case "640":
				if($(".hp").length){
					photoGallery();
				}
			break;
			case "480":
				if($(".hp").length){
					photoGallery();
				}
			break;
			case "320":
				if($(".hp").length){
					photoGallery();
				}
			break;
		}
	}	

	function photoGallery() {
		if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length){
		        // MULTIMEDIA ROTATOR
		        $("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
		                "imageWidth"              : 1500,
		                "imageHeight"             : 915,
		                "efficientLoad"           : "no",
		                "titles"                  : "yes",
		                "captions"                : "yes",
		                "linkedElement"           : ["image"], // ["image", "title", "overlay", "all"]
		                "mainControls"            : "yes",
		                "descMoreLink"            : "yes",
		                "descMoreLinkText"        : "Learn More",
		                "hoverPause"              : "yes",
		                "autoRotate"              : "yes",
		                "fullScreenRotator"       : "yes", // image dimensions work well at 1500x915
		                "fullScreenBreakpoints"   : ["960"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
		                "fullScreenVideo"         : "no",
		                "transitionType"          : "fade", // fade or slide
		                "transitionSpeed"         : 3,
		                "transitionDelay"         : 8,
		                "onImageLoad"             : function() { },
		                "allImagesLoaded"         : function() { },
		                "onThumbnailLoad"         : function() { },
		                "allThumbnailsLoaded"     : function() { },
		                "onTransitionStart"       : function() { },
		                "onTransitionEnd"         : function() { },
		                "watchVidClick"           : function() { },
		                "closeVidClick"           : function() { },
		                "allLoaded"               : function() { 
							$("#hp-slideshow .rs-gallery-desc").wrap("
");
                        }
		        });
		}
	}

