            $(document).on("keydown", ".mega-columns a", function(e) {
            	var megaColumn = $(this).parents(".mega-columns");
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case 37:
                    case 38:
                        e.preventDefault();
                        if($("a", megaColumn).index(this) == 0) {
                        	$(this).parents(".sw-channel-item").find("> a").focus();
                        } else {
                        	var index = $("a", megaColumn).index(this)-1;
                        	$("a", megaColumn).eq(index).focus();
                        }
                    break;             
                    // CONSUME RIGHT AND DOWN ARROWS
                    case 39:
                    case 40:
                        e.preventDefault();
                        if($("a", megaColumn).index(this) == $("a", megaColumn).length-1) {
                        	$(this).parents(".sw-channel-item").find("> a").focus();
                        } else {
                            var index = $("a", megaColumn).index(this)+1;
                            $("a", megaColumn).eq(index).focus();
                        }
                    break;
                    case 9:
                   		e.preventDefault();
                        var index = $("a", megaColumn).index(this)+1;
                        $("a", megaColumn).eq(index).focus();
                    break;
                  }
            });

        }
    }

	// GET BROWSER AND VERSION
	function getBrowser(){
		var N = navigator.appName, ua=navigator.userAgent, tem;
		var M = ua.match(/(opera|chrome|safari|firefox|msie)\/?\s*(\.?\d+(\.\d+)*)/i);
		if(M && (tem= ua.match(/version\/([\.\d]+)/i))!= null) M[2] = tem[1];
		M = M ? [M[1], M[2]] : [N, navigator.appVersion, '-?'];
		return [M[0], M[1]];
	}

	var browser = getBrowser();
	var browserName = browser[0].toLowerCase();
	var browserVersion = Math.floor(parseInt(browser[1]));

    function isIEorEDGE() {
      return navigator.appName == 'Microsoft Internet Explorer' || (navigator.appName == "Netscape" && navigator.appVersion.indexOf('Trident') > -1);
    }

    function hexToRgb(h){
        var r = parseInt((cutHex(h)).substring(0,2),16);
        var g = parseInt((cutHex(h)).substring(2,4),16);
        var b = parseInt((cutHex(h)).substring(4,6),16);
        return r+', '+g+', '+b;
    };

    function cutHex(h) {
    	return (h.charAt(0)=="#") ? h.substring(1,7):h;
    };

if(false) {
    //loads the IFrame Player API code asynchronously.
    var tag = document.createElement('script');
    tag.src = "//www.youtube.com/iframe_api";
    var firstScriptTag = document.getElementsByTagName('script')[0];
    firstScriptTag.parentNode.insertBefore(tag, firstScriptTag);

	//creates an iframe (and YouTube player) after the API code downloads.
	var player;
	var windowWidth;
	var windowHeight;
    function onYouTubeIframeAPIReady() {
        windowWidth = $(window).width();
        windowHeight = $(window).height();
        player = new YT.Player('hp-video-player', {
            height: windowHeight,
            width: windowWidth,
            videoId: 'jm5Bj70PQIk',
            playerVars: { 
                    'autoplay': 1,
                    'modestbranding': 1,
                    'controls': 0, 
                    'rel' : 0,
                    'loop': 1,
                    'showinfo': 0,
                    'wmode': 'opaque'
            },
            events: {
                'onReady': onPlayerReady,
                'onStateChange': onPlayerStateChange
            }
        });
    }

	//the API will call this function when the video player is ready.
	function onPlayerReady(event) {
    	player.mute();
        $('#hp-video-button').circleProgress({
            value: 0,
            size: 40,
            thickness: 2,
            startAngle: 0,
            fill: {
                color: ["#daaa27"]
            }
        });

		if(csGetBreakPoint() == "desktop") {
			event.target.playVideo();
		} else {
			$("#hp-video-button span").text("Play Video");
			$("#hp-video-button").attr("aria-label", "Play Video");
			$("#hp-video-button").removeClass("cs-icon-pause");
		}

		csVideoResize();

		$("#hp-video-button").click(function() {
			$(this).toggleClass("cs-icon-pause cs-icon-play");
			pausePlayVideo();
		});
	}

	//The API calls this function when the players state changes.
	//The function indicates that when playing a video (state=1),
	//the player should play for six seconds and then stop.
	var done = false;
	function onPlayerStateChange(event) {
    	var mytimer;
		if(csGetBreakPoint() == "desktop") {
			pausePlayVideo();
		}

        if (event.data == YT.PlayerState.PLAYING) {
          var playerTotalTime = player.getDuration();
          mytimer = setInterval(function() {
            var playerCurrentTime = player.getCurrentTime();
            var playerTimeDifference = (playerCurrentTime / playerTotalTime) * 100;
            if(playerTimeDifference != 100) {
            	var playerTimeDifference2 = playerTimeDifference;
                var playerTimeDifference3 = Math.floor(playerTimeDifference);
            	playerTimeDifference = 0+"."+Math.floor(playerTimeDifference);
                if(playerTimeDifference3 < 10) {
                	playerTimeDifference = 0+".0"+Math.floor(playerTimeDifference2);
                }
            }
           	$("#hp-video-button").circleProgress('value', playerTimeDifference);
          }, 1000);
        } else {
          clearTimeout(mytimer);
        }
	}

	function pausePlayVideo() {
		if($("#hp-video-button").hasClass("cs-icon-pause")) {
			$("#hp-video-button span").text("Pause Video");
			$("#hp-video-button").attr("aria-label","Pause Video");
			player.playVideo();
			player.mute();
		} else {
			$("#hp-video-button span").text("Play Video");
			$("#hp-video-button").attr("aria-label","Play Video");
			player.pauseVideo();
		}
	}

	function stopVideo() {
		player.pauseVideo();
	}

    function degreesToRadians(degrees) {
        return degrees * (Math.PI/180);     
    }
}

	function csVideoResize() {
		if($("#hp-video-player").length) {
			switch(csGetBreakPoint()) {
				case "desktop":
					var topOffSet = $("#hp-video-player").offset().top;
					var screenWidth = $(window).width();
					var screenHeight = $(window).height();
					var screenRatio = screenHeight / screenWidth;
                    var containerHeight = screenWidth * screenRatio;
					var vidWidth = screenWidth, vidHeight = 0;

					// GET IMAGE RATIO
					var initW = 853.5, initH = 480;
					var ratio = initH / initW;
					vidHeight = vidWidth * ratio;

					if(vidHeight < screenHeight){
						vidHeight = screenHeight;
						vidWidth = vidHeight / ratio;
					}

                    var centerWidth = (vidWidth - screenWidth) / 2;
                    var centerHeight = (vidHeight - screenHeight) / 2;

					// APPLY THE DIMENSIONS
					$("#hp-video").css({"height": containerHeight + "px"});
					$("#hp-video-player").css({"width": vidWidth + "px", "height": vidHeight + "px", "margin": "-" + centerHeight + "px 0px 0px -" + centerWidth + "px"});
				break;
				case "768": case "640": case "480": case "320":
					var screenWidth = $(window).width();
					var screenHeight = $(window).height();
					var vidWidth = screenWidth, vidHeight = 0;

					// GET IMAGE RATIO
					var initW = 853.5, initH = 480;
					var ratio = initH / initW;
					vidHeight = vidWidth * ratio;

					// APPLY THE DIMENSIONS
					$("#hp-video").css({"height": "auto"});
					$("#hp-video-player").css({"width": "100%", "height": vidHeight + "px", "margin": "0px"});
				break;
			}
		}
	}

