    function LoadRating8581635() {
    var ModuleInstanceID = 85;
    var FlexDataID = 81635;

        GetContent("https://www.yisd.net//site/UserControls/Rating/RatingListWrapper.aspx?ModuleInstanceID=" + ModuleInstanceID + "&FlexDataID=" + FlexDataID + "&IsDisabled=true", "swmoduleinstancerating-pnl-rating8581635", 2);
}

    function InsertRating8581635() {
    var ModuleInstanceID = 85;
    var FlexDataID = 81635;
    var Rating = $('#swmoduleinstancerating-hid-rating8581635').val();
    var data = "{ModuleInstanceID: " + ModuleInstanceID + ", " +
                "FlexDataID: " + FlexDataID + ", " +
                "Rating: " + Rating + "} ";  
    var success = 'LoadRating8581635();';
    var failure = "CallControllerFailure(result[0].errormessage);";

    if (Rating != '' && Rating !== undefined) {
        CallController("https://www.yisd.net//site/UserControls/Rating/RatingController.aspx/InsertRating", data, success, failure);
    }
}

    $(document).ready(function() {
        CheckScript('Rating');
        CheckScript('Metadata');

        // initialize rating script
        $("input[name='swmoduleinstanceratinghidrating8581635']").rating({
            callback: function() {
                Getswmoduleinstanceratinghidrating8581635Values();
                InsertRating8581635($('#swmoduleinstancerating-hid-rating8581635').val());
            }
        });
    });
    
    // fill hidden input with value clicked
    function Getswmoduleinstanceratinghidrating8581635Values() {
        var selItem;

        $("input[name='swmoduleinstanceratinghidrating8581635']").each(function(i) {
            if ($(this).is(':checked')) {
                $('#swmoduleinstancerating-hid-rating8581635').val((i + 1) / 2);
            }
        });
    }

