                InsertRating8581077($('#swmoduleinstancerating-hid-rating8581077').val());
            }
        });
    });
    
    // fill hidden input with value clicked
    function Getswmoduleinstanceratinghidrating8581077Values() {
        var selItem;

        $("input[name='swmoduleinstanceratinghidrating8581077']").each(function(i) {
            if ($(this).is(':checked')) {
                $('#swmoduleinstancerating-hid-rating8581077').val((i + 1) / 2);
            }
        });
    }

