jQuery(document).ready(function(){
    jQuery(window).on('bp:xs', function() {
        jQuery("#hero-photos-container").appendTo("#hero-photos-mobile");
    });
    jQuery(window).on('bp:sm bp:md bp:lg', function() {
        jQuery("#hero-photos-container").appendTo("#hero-desktop-photos");
    });
});

jQuery(document).ready(function() {

	jQuery(".digits__photo").addClass("hidden");
	jQuery(".digits__photo--1").removeClass("hidden");


		// Digit Box #0
		jQuery( '.digit__box--no0' ).on('click keypress', function(event) {
			$digitContent	= jQuery('.digit__content--no0'),
			$digitPhoto   = jQuery('.digits__photo--0');

			if ( a11yClick(event) === true ) {
				$digitContent.addClass('active');

				$digitContent.toggleClass( 'animated flipInX', function() {
					jQuery(this).removeClass( 'animated flipInX' );
				});

				if ( $digitContent.attr( 'aria-hidden' ) == 'true' ) {
					$digitContent.attr( 'aria-hidden', 'false' );
				}

				jQuery( '.digits__photo' ).addClass( 'hidden' );
				$digitPhoto.removeClass( 'hidden' );
			}
		});


		// Digit Box #1
