			}
		});


	jQuery(".digit__close").on('click keypress', function(event){
		if( a11yClick(event) === true) {
			var $parent = jQuery(this).parent();

			$parent.removeClass('active');

			if ( $parent.attr( "aria-hidden" ) == 'false' ) {
				$parent.attr( "aria-hidden", 'true' );
			}

			jQuery(".digits__photo").addClass("hidden");
			jQuery(".digits__photo--1").removeClass("hidden");
		}
	});
});

jQuery(document).ready(function(){

  jQuery(window).on('bp:xs', function() {
    jQuery('.life__photo').prependTo('#lifeat_slider');

    jQuery('#lifeat_slider').slick({
      dots: true,
      infinite: true,
      speed: 300,
      slidesToShow: 1,
      appendArrows: '.life__arrows',
      adaptiveHeight: true
    });
  });

  jQuery(".life__photo").on( "mouseenter focus mouseleave", function() {
    if (jQuery(this).hasClass('active')) {
      jQuery(this).removeClass('active');
    } else {
      jQuery('.life__photo').removeClass('active');
      jQuery(this).addClass('active');
    }

    jQuery(this).find(".life__content").toggleClass("animated fadeIn",function(){
      jQuery(this).find("life__content").removeClass('animated fadeIn');
    });
  });

  jQuery(window).on('bp:sm', function() {
    if (jQuery('#lifeat_slider').hasClass('slick-initialized')) {
      jQuery('#lifeat_slider').slick('unslick');
    }

    jQuery(".life__photo--photo1").appendTo(".life__photo__container--left");
    jQuery(".life__photo--photo3").appendTo(".life__photo__container--left");
    jQuery(".life__photo--photo4").appendTo(".life__photo__container--left");
    jQuery(".life__photo--photo2").appendTo(".life__photo__container--right");
    jQuery(".life__photo--photo5").appendTo(".life__photo__container--right");
  });

  jQuery(window).on('bp:md bp:lg', function() {
    if (jQuery('#lifeat_slider').hasClass('slick-initialized')) {
      jQuery('#lifeat_slider').slick('unslick');
    }

    jQuery(".life__photo--photo1").appendTo(".life__photo__container--left");
    jQuery(".life__photo--photo3").appendTo(".life__photo__container--left");
    jQuery(".life__photo--photo2").appendTo(".life__photo__container--middle");
    jQuery(".life__photo--photo4").appendTo(".life__photo__container--middle");
    jQuery(".life__photo--photo5").appendTo(".life__photo__container--right");
  });
});

