    function addBrandedTranslator() { //added by Lyndsay - switched from frameset
    	$("#gb-header-right-inner").creativeTranslate({
            "type": 2,
            "advancedOptions": {
                "addMethod": "append",
                "dropdownHandleText": "Translate this page...",
                "useTranslatedNames": true
            }
		});
    }
	function jsMediaQueries() {
		switch(csGetBreakPoint()) {
			case "desktop": 
				if(csMyStartItemsMoved) {
                	$("#gb-header").append($(".gb-header.right"));
                    csMyStartItemsMoved = false;
                }
			break;
			case "768":
			case "640":
			case "480":
			case "320":
				if(!csMyStartItemsMoved) {
                	$("#gb-channel-list").append($(".gb-header.right"));
                    csMyStartItemsMoved = true;
                }
			break;
		}
	}

    function hpAccentImages() {
    	if($(".hp").length) {
            var hpImages = [
                "/cms/lib/WA01001541/Centricity/Template/GlobalAssets/images///background images/bremerton.jpg",
                "/cms/lib/WA01001541/Centricity/Template/GlobalAssets/images///background images/bremerton.jpg",
                "/cms/lib/WA01001541/Centricity/Template/GlobalAssets/images///background images/bremerton.jpg"
            ];
            var imageStructure = "", imageNum = 0, pathArray = "";

            for(var i = hpImages.length - 1; i >= 0; i--) {
                var pathArray = hpImages[i].split("/");
                if((hpImages[i] != "") && (pathArray[pathArray.length - 1] != "default-man.jpg")) {
                    // Changed by Brenton Kelly - 11.4.2016 - Case: 00418946
                    //imageStructure += '
';
                    imageStructure += '
';

                	imageNum++;
                }
            }

            $("#hp-header-background-accent").html('' + imageStructure + '');

            if(imageNum > 1) {
            	hpAccentImageRotate();
            }
        }
    }

    function hpAccentImagesResize() {
    	/*
        Commented by Brenton Kelly - 11.4.2016 - Case: 00418946

        if($(".hp").length) {
            var initWidth = 1490;
            var initHeight = 630;
            var ratio = initHeight / initWidth;
            var newWidth = $(window).width();
            var newHeight = newWidth * ratio;
            if(newHeight < $("header").height()) {
                newHeight = $("header").height();
                newWidth = newHeight / ratio;
            }

            var centerWidth = (newWidth - $(window).width()) / 2;
            var centerHeight = (newHeight - $("header").height()) / 2;

            $("#hp-accent-images li img").css({"width": newWidth, "height": newHeight, "margin": "-" + centerHeight + "px 0px 0px -" + centerWidth + "px"});
        }
        */
    }

    function hpAccentImageRotate() {
    	setInterval(function(){
        	$("#hp-accent-images li:last-child").fadeOut((0 * 1000), function() {
            	$("#hp-accent-images").prepend($(this).css("display", ""));
            });
        }, (30 * 1000));
    }

    function photoGallery() {
    	if($("#sw-content-container13.ui-hp > div:first-child div.ui-widget.app.gallery.json").length) {
            var recordNum = 0;
            var thisAppInstance = this;
            var moduleID = "";
            var galleryApp = $("#sw-content-container13.ui-hp > div:first-child div.ui-widget.app.gallery.json");

            // GET THE PHOTO GALLERY DATA AND SET UP SOME USEFUL VARIABLES
            moduleID = galleryApp.parent().parent().attr("id").replace("pmi-", "");
            var gallery = eval("photoGallery" + moduleID);
            var galleryRecords = gallery.Album.Images;
            var records = "";
            recordNum = gallery.Album.Images.length;

            // GENERATE THE SLIDER RECORDS
            for(var i in galleryRecords) {
                records +=	'
';
                records +=		'
';
                records +=		'
';
                records +=			((galleryRecords[i].photoname.toLowerCase() != "hide") ? '
' + galleryRecords[i].photoname + '' : '');
                records +=			(($.trim(galleryRecords[i].caption) != "") ? '
' + galleryRecords[i].caption + '' : '');
                records +=			(($.trim(galleryRecords[i].link) != "") ? '
read more about this slide' : '');
                records +=		'';
                records +=	'';
            }

            // ADD EVERYTHING TO THE DOM
            galleryApp.prepend('' +
                '
' +
                    '<button class="hp-slider-control prev cs-html-btn-reset flipInY animated" data-direction="prev" aria-label="Navigate to previous slide" aria-hidden="' + ((recordNum > 3) ? "false" : "true") + '"' + ((recordNum <= 3) ? ' style="display: none;"' : '') + '>' +
                    '
' +
                        '
' + records + '' +
                    '' +
                    '<button class="hp-slider-control next cs-html-btn-reset flipInY animated" data-direction="next" aria-label="Navigate to next slide" aria-hidden="' + ((recordNum > 3) ? "false" : "true") + '"' + ((recordNum <= 3) ? ' style="display: none;"' : '') + '>' +
                '' +
            '');

            if(recordNum > 0) {
                // GALLERY ELEMENTS OBJECT
                var galleryElements = {
                    "galleryViewer": ".hp-slider-viewer",
                    "galleryPanelContainer": ".ui-articles",
                    "galleryPanel": ".ui-article-panel",
                    "galleryArticle": ".ui-article-container",
                    "galleryControl": ".hp-slider-control",
                    "galleryPrevControl": ".hp-slider-control.prev",
                    "galleryNextControl": ".hp-slider-control.next"
                }

                var galleryTimer = [];
                var currentView;
                // GALLERY PROPERTIES AND METHODS OBJECT
                var galleryProps = {
                    "animationSpeed": 2000,
                    "animationDelay": 6000,
                    "setAttributes": function() {
                        $(galleryElements.galleryPanel, galleryApp).each(function() {
                            // ACTIVE PANEL
                            if($(this).hasClass("active")) {
                                $(this).attr("aria-hidden", "false");
                                $("a", this).attr("tabindex", "0");
                            }

                            // HIDDEN PANEL
                            else {
                                $(this).attr("aria-hidden", "true");
                                $("a", this).attr("tabindex", "-1");
                            }
                        });
                    },
                    "triggerAction": function(direction) {
                        // FOR SCOPE
                        var $this = this;

                        // HANDY VARIABLES
                        var active = $(galleryElements.galleryPanel + ".active", galleryApp);
                        var nextActive;

                        // STOP CURRENT ANIMATION
                        $(galleryElements.galleryPanel, galleryApp).stop(true, true);

                        // IF TRYING TO NAVIGATE TO PREVIOUS PANEL
                        if(direction == "prev") {
                            // IF FIRST CHILD IS CURRENTLY ACTIVE
                            if($(galleryElements.galleryPanel + ".active", galleryApp).is(":first-child")) {
                                nextActive = $(galleryElements.galleryPanel + ":last-child", galleryApp);
                            }

                            // FIRST CHILD IS NOT CURRENTLY ACTIVE
                            else {
                                nextActive = $(galleryElements.galleryPanel + ".active", galleryApp).prev(galleryElements.galleryPanel);
                            }
                        }

                        // IF TRYING TO NAVIGATE TO NEXT SLIDE
                        else if(direction == "next") {
                            // IF LAST CHILD IS CURRENTLY ACTIVE
                            if($(galleryElements.galleryPanel + ".active", galleryApp).is(":last-child")) {
                                nextActive = $(galleryElements.galleryPanel + ":first-child", galleryApp);
                            }

                            // LAST CHILD IS NOT CURRENTLY ACTIVE
                            else {
                                nextActive = $(galleryElements.galleryPanel + ".active", galleryApp).next(galleryElements.galleryPanel);
                            }
                        }

                        // ANIMATE THE PANELS
                        // CREATE A PROMISE SO WE CAN RESET ATTRIBUTES ONLY AFTER EVERYTHING IS DONE ANIMATING
                        $.when(
                            active.fadeOut(this.animationSpeed, function() {
                                $(this).removeClass("active");
                            }),
                            nextActive.css({"display": "flex", "display": "-webkit-flex"}).hide().fadeIn(this.animationSpeed, function() {
                                $(this).addClass("active");
                            })
                        ).done(function() {
                            $this.setAttributes();
                        });
                    },
                    "setTimer": function() {
                        // FOR SCOPE
                        var $this = this;

                        galleryTimer[moduleID] = setInterval(function() {
                            $this.triggerAction("next");
                        }, this.animationDelay);
                    },
                    "clearTimer": function(moduleID) {
                        clearInterval(galleryTimer[moduleID]);
                    },
                    "bindEvents": function() {
                        // FOR SCOPE
                        var $this = this;

                        galleryApp.on("click keydown", galleryElements.galleryControl, function(e) {
                            if(e.type == "click" || e.type == "keydown" && e.keyCode == csKeyCodes.space || e.type == "keydown" && e.keyCode == csKeyCodes.enter) {
                                e.preventDefault();

                                $this.triggerAction($(this).attr("data-direction"));
                                $this.clearTimer(moduleID);
                                $this.setTimer();
                            }
                        });

                        // SLIDER HOVER
                        galleryApp.hover(function() {
                            $this.clearTimer(moduleID);
                        }, function() {
                            $this.clearTimer(moduleID);
                            $this.setTimer();
                        });
                    },
                    "wrapArticles": function() {
                        var wrapNum;
                        var breakPoint = csGetBreakPoint();

                        // ADJUST THE BREAKPOINT VALUE IF NEEDED
                        if(breakPoint == "640") {
                            breakPoint = "768";
                        }
                        if(breakPoint == "320") {
                            breakPoint = "480";
                        }

                        if(currentView != breakPoint) {
                            // STOP CURRENT ANIMATION AND CLEAR THE TIMER
                            $(galleryElements.galleryPanel, galleryApp).stop(true, true);
                            this.clearTimer(moduleID);

                            // UNWRAP PANELS THAT MAY ALREADY EXIST
                            $(galleryElements.galleryPanel, galleryApp).children().unwrap();

                            // SET THE WRAP NUMBER
                            switch(breakPoint) {
                                case "desktop": 
                                    if(currentView != "desktop") {
                                        wrapNum = 3;
                                        currentView = "desktop";
                                    }
                                break;
                                case "768":
                                    if(currentView != "768") {
                                        wrapNum = 2;
                                        currentView = "768";
                                    }
                                break;
                                case "480":
                                    if(currentView != "480") {
                                        wrapNum = 1;
                                        currentView = "480";
                                    }
                                break;
                            }

                            // WRAP THE ARTICLES IN PANELS WITH THREE ARTICLES IN EACH
                            for(var a = 0; a < recordNum; a += wrapNum) {
                                $(galleryElements.galleryArticle, galleryApp).slice(a, a + wrapNum).wrapAll('
');
                            }

                            // SET ATTRIBUTES
                            this.setAttributes();

                            // SET THE TIMER AND ROTATION
                            this.clearTimer(moduleID);
                            this.setTimer();
                        }
                    }
                }

                // WRAP THE ARTICLES IN PANELS
                galleryProps.wrapArticles();

                // BIND THE GALLERY EVENTS
                galleryProps.bindEvents();

                // ADJUST PANELS ON WINDOW RESIZE
                $(window).resize(function() {
                    galleryProps.wrapArticles();
                });
            }
        }
    }

	function hpSlider(app, element){
    	var element = "#sw-content-container1.ui-hp div.ui-widget.app.headlines";

        $(element).each(function() {
                var recordNum = 0;
                var thisAppInstance = this;
                var moduleID = "";

                // GET THE PHOTO GALLERY DATA AND SET UP SOME USEFUL VARIABLES
                moduleID = $(thisAppInstance).parent().parent().parent().attr("id").replace("pmi-", "")
                recordNum = $(".ui-articles > li", thisAppInstance).length;

                $(".ui-widget-detail .ui-articles", thisAppInstance).wrap('
');
                $(".ui-widget-detail", thisAppInstance).append('' +
                    '' +
                    '' +
                '');

                if(recordNum > 0) {
                    // SET CARD ATTRIBUTES
                    var recordWidth = $(".ui-articles > :first-child", thisAppInstance).outerWidth(true);
                    var recordViewNum = Math.round($(".hp-slider-viewer", thisAppInstance).width() / recordWidth);
                    var recordOverflow = recordNum - recordViewNum;
                    $(".ui-articles li:nth-child(-n+" + recordViewNum + ")", thisAppInstance).addClass("active").attr("aria-hidden", "false");
                    $(".ui-articles li:nth-child(n+" + (recordViewNum + 1) + ")", thisAppInstance).attr("aria-hidden", "true");
                    $(".ui-articles > li", thisAppInstance).each(function() {
                        if($(this).hasClass("active")) {
                            $("a", this).attr("tabindex", "0");
                        } else {
                            $("a", this).attr("tabindex", "-1");
                        }
                    });

                    var props = {
                    	"element": $("#pmi-" + moduleID),
                        "recordNum": recordNum,
                        "viewer": ".ui-articles",
                        "slideDirection": "",
                        "buttonControl": ".hp-slider-control",
                        "nextButtonControl": ".hp-slider-control.next",
                        "prevButtonControl": ".hp-slider-control.prev",
                        "moduleID": moduleID
                    }

                    // BIND THE SLIDER EVENTS
                    hpSliderEvents(props);

                    // ADJUST SLIDER ON WINDOW RESIZE
                    $(window).resize(function() {
                        hpSliderSetDimensions(props);

                        switch(csGetBreakPoint()) {
                            case "desktop":
                            case "768":
                            case "640":
                            	clearInterval(sliderTimer[moduleID]);
                        		hpSliderHandleRotate(props);
                            break;
                            case "480":
                            case "320":
                                clearInterval(sliderTimer[moduleID]);
                            break;
                        }
                    });

                    if(csGetBreakPoint() != "480" && csGetBreakPoint() != "320") {
                    	hpSliderHandleRotate(props);
                    }

                    $(props.element).on("click", ".ui-widget-header", function() {
                    	var $this = this;
                        setTimeout(function() {
                            if($($this).parent().hasClass("open")) {
                                hpSliderSetDimensions(props);
                                clearInterval(sliderTimer[moduleID]);
                                hpSliderHandleRotate(props);
                            } else {
                                clearInterval(sliderTimer[moduleID]);
                            }
                        }, 100);
                    });
                }
        });
    }

    function hpSliderEvents(props) {
    	// BUTTON CONTROL
        $(props.element).on("click keydown", props.buttonControl, function(e) {
        	if(e.type == "click" || e.type == "keydown" && e.keyCode == csKeyCodes.space || e.type == "keydown" && e.keyCode == csKeyCodes.enter) {
                e.preventDefault();

                hpSliderHandleEvent({
                    "element": props.element,
                    "recordNum": props.recordNum,
                    "viewer": props.viewer,
                    "slideDirection": $(this).attr("data-direction"),
                    "buttonControl": props.buttonControl,
                    "nextButtonControl": props.nextButtonControl,
                    "prevButtonControl": props.prevButtonControl,
                    "moduleID": props.moduleID
                });

                clearInterval(sliderTimer[props.moduleID]);
                hpSliderHandleRotate({
                    "element": props.element,
                    "recordNum": props.recordNum,
                    "viewer": props.viewer,
                    "slideDirection": props.slideDirection,
                    "buttonControl": props.buttonControl,
                    "nextButtonControl": props.nextButtonControl,
                    "prevButtonControl": props.prevButtonControl,
                    "moduleID": props.moduleID
                });
            }
        });

        // SLIDER HOVER
        $(props.element).hover(function() {
        	clearInterval(sliderTimer[props.moduleID]);
        }, function() {
        	clearInterval(sliderTimer[props.moduleID]);
            hpSliderHandleRotate(props);
        });
    }

    function hpSliderHandleEvent(props) {
    	// STOP CURRENT ANIMATION
        $(props.viewer, props.element).stop(true, true);

        // SET UP SOME USEFUL VARIABLES
    	var recordWidth = $(props.viewer + " > :first-child", props.element).outerWidth(true);
        var recordViewNum = Math.round($(".hp-slider-viewer", props.element).width() / recordWidth);
        var slideStart = 0, slideEnd = ((recordWidth * props.recordNum) - $(".hp-slider-viewer", props.element).width()) * -1;
        var slidePosition = parseInt($(props.viewer, props.element).css("left").replace("px", ""));
        var isRecordOverflow = (recordViewNum < props.recordNum) ? true : false;
        var slideSpeed = 750;

        // IF TRYING TO NAVIGATE TO PREVIOUS SLIDE
    	if(props.slideDirection == "prev") {
        	// MAKE SURE TO NOT OVERSLIDE
        	if(!$(props.viewer + " > li:first-child", props.element).hasClass("active")) {
            	// ANIMATE VIEWER THE AMOUNT OF ONE SLIDE
                $(props.viewer, props.element).animate({
                	left: slidePosition + recordWidth
                }, slideSpeed);

                // UPDATE ATTRIBUTES
                var firstActive = $(props.viewer + " > .active:first", props.element).prev().index();
                var rangeStart = firstActive + 1;
                var rangeEnd = firstActive + recordViewNum;

                $(props.viewer + " > .active", props.element).removeClass("active").attr("aria-hidden", "true")
                $(props.viewer + " > li:nth-child(n+" + rangeStart + "):nth-child(-n+" + rangeEnd + ")", props.element).addClass("active").attr("aria-hidden", "false");
                $(props.viewer + " > li", props.element).each(function() {
                    if($(this).hasClass("active")) {
                        $("a", this).attr("tabindex", "0");
                    } else {
                        $("a", this).attr("tabindex", "-1");
                    }
                });

                // FADE OUT PREVIOUS BUTTON CONTROL IF ANIMATING TO THE BEGINNING
                if($(props.viewer + " > li:first-child", props.element).hasClass("active") && isRecordOverflow) {
                	$(props.prevButtonControl, props.element).fadeOut(function() {
                    	$(this).attr("aria-hidden", "true");
                    });
                }

                // FADE IN THE NEXT BUTTON CONTROL IF ITS HIDDEN
                if(!$(props.viewer + " > li:last-child", props.element).hasClass("active") && isRecordOverflow) {
                    $(props.nextButtonControl, props.element).fadeIn(function() {
						$(this).attr("aria-hidden", "false");
                    });
                }
            }

            // MAKE SURE ATTEMPT IS KILLED
            else {
            	return false;
            }
        }

        // IF TRYING TO NAVIGATE TO NEXT SLIDE
        else if(props.slideDirection == "next") {
        	// MAKE SURE TO NOT OVERSLIDE
        	if(!$(props.viewer + " > li:last-child", props.element).hasClass("active")) {
            	// ANIMATE VIEWER THE AMOUNT OF ONE SLIDE
                $(props.viewer, props.element).animate({
                	left: slidePosition - recordWidth
                }, slideSpeed);

                // UPDATE ATTRIBUTES
                var firstActive = $(props.viewer + " > .active:first", props.element).next().index();
                var rangeStart = firstActive + 1;
                var rangeEnd = firstActive + recordViewNum;

                $(props.viewer + " > .active", props.element).removeClass("active").attr("aria-hidden", "true")
                $(props.viewer + " > li:nth-child(n+" + rangeStart + "):nth-child(-n+" + rangeEnd + ")", props.element).addClass("active").attr("aria-hidden", "false");
                $(props.viewer + " > li", props.element).each(function() {
                    if($(this).hasClass("active")) {
                        $("a", this).attr("tabindex", "0");
                    } else {
                        $("a", this).attr("tabindex", "-1");
                    }
                });

                // FADE OUT NEXT BUTTON CONTROL IF ANIMATING TO THE END
                if($(props.viewer + " > li:last-child", props.element).hasClass("active") && isRecordOverflow) {
                	$(props.nextButtonControl, props.element).fadeOut(function() {
                    	$(this).attr("aria-hidden", "true");
                    });
                }

                // FADE IN THE BACK BUTTON CONTROL IF ITS HIDDEN
                if(!$(props.viewer + " > li:first-child", props.element).hasClass("active") && isRecordOverflow) {
                    $(props.prevButtonControl, props.element).fadeIn(function() {
						$(this).attr("aria-hidden", "false");
                    });
                }
            }

            // MAKE SURE ATTEMPT IS KILLED
            else {
            	return false;
            }
        }
    }

    function hpSliderSetDimensions(props) {
    	// STOP CURRENT ANIMATION AND RESET THE VIEWER POSITION
        $(props.viewer, props.element).stop(true, true).css("left", "0px");

        // SET UP SOME USEFUL VARIABLES
    	var recordWidth = $(props.viewer + " > :first-child", props.element).outerWidth(true);
        var firstActive = $(props.viewer + " > .active:first", props.element).index();

        // ADJUST THE SLIDE ATTRIBUTES
        var recordViewNum = Math.round($(props.viewer, props.element).width() / recordWidth);
        var recordOverflow = props.recordNum - recordViewNum;
        var isRecordOverflow = (recordViewNum < props.recordNum) ? true : false;

        $(props.viewer + " > .active", props.element).removeClass("active").attr("aria-hidden", "true")
        $(".ui-articles li:nth-child(-n+" + recordViewNum + ")", props.element).addClass("active").attr("aria-hidden", "false");
        $(".ui-articles li:nth-child(n+" + (recordViewNum + 1) + ")", props.element).attr("aria-hidden", "true");
        $(".ui-articles > li", props.element).each(function() {
            if($(this).hasClass("active")) {
                $("a", this).attr("tabindex", "0");
            } else {
                $("a", this).attr("tabindex", "-1");
            }
        });

        // ADJUST BACK BUTTON CONTROL
        $(props.prevButtonControl, props.element).hide().attr("aria-hidden", "true");

        // ADJUST NEXT BUTTON CONTROL
        if(isRecordOverflow) {
        	$(props.nextButtonControl, props.element).show().attr("aria-hidden", "false");
        } else {
        	$(props.nextButtonControl, props.element).hide().attr("aria-hidden", "true");
        }
    }

    function hpSliderHandleRotate(props) {
    	var recordWidth = $(props.viewer + " > :first-child", props.element).outerWidth(true);
        var recordViewNum = Math.round($(props.viewer, props.element).width() / recordWidth);
        var isRecordOverflow = (recordViewNum < props.recordNum) ? true : false;
        //var firstActive = $(viewer + " > .active:first", element).prev().index();
        //var rangeEnd = firstActive + recordViewNum;

		if(props.recordNum > recordViewNum) {
            sliderTimer[props.moduleID] = setInterval(function(){
            	if($(props.viewer + " > li:last-child", props.element).hasClass("active")) {
                	$(props.viewer, props.element).animate({
                        left: "0px"
                    }, 1000);

                    $(props.viewer + " > .active", props.element).removeClass("active").attr("aria-hidden", "true")
                    $(props.viewer + " > li:nth-child(n+1):nth-child(-n+" + recordViewNum + ")", props.element).addClass("active").attr("aria-hidden", "false");
                    $(props.viewer + " > li", props.element).each(function() {
                        if($(this).hasClass("active")) {
                            $("a", this).attr("tabindex", "0");
                        } else {
                            $("a", this).attr("tabindex", "-1");
                        }
                    });

                    // FADE OUT PREVIOUS BUTTON CONTROL IF ANIMATING TO THE BEGINNING
                    if($(props.viewer + " > li:first-child", props.element).hasClass("active") && isRecordOverflow) {
                        $(props.prevButtonControl, props.element).fadeOut(function() {
                            $(this).attr("aria-hidden", "true");
                        });
                    }

                    // FADE IN THE NEXT BUTTON CONTROL IF ITS HIDDEN
                    if(!$(props.viewer + " > li:last-child", props.element).hasClass("active") && isRecordOverflow) {
                        $(props.nextButtonControl, props.element).fadeIn(function() {
                            $(this).attr("aria-hidden", "false");
                        });
                    }
                } else {
                	hpSliderHandleEvent({
                        "element": props.element,
                        "recordNum": props.recordNum,
                        "viewer": props.viewer,
                        "slideDirection": "next",
                        "buttonControl": props.buttonControl,
                        "nextButtonControl": props.nextButtonControl,
                        "prevButtonControl": props.prevButtonControl,
                        "moduleID": props.moduleID
                    });
                }
            }, 5000);
        }
    }

    function modMystart() {
    	$(".sw-mystart-dropdown.schoollist .selector").text("Our Schools");
        $(".sw-mystart-dropdown.schoollist .sw-dropdown-list").prepend('
District Home');
    	$("#gb-mystart-inner").prepend($(".sw-mystart-dropdown.schoollist"));


            $(".sw-mystart-button.signin a span").text("Log In");
			$("#gb-mystart-inner").append($(".sw-mystart-button.signin"));
            if($(".sw-mystart-button.register").length){
				$("#gb-mystart-inner").append($(".sw-mystart-button.register"));
			}


        $("#gb-mystart-inner").prepend($(".sw-mystart-button.home"));

        popularLinks();

        $(".sw-mystart-dropdown").click(function() {
        	$(this).addClass("open");
            $("#gb-mystart-inner").addClass("dropdown-active");
        });
        $(".sw-mystart-dropdown").focus(function() {
        	$(this).addClass("open");
            $("#gb-mystart-inner").addClass("dropdown-active");
        });
        $(".sw-mystart-dropdown").mouseleave(function() {
