        });
    }

	function dynamicContent() {
		$("#skipLink").attr("tabindex", "0");
    	$("#sw-maincontent").removeAttr("tabindex");

        // ********** MYSTART ********** //
        // MYSTART DROPDOWNS
        $(document).off("click", ".sw-mystart-dropdown");
		$(document).on("click", ".sw-mystart-dropdown", function(e) {
			$(".sw-dropdown", this).slideDown(300, "swing");
			$(this).addClass("open");
            $("#gb-mystart-inner").addClass("dropdown-active");
		});
		$(".sw-mystart-dropdown").hover(function(e) {
			e.preventDefault();
		}, function() {
			$(".sw-dropdown",this).slideUp(300, "swing").attr('aria-hidden', 'true');
            $("#gb-mystart-inner").removeClass("dropdown-active");
			$(this).removeClass("open");
		});
        $(document).off("focus", ".sw-mystart-dropdown");
		$(document).on("focus", ".sw-mystart-dropdown", function(e) {
			$(".sw-dropdown", this).slideDown(300, "swing").attr('aria-hidden', 'false');
            $("#gb-mystart-inner").addClass("dropdown-active");
			$(this).addClass("open");
		});
        $(".sw-mystart-dropdown").focusout(function() {
        	$("#gb-mystart-inner").removeClass("dropdown-active");
			$(this).removeClass("open");
		});
                /* Added by Lyndsay - Case 00327378 */
       if (($(".sp.column.one div.ui-widget.app.navigation div.ui-widget-header h1").height()) > 30) {	
        	$('.sp.column.one div.ui-widget.app.navigation div.ui-widget-header h1').css('text-align', 'center');
        } else { 
        	$('.sp.column.one div.ui-widget.app.navigation div.ui-widget-header h1').css('text-align', 'left');
		}
        /*End of Case 00327378 */

        // ADJUST PASSKEY OVERLAY
        $(document).ajaxComplete(function() {
            if($("#ui-mypasskey-overlay").length && !$("#sw-mystart-mypasskey #ui-mypasskey-overlay").length) {
                $("#sw-mystart-mypasskey").append($("#ui-mypasskey-overlay"));
            }
        });

		// ********** HEADER ********** //
		// ADD LOGO
		var logoCheck = "true";
		var logoSrc = jQuery.trim("/cms/lib/WA01001541/Centricity/Template/GlobalAssets/images///Logos/BSD Short Logo Gold Color.png");
		if(logoCheck == "true") {
			var srcSplit = logoSrc.split("/");
			var srcSplitLen = srcSplit.length;
			if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
				$("#gb-logo").append('').removeClass("hidden");
			} else {
				$("#gb-logo").append('').removeClass("hidden");
			}
		}

		// ADD SITENAME
		var siteNameOne = jQuery.trim('WE ARE BREMERTON!');
		var siteNameTwo = jQuery.trim('');
		if((siteNameOne == "") && (siteNameTwo == "")) {
			var splitLen = 2;
			var siteName = "Bremerton School District";
			siteName = siteName.split(" ");
			var siteNameLength = siteName.length;
			if(siteNameLength > 2){
				siteNameEnd = jQuery.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
			} else {
				siteNameEnd = jQuery.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
			}
			siteNameBegin = jQuery.trim(siteName.toString().replace(/,/g, " "));
			$("#gb-sitename").prepend("
" + siteNameBegin + "
" + siteNameEnd + "");
		} else if((siteNameOne != "") && (siteNameTwo == "")) {
			$("#gb-sitename").prepend("
" + siteNameOne + "");
		} else if((siteNameOne == "") && (siteNameTwo != "")) {
			$("#gb-sitename").prepend("
" + siteNameTwo + "");
		} else if((siteNameOne != "") && (siteNameTwo != "")) {
			$("#gb-sitename").prepend("
" + siteNameOne + "
" + siteNameTwo + "");
		}

		// ********** CHANNEL BAR ********** //
        //DOC set icon for home channel
		var firstChannelText = $("li.sw-channel-item:first-child > a span").text().toLowerCase();
		if(firstChannelText == "home") $("li.sw-channel-item").first().addClass("home");
		//DOC set icon for calendar channel
		var lastChannelText = $("li.sw-channel-item:last > a span").text().toLowerCase();
		if(lastChannelText == "calendar") $("li.sw-channel-item").last().addClass("calendar").insertAfter($("li.sw-channel-item").first());

		$(".sw-channel-item").unbind('hover');
		$(".sw-channel-item").hover(function(){
			$(".sw-channel-item ul").stop(true, true);
			var subList = $(this).children('ul');
			if ($.trim(subList.html()) !== "") {
				subList.slideDown(300, "swing");
			}
			$(this).addClass("hover");
		}, function(){
			$(".sw-channel-dropdown").slideUp(300, "swing");
			$(this).removeClass("hover");
		});

		// ********** BODY ********** //
		// AUTO FOCUS SIGN IN FIELD
		$("#swsignin-txt-username").focus();

		// CENTER SIGNED OUT MESSAGE AND SIGNIN BUTTON
        if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
            $("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
