    function modEvents() {
    	head.ready("modEvents", function() {
            $(".upcomingevents").modEvents({
                columns:"yes",
                monthLong:"no",
                dayWeek: "yes"
            });

            $(".upcomingevents .ui-article").each(function(){
                if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
                    var moveArticle = $(this).html();
                    $(this).parent().prev().children().children().next().append(moveArticle);
                    $(this).parent().remove();
                }

                if(!$(".sw-calendar-block-time", this).length) {
                	$(".ui-article-description", this).prepend('all day');
                }

                // MOVE DAY TO RIGHT COLUMN
				$(".upcoming-column.right", this).prepend($(".joel-day", this));
            });
        });
	}

    function scrollBtn() {
        if ($(window).scrollTop() < $("main").offset().top) {
            if(scrollBtnIsVisible) {
                $("#gb-scroll-to-top").fadeOut();

                scrollBtnIsVisible = false;
            }
        } else {
            if(!scrollBtnIsVisible) {
                $("#gb-scroll-to-top").fadeIn();

                scrollBtnIsVisible = true;
            }
        }
    }

