    function stickyChannelBar() {
		if(!isMyViewPage) {
			switch(csGetBreakPoint()) {
				case "desktop":
					var gbNavOffSet = $("nav").offset().top;
					if ($(window).scrollTop() <= gbNavOffSet) {
						if(csNavIsSticky) {
							$("#gb-channel-list-outer").removeClass("sticky");
                            $("nav, .gb-header.right.sticky").removeAttr("style");

							csNavIsSticky = false;
						}
					} else {
						$("nav").css("height", $("#gb-channel-list-outer").outerHeight(true));

						if(!csNavIsSticky) {
							$("#gb-channel-list-outer").addClass("sticky");
                            $(".gb-header.right.sticky").css("top", $("#gb-channel-list-outer").outerHeight(true));

							csNavIsSticky = true;
						}
					}
				break;
				case "768": case "640": case "480": case "320":
					if(csNavIsSticky) {
						$("#gb-channel-list-outer").removeClass("sticky");
						$("nav, .gb-header.right.sticky").removeAttr("style");

						csNavIsSticky = false;
					}
				break;
			}
		}
	}

    function stickyHeader() {
		switch(csGetBreakPoint()) {
            case "desktop":
                if ($(window).scrollTop() < 1) {
                    if(csHeaderIsSticky) {
                        $(".gb-header.right").removeClass("sticky");
                        $("#gb-header").removeAttr("style");

                        csHeaderIsSticky = false;
                    }
                } else {
                    if(!csHeaderIsSticky) {
                        $(".gb-header.right").addClass("sticky");

                        csHeaderIsSticky = true;
                    }

                    $("#gb-header").css("min-height", $(".gb-header.right").outerHeight(true));
                }
            break;
            case "768": case "640": case "480": case "320":
                if(csHeaderIsSticky) {
                    $(".gb-header.right").removeClass("sticky");
                    $("#gb-header").removeAttr("style");

                    csHeaderIsSticky = false;
                }
            break;
        }
	}

