        "ModEvents": function() {
            $(".upcomingevents").modEvents({
                columns     : "yes",
                monthLong   : "no",
                dayWeek     : "no"
            });

            $(".upcomingevents .ui-articles .ui-article").each(function(){
                if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
                    var moveArticle = $(this).html();
                    $(this).parent().prev().children().children().next().append(moveArticle);
                    $(this).parent().remove();
                } else {
                    var eventMonth = $(".joel-month", this).text();
                    $(".joel-month", this).remove();

                    var eventDate = $.trim($(".ui-article-title.sw-calendar-block-date", this).text());
                    if(eventDate.length == 1) {
                        eventDate = "0" + eventDate;
                    }
                    $(".ui-article-title.sw-calendar-block-date", this).html('' + eventMonth + '' + eventDate + '');

                	$(".upcoming-column.left", this).prepend('');
                }
            });

            $(".view-calendar-link").each(function() {
            	$("span", this).html('|view all events');
            	$(this).closest(".ui-widget.app.upcomingevents").find(".ui-widget-header").append($(this));
            });
        },

