        "MovePassKeys": function() {
        	if(this.GetBreakPoint() == "desktop") {
            	if(this.PasskeysAreMoved) {
                	$("#sw-mystart-mypasskey").insertAfter($(".cs-mystart-dropdown.translate"));

                	this.PasskeysAreMoved = false;
                }
            } else {
            	if(!this.PasskeysAreMoved) {
                	$("#gb-header").append($("#sw-mystart-mypasskey"));

                	this.PasskeysAreMoved = true;
                }
            }
        },

        "AppAccordion": function() {
            $(".hp-content.top, .hp.column.one[data-show-header='false'], .hp.column.two, .hp-content-outer.bottom, .sp.column.one").csAppAccordion({
                "accordionBreakpoints" : [480, 320]
            });
        },

        "FindItFastAccordion": function() {
        	var template = this;

            if(true) {
                if(this.GetBreakPoint() == "480" || this.GetBreakPoint() == "320") {
                    if(!this.FindItFastAccordionIsActive) {
                        // SET ROLES AND ATTRIBUTES
                        $(".hp.column.one .hp-column-header").attr("role", "button").attr("tabindex", "0");
                        $("#sw-content-container3.ui-hp").hide().attr("aria-hidden", "true");

                        // ACCORDION HEADER CLICK
                        $(document).on("click keydown", ".hp.column.one .hp-column-header", function(e) {
                            if(template.AllyClick(e)) {
                                $(this).toggleClass("open");
                                $("#sw-content-container3.ui-hp").slideToggle("300", "swing").attr("aria-hidden", ($("#sw-content-container3.ui-hp").attr("aria-hidden") == "true") ? "false" : "true");
                            }
                        });

                        this.FindItFastAccordionIsActive = true;
                    }
                } else {
                    if(this.FindItFastAccordionIsActive) {
                        // REMOVE ROLES AND ATTRIBUTES
                        $(".hp.column.one .hp-column-header").removeAttr("role").removeAttr("tabindex");
                        $("#sw-content-container3.ui-hp").removeAttr("style").removeAttr("aria-hidden");

                        // UNBIND EVENTS
                         $(document).off("click keydown", ".hp.column.one .hp-column-header");

                        this.FindItFastAccordionIsActive = false;
                    }
                }
            }
        },

        "Search": function() {
        	// FOR SCOPE
            var template = this;

            //SEARCH TEXT
            $("#gb-search-input").focus(function() {
                if($(this).val() == "Search") {
                    $(this).val("");
                }
            }).blur(function() {
                if($(this).val() == "") {
                    $(this).val("Search");
                }
            });

            // SEARCH OPEN
            $(document).on("click keydown", "#gb-search-toggle", function(e) {
            	if(template.AllyClick(e)) {
                	e.preventDefault();

                	if($("#gb-search").hasClass("open")) {
                    	$("#gb-search").removeClass("open");
                        $(this).attr("aria-label", "Expand Search Dialog");
                        $("#gb-search-form").attr("aria-hidden", "true");
                    } else {
                    	$("#gb-search").addClass("open");
                    	$(this).attr("aria-label", "Collapse Search Dialog");
                        $("#gb-search-form").attr("aria-hidden", "false");

                        setTimeout(function() {
                        	$("#gb-search-input").focus();
                        }, 500);
                    }
                }
            });

            // SEARCH SUBMIT
            $("#gb-search-form").submit(function(e){
                e.preventDefault();

                window.location.href = "/site/Default.aspx?PageType=6&SiteID=1&SearchString=" + $("#gb-search-input").val();
            });

            // SEARCH FOCUS OUT
            $("#gb-search").focusout(function() {
            	setTimeout(function () {
                	if(!$("#gb-search").find(":focus").length) {
                        $("#gb-search").removeClass("open");
                        $("#gb-search-toggle").attr("aria-label", "Expand Search Dialog");
                        $("#gb-search-form").attr("aria-hidden", "true");
                    }
                }, 500);
            });
        },

        "CalendarListView": function() {
            if($(".ui-widget.app.calendar").length) {
                // AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
                if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
                    $("a.ui-btn-toggle.list").click();
                    $("a.ui-btn-toggle.month").click();
                    $(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
                }

                switch(this.GetBreakPoint()) {
                    case "desktop": case "768": case "640":
                        $(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
                        $(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
                    break;
                    case "480": case "320":
                        $(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
                        $(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
                        $(".ui-btn-toggle.month").removeClass("active");
                        $(".ui-btn-toggle.list").addClass("active");
                    break;
                }
            }
        },

        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        }
    };

