	function switchDip_398210(num,pause){
		clearTimeout(slideshowTimer_398210);
		var timer;
		var nums = num;
		var slideshowMotion = slideshowMotion_398210();
		var speed = slideshowMotionSpeed_398210();
		var currentNum = document.getElementById('currentNum_398210').value;
		var totalDips = document.getElementById('totalDips_398210').value;
		var video = document.getElementById('video_398210').value;
		if(speed == '' || speed == null){
			speed = 800;
		}
		
		var direction = directions_398210(slideshowMotion);
		var directionOn = direction.directionOn;
		var directionOff = direction.directionOff;
		
		if(totalDips > 1 && video == 0){
		
			if(nums < 1){nums = 1;}
			if(nums > totalDips){nums = totalDips;}
			
			if($('#nav_398210' + nums).length){
				$('#nav_398210' + nums).removeClass('on');
			}
			
			if($('#nav_li_398210' + nums).length){
				$('#nav_li_398210' + nums).removeClass('on');
			}
			
			if(slideshowMotion != 'f'){
				if($('#videoShell_398210' + nums).length){
					$('#videoShell_398210' + nums).hide();
				}
				$('#dipImage_398210' + nums).hide("slide", { direction: directionOff }, speed);
			}else{
				$('#dipImage_398210' + nums).fadeOut(speed);
			}
			
			if($('#videoShell_398210' + nums).length){
				$('#videoShell_398210' + nums).fadeOut(speed);
			}
			if($('#dipContent_398210' + nums).length){
				$('#dipContent_398210' + nums).fadeOut(speed);
			}
			
			if(nums >= totalDips){nums = 1;}else{nums = nums + 1;}
			
			if($('#nav_398210' + nums).length){
				$('#nav_398210' + nums).addClass('on');
			}
			
			if($('#nav_li_398210' + nums).length){
				$('#nav_li_398210' + nums).addClass('on');
			}
			
			if(slideshowMotion != 'f'){
				$('#dipImage_398210' + nums).show("slide", { direction: directionOn }, speed, function(){
				if($('#videoShell_398210' + nums).length){
					$('#videoShell_398210' + nums).fadeIn(speed);
					}	
				});
			}else{
				$('#dipImage_398210' + (nums)).fadeIn(speed);
			}
			
			if(slideshowMotion == 'f'){
				if($('#videoShell_398210' + nums).length){
					$('#videoShell_398210' + nums).fadeIn(speed);
				}
			}
			if($('#dipContent_398210' + nums).length){
				$('#dipContent_398210' + (nums)).fadeIn(speed);
			}
				
			if(num < totalDips){timer = num + 1;}else{timer = 1;}
			rotateDip_398210(timer);
			document.getElementById('currentNum_398210').value = nums;
		}
	}
	
	function slideshowMotion_398210(){
		if($('#slideshowMotion_398210').length){
			slideshowMotion = document.getElementById('slideshowMotion_398210').value;
		}else{
			slideshowMotion = 'f';
		}
		if(slideshowMotion == ''){slideshowMotion = 'f'}
		return slideshowMotion;
	}
	
	function slideshowMotionSpeed_398210(){
