			slideshowMotionSpeed = document.getElementById('slideshowMotionSpeed_398210').value;
		}else{
			slideshowMotionSpeed = '';
		}
		return slideshowMotionSpeed;
	}
	
	
	function slideshowQuickNavSpeed_398210(){
		if($('#slideshowQuickNavSpeed_398210').length){
			slideshowQuickNavSpeed = document.getElementById('slideshowQuickNavSpeed_398210').value;
		}else{
			slideshowQuickNavSpeed = 50;
		}
	
		return slideshowQuickNavSpeed;
	}
	
	//motion direction
	function directions_398210(slideshowMotion){
		switch(slideshowMotion){
			case 'sr':
			directionOn = 'left';
			directionOff = 'right';
			break;
			case 'su':
			directionOn = 'down';
			directionOff = 'up';
			break;
			case 'sd':
			directionOn = 'up';
			directionOff = 'down';
			break;
			default:
			directionOn = 'right';
			directionOff = 'left';
		}
		
		return {directionOn: directionOn, directionOff: directionOff};
	}

