    $(function() {
        CreativeTemplate.Init(); //tip of the iceberg
    });

    var csTemplate = {};

    var CreativeTemplate = {

        /*****
            PROPERTIES
        *****/

        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD
        "DefaultLogoSrc": "",
        "DynamicStyles": "", //This can be added to and if it's not empty it'll be added to the page in a stylesheet
        "MobileMenuBreakpoint": 768,



        /*****
            METHODS
        *****/

// BIG BANG METHOD
        "Init": function() {
            // FOR SCOPE
            window.csTemplate = this;

            this.SetTemplateProps();
            this.JsMediaQueries();
            this.SLM(); //will daisy chain the RsMenu function
            this.GlobalIcons();
            this.SocialIcons();
            this.MyStart();
            this.Search();
            this.Header();
            this.ChannelBar();
            this.Body();
            this.HomepageEvents();
            this.Slideshow(); //move this into JsMediaQueries if it isn't present across all of the Breakpoints
            this.SlideshowLinks();
            this.AppAccordion();
            this.Footer();
            this.BuildStylesheet();

            $(window).load(function(){ window.csTemplate.WindowLoad(); });
            $(window).resize(function(){ 
                 window.csTemplate.WindowResize(); 
                 window.csTemplate.CalendarListView();
            });
            $(window).scroll(function() { window.csTemplate.WindowScroll(); });
        },


// TOP LEVEL (Window/Document) EVENT HANDLERS
        "WindowLoad": function() {
            if($(window).width() >= 320 && $(window).width() <= 639){
                this.CalendarListView();
            }    
            this.Search();

            $("div.ui-widget.app.navigation li div.bullet").click(function(){
                return true;
            });

            //UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
            $(".sw-channel-item").unbind('hover');
            $(".sw-channel-item").hover(function(){
                $(".sw-channel-item ul").stop(true, true);
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function(){
                $(".sw-channel-item ul").stop(true, true);
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });

              // MAKE NAVIGATION APPS ACCESSIBLE
            $(".ui-widget.app.navigation").csAccessibleNavigationApp();

            $(".hp.row.two .ui-widget.app.headlines .ui-articles .ui-article-info.has-thumb .ui-article-title > a").hover(function() {
                $(this).parent().parent().css("top","0px");
            }, function(){
            	$(this).parent().parent().css("top","100%");
            });

            $(".hp.row.two .ui-widget.app.headlines .ui-articles .ui-article-info.has-thumb .ui-article-title > a").focus(function() {
                $(this).parent().parent().css("top","0px");
            });

            $(".hp.row.two .ui-widget.app.headlines .ui-articles .ui-article-info.has-thumb .ui-article-title > a").focusout(function() {
                $(this).parent().parent().css("top","100%");
            });
        },
        "WindowResize": function() {
            this.JsMediaQueries();
            this.CalendarListView();
        },
        "WindowScroll": function() {
        },

//TEMPLATE EVENT HANDLERS
        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 

                break;
                case "768":

                break;
                case "640":

                break;
                case "480":

                break;
                case "320":

                break;
            }
        },

        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
        },
        "AllyClickForButtons": function(event) {
            if(event.type === 'click'){
                return true;
            }
            else if(event.type === 'keypress'){
                var code = event.charCode || event.keyCode;
                if(code === 13){
                    return true;
                }
            }
            else{
                return false;
            }
        },

// INFORMATION SETTERS
        "SetTemplateProps": function() {
            // MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },
        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        },

          // 
        "BuildStylesheet": function() {

            var primaryColor79 = "rgba(" + this.HexToRgb('#B8242E') + ", .79)";

            var dynStyleSheet = document.createElement('style');

            if(dynStyleSheet) {
                dynStyleSheet.setAttribute('type', 'text/css');
                var head = document.getElementsByTagName('head')[0];

                if(head) {
                    head.insertBefore(dynStyleSheet, head.childNodes[0]);
                }

                var dynStyles =	'#hp-row-one-outer {' +
                        '	background: ' + primaryColor79 + ';' +
                        '}';

                var rules = document.createTextNode(dynStyles);

                if(dynStyleSheet.styleSheet){ // IE
                    dynStyleSheet.styleSheet.cssText = dynStyles;
                } else {
                    dynStyleSheet.appendChild(rules);
                }
            }
        },
        "HexToRgb": function(hex){
            var r = parseInt((this.CutHex(hex)).substring(0,2),16);
            var g = parseInt((this.CutHex(hex)).substring(2,4),16);
            var b = parseInt((this.CutHex(hex)).substring(4,6),16);
            return r+', '+g+', '+b;
        },
        "CutHex": function(hex){
            return (hex.charAt(0)=="#") ? hex.substring(1,7):hex;
        },


//TEMPLATE METHODS
        "MyStart": function() {

        	var template = this;
            //TO FIX THE ISSUE OF SEARCH NOT ALLOWING SPACES
            $("#sw-mystart-search").unbind();

            //APPEND SEARCH TO GB-SEARCH-BOTTOM
            $("#sw-mystart-search").appendTo("#gb-search");

            //ADD ROLE AND ARIA LABEL TO SEARCH BUTTON
            $("#sw-search-button").attr("role","button").attr("aria-label","Submit Search").html("");

            //ANIMATE SEARCH CONTAINER
            $("#gb-overlay-btn").on("click keydown", function(e) {
            	searchHoverTimer = "";
                if(template.AllyClick(e)) {
                    e.preventDefault();
                    if($("#gb-overlay-outer").hasClass("open")){
                    $("#gb-page").removeClass("overlay");
                    $(this).attr("aria-expanded","false").focus();
                    clearTimeout(searchHoverTimer);
                        $("#gb-overlay-outer").removeClass("open").attr("aria-hidden","true");
                        //$("#gb-overlay-outer").fadeOut("slow").attr("aria-hidden","true");
                            $("#gb-overlay-outer #sw-search-input").attr("tabindex","-1");

                    } else {
                    	$(this).attr("aria-expanded","true");
                        $("#gb-page").addClass("overlay");
                        $("#gb-overlay-outer").addClass("open").attr("aria-hidden","false");
                        //$("#gb-overlay-outer").fadeIn("slow").attr("aria-hidden","false");
                        searchHoverTimer = setTimeout(function(){
                        	$("#gb-overlay-outer #sw-search-input").focus().attr("tabindex","0");

                        }, 1000);

                    }
                }
            });  
            //OVERLAY CLOSE BUTTON
            $("#gb-overlay-close").on("click keydown", function(e) {
                if(template.AllyClick(e)) {
                    e.preventDefault();
                    $("#gb-page").removeClass("overlay");
                    $("#gb-overlay-outer").removeClass("open").attr("aria-hidden","true");
                    $("#gb-overlay-outer").fadeOut("slow").attr("aria-hidden","true");
                    $("#gb-overlay-outer #sw-search-input").attr("tabindex","-1");
                    $("#gb-overlay-btn").attr("aria-expanded","false").focus();
                }
            }); 

            $("#gb-overlay-close").on("keydown", function(e) {
            	// CAPTURE KEY CODE

                if(e.shiftKey && e.keyCode == template.KeyCodes.tab) {
                    	e.preventDefault();
                        //FOCUS LAST GLOBAL ICON
                    	$("#gb-global-icons .cs-global-icons li:last-child > a").focus();
                }
                switch(e.keyCode) {
                	case template.KeyCodes.esc:
                        e.preventDefault();

                        // FOCUS DROPDOWN BUTTON
                        $("#gb-page").removeClass("overlay");
                        $("#gb-overlay-outer").removeClass("open").attr("aria-hidden","true");
                        $("#gb-overlay-outer #sw-search-input").attr("tabindex","-1");
                        $("#gb-overlay-btn").attr("aria-expanded","false").focus();
                }
            });

            $("#gb-overlay-outer #sw-search-input, #gb-overlay-outer #sw-search-button").on("keydown", function(e) {
            	// CAPTURE KEY CODE
                switch(e.keyCode) {
                	case template.KeyCodes.esc:
                        e.preventDefault();

                        // FOCUS DROPDOWN BUTTON
                        $("#gb-page").removeClass("overlay");
                        $("#gb-overlay-outer").removeClass("open").attr("aria-hidden","true");
                        $("#gb-overlay-outer #sw-search-input").attr("tabindex","-1");
                        $("#gb-overlay-btn").attr("aria-expanded","false").focus();
                }
            });

            $("#gb-global-icons .cs-global-icons li a").on("keydown", function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.left:
                    case template.KeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS DROPDOWN BUTTON
                            //$(this).closest("#gb-global-icons .cs-global-icons").find("li:last-child > a").focus();
                            $("#sw-search-button").focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).parent().prev("li").find("> a").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case template.KeyCodes.right:
                    case template.KeyCodes.down:
                        e.preventDefault();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            //$(this).closest("#gb-global-icons .cs-global-icons").find("li:first-child > a").focus();
                            $("#sw-search-input").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).parent().next("li").find("> a").focus();
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                    case template.KeyCodes.esc:
                        	e.preventDefault();

                        	// FOCUS DROPDOWN BUTTON
                            $("#gb-page").removeClass("overlay");
                            $("#gb-overlay-outer").removeClass("open").attr("aria-hidden","true");
                            $("#gb-overlay-outer #sw-search-input").attr("tabindex","-1");
                            $("#gb-overlay-btn").attr("aria-expanded","false").focus();

                    break;

                    // CONSUME HOME KEY
                    case template.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(this).closest("#gb-global-icons .cs-global-icons").find("li:first-child > a").focus();
                    break;

                    // CONSUME END KEY
                    case template.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).closest("#gb-global-icons .cs-global-icons").find("li:last-child > a").focus();
                    break;
                }
            });

            //ADD DISTRICT HOME
            $(".cs-mystart-button.home").append($(".sw-mystart-button.home a"));
            $(".cs-mystart-button.home #sw-home-icon").remove();
            $(".gb-mystart .cs-mystart-button a span").text("county office home");

            //REPLACE MYSTART DROPDOWN ARROWS
            $(".sw-myaccount-chevron").text("");
            $(".sw-dropdown-arrow").text("");

            //ADD TRANSITION CLASS TO ALL MYSTART DROPDOWNS
            $(".sw-mystart-dropdown").addClass("transition");

            //CHANGE THE SCHOOLLIST TEXT
            $(".sw-mystart-dropdown.schoollist .selector").text("Our Schools");

            //ADD TRANSITION CLASS TO ALL MYSTART BUTTONS
            $(".sw-mystart-button").addClass("transition");

            var userOptionsItems = "";



			// SIGNIN BUTTON
            if($(".sw-mystart-button.signin").length) {
				userOptionsItems += "
" + $(".sw-mystart-button.signin").html() + "";
			}

			// REGISTER BUTTON
            if($(".sw-mystart-button.register").length) {
                userOptionsItems += "
" + $(".sw-mystart-button.register").html() + "";
            }



			// ADD USER OPTIONS DROPDOWN TO THE DOM
            $(".cs-mystart-dropdown.user-options .cs-dropdown-list").html(userOptionsItems);

			// BIND DROPDOWN EVENTS
			this.DropdownActions({
            	"dropdownParent": ".cs-mystart-dropdown.user-options",
                "dropdownSelector": ".cs-selector",
                "dropdown": ".cs-dropdown",
                "dropdownList": ".cs-dropdown-list"
            });

            this.Translate();
        },
        "Header": function() {
            //ADD LOGO
            var logoCheck = "true";
            var logoSrc = jQuery.trim("/cms/lib/CA01001323/Centricity/Template/GlobalAssets/images///Logos/MCOE_district_logo.png");
            if(logoCheck == "true") {
                var srcSplit = logoSrc.split("/");
                var srcSplitLen = srcSplit.length;
                if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
                    $("#gb-logo").append('').removeClass("hidden");
                } else {
                    $("#gb-logo").append('').removeClass("hidden");
                }
            }

            //ADD SITENAME
