		"DropdownActions": function(params) {
        	// FOR SCOPE
            var template = this;

            var dropdownParent = params.dropdownParent;
            var dropdownSelector = params.dropdownSelector;
            var dropdown = params.dropdown;
            var dropdownList = params.dropdownList;

            $(dropdownParent + " " + dropdownList + " a").attr("tabindex", "-1");

            // MYSTART DROPDOWN SELECTOR CLICK EVENT
            $(dropdownParent).on("click", dropdownSelector, function(e) {
                e.preventDefault();

                if($(this).parent().hasClass("open")){
                	$("+ " + dropdownList + " a").attr("tabindex", "-1");
                    $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                } else {
                	$(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden","false").slideDown(300, "swing");
                }
        	});

            // MYSTART DROPDOWN SELECTOR KEYDOWN EVENTS
            $(dropdownParent).on("keydown", dropdownSelector, function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.enter:
                    case template.KeyCodes.space:
                        e.preventDefault();

                        // IF THE DROPDOWN IS OPEN, CLOSE IT
                        if($(dropdownParent).hasClass("open")){
                        	$("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                            $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                        } else {
                        	$(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden", "false").slideDown(300, "swing", function(){
                                $(dropdownList + " li:first-child a", this).attr("tabindex", "0").focus();
                            });
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                        if($("+ " + dropdown + " " + dropdownList + " a").length) {
                        	$("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        	$(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                        }

                    break;

                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.down:
                    case template.KeyCodes.right:
                        e.preventDefault();

                        $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        $("+ " + dropdown + " " + dropdownList + " li:first-child > a", this).attr("tabindex", "0").focus();
                    break;
                }
            });

            // MYSTART DROPDOWN LINK KEYDOWN EVENTS
            $(dropdownParent).on("keydown", dropdownList + " li a", function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.left:
                    case template.KeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().prev("li").find("> a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case template.KeyCodes.right:
                    case template.KeyCodes.down:
                        e.preventDefault();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().next("li").find("> a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                    	if(e.shiftKey) {
                        	e.preventDefault();

                        	// FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case template.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                    break;

                    // CONSUME END KEY
                    case template.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:last-child > a").attr("tabindex", "0").focus();
                    break;

                    // CONSUME ESC KEY
                    case template.KeyCodes.esc:
                        e.preventDefault();

                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownParent).removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                        $(this).closest(dropdownParent).find(dropdownSelector).attr("aria-expanded", "false").focus();

                    break;
                }
            });

            $(dropdownParent).mouseleave(function() {
               var thisDropdown = this;
               if($(thisDropdown).find("#google_translate_element").length){
                    if(!$(thisDropdown).find("#cs-branded-translate-label").is(":focus")){
                      $(dropdownList + " a", this).attr("tabindex", "-1");
                      $(dropdownSelector, this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                    } else {
                      	$("#cs-branded-translate-label").on("change", function() {
                       		$(".goog-logo-link").focus();
                        })
                    }
                } else {
                    $(dropdownList + " a", this).attr("tabindex", "-1");
                    $(dropdownSelector, this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                }

            }).focusout(function() {
                var thisDropdown = this;

                setTimeout(function () {
                    if($(thisDropdown).find("#google_translate_element").length){

                	} else {
                    	if(!$(thisDropdown).find(":focus").length) {
                       		$(dropdownSelector, thisDropdown).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                        }
                    }

                }, 500);
            });

        },
        "AppAccordion": function(){
            //DOC dont add app into accordion if it doesnt have a header
            $("#hp-content").csAppAccordion({
                "accordionBreakpoints" : [768, 640, 480, 320]
            });

            $(".sp.column.one").csAppAccordion({
                "accordionBreakpoints" : [768, 640, 480, 320]
            });
        },
        "CsOpenPageSubMenu": function(li) {
            if (li.prop('tagName').toLowerCase() == "li") {
                if (li.hasClass('expandable')) {  
                    li.removeClass('expandable').addClass('collapsible');
                }
                if (li.find('div:first').hasClass('expandable')) {
                    li.find('div:first').removeClass('expandable').addClass('collapsible');
                }
                li.find('ul:first').slideDown();

                PageNavigationStateCookie();
            }
        },

        "CsClosePageSubMenu": function(li) {
            if (li.prop('tagName').toLowerCase() == "li") {
                li.removeClass('collapsible').addClass('expandable');
                li.find('div:first').removeClass('collapsible').addClass('expandable');  
                li.find('ul:first').slideUp();

                PageNavigationStateCookie();
            }
        },


// APP METHODS
        "CalendarListView": function() {
            if($(".ui-widget.app.calendar").length) {
                // AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
                if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
                    $("a.ui-btn-toggle.list").click();
                    $("a.ui-btn-toggle.month").click();
                    $(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
                }

                switch(this.GetBreakPoint()) {
                    case "desktop": 
                    case "768": 
                    case "640":
                        $(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
                        $(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
                    break;
                    case "480": 
                    case "320":
                        $(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
                        $(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
                        $(".ui-btn-toggle.month").removeClass("active");
                        $(".ui-btn-toggle.list").addClass("active");
                    break;
                }
            }
        },
        "UpcomingEvents": function(){
            if (!$(".upcomingevents .joel-month").length) {
                $(".upcomingevents").modEvents({
                    columns     : "yes",
                    monthLong   : "yes",
                    dayWeek     : "yes"
                });
                var countLi = 0;
                $(".upcomingevents .ui-articles .ui-article").each(function(){
                    if (!$(".ui-article-title.sw-calendar-block-date", this).length && $.trim($(".ui-article-description", this).text()) != "There are no upcoming events to display.") {
                        var moveArticle = $(this).html();
                        $(this).parent().prev().children().children().next().append(moveArticle);
                        $(this).parent().remove();
                    };

                    if($('.upcoming-column.left h1', this).length){	

                        //add an ALL DAY label if no time was given
                        $('.upcoming-column.right .ui-article-description', this).each(function(){
                            if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
                                $(this).prepend('ALL DAY');
                            }
                        });

                    } 

                });



            }
        },
        "HomepageEvents": function(){

            // FOR SCOPE
            var template = this;

            var totalEventDays = $(".hp.row.one .upcomingevents li").length;
            var viewCalendarURL = $(".hp.row.one .upcomingevents .view-calendar-link").attr("href");

            if($(".hp.row.one .upcomingevents").length){
            	$(".hp.row.one").addClass("has-UE");
            }

            if(totalEventDays > 5 && $(".hp.row.one .upcomingevents").length){
                //ADD VIEW CALENDAR LINK AND EXPAND EVENTS BUTTON
                $(".hp.row.one").append("

Expand EventsView Calendar");

                eventA11y();

                $("#view-btn").on('click keypress', function(event){
                    if(template.AllyClickForButtons(event) === true){
                        //DONT LET THE PAGE JUMP ON KEYPRESS
                        event.preventDefault();

                        var events = $(".hp.row.one");
                        var clickedButton = $(".btn-text",this);
                        var clickedBtnArrow = $(".icon-font-arrow",this);
                        var initialHeight = 450;

                        if(events.hasClass("open")){
                        	clickedButton.removeClass("open").text("Expand Events");
                                clickedBtnArrow.removeClass("open");
                            events.animate({
                                height: initialHeight

                            }, 500, function() {
                                eventA11y();
                                events.removeClass("open");
                            });
                        } else {
                            var totalHeight = events.get(0).scrollHeight;
							clickedButton.addClass("open").text("Contract Events");
                            clickedBtnArrow.addClass("open");
                            events.animate({
                                height: totalHeight
                            }, 500, function() {
                                events.find("a").attr("tabindex","0");
                                events.addClass("open");
                                $(".hp.row.one .upcomingevents .ui-articles > li:first-child .ui-article-description:first-child .sw-calendar-block-title a").focus();
                            });
                        } 
                    }
                }) 

            } else {
                //ADD VIEW CALENDAR LINK
                if($(".hp.row.one .upcomingevents").length){
                	$(".hp.row.one").append("

View Calendar");
                } else {
                	$(".hp.row.one").addClass("no-events");
                }
            }

            function eventA11y(){
                $(".hp.row.one .upcomingevents li p").each(function(){
                    var elementPosition = $(this).position().top;
                    if(elementPosition > 400){

                        $(this).find("a").attr("tabindex","-1");
                    }

                });
            }
        }
    };
    jQuery.fn.animateAuto = function(prop, speed, callback){
        var elem, height, width;
        return this.each(function(i, el){
            el = jQuery(el), elem = el.clone().css({"height":"auto","width":"auto"}).appendTo(el.parent());
            height = parseInt(elem.css("height")),
            width = parseInt(elem.css("width"))+5,
            elem.remove();

            if(prop === "height")
                el.animate({"height":height}, speed, callback);
            else if(prop === "width")
                el.animate({"width":width}, speed, callback);  
            else if(prop === "both")
                el.animate({"width":width,"height":height}, speed, callback);
        });  
    }

