	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			photoGallery();
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			photoGallery();
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			photoGallery();
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			photoGallery();
			appView();
			$("#gb-mystart .gb-search").removeAttr("style").addClass("closed");
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			photoGallery();
			appView();
		}
	}

	function photoGallery(){
		head.ready("mediaRotator", function(){
			if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length){
				// MULTIMEDIA ROTATOR
				$("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
					"imageWidth"		: 1500,
				        "imageHeight"		: 915,
				        "appHeader"		: "no",
				        "titles"			: "yes",
				        "captions"		: "yes",
				        "playPause"		: "no",
				        "mainControls"		: "yes",
				        "bullets"		: "no",
				        "bulletControls"		: "no",
				        "hoverPause"		: "yes",
				        "fullScreenVideo"	: "no",
				        "transitionSpeed"	: 2,
				        "transitionDelay"	: 8,
				        "onImageLoad"		: function(){ },
				        "onTransitionStart"	: function(){
				        		$("#hp-slideshow-guide").addClass("bounce animated");

				        		var nextLinkCheck = $("#hp-slideshow .rs-gallery-images ul.rs-gallery-images-list li.next a").length;
						var nextLink = $("#hp-slideshow .rs-gallery-images ul.rs-gallery-images-list li.next a").attr("href");
						if(nextLinkCheck == 0) {
							$("#hp-slideshow .rs-gallery-read-more").fadeOut(1000);
						} else {
							$("#hp-slideshow .rs-gallery-read-more a").attr("href", nextLink);
							$("#hp-slideshow .rs-gallery-read-more").delay(1000).fadeIn(1000);
						}
				        },
				        "onTransitionEnd"	: function(){
				        		$("#hp-slideshow-guide").removeClass("bounce animated");
				        },
				        "watchVidClick"		: function(){ },
				        "closeVidClick"		: function(){ },
				        "allLoaded"		: function(){
				        		$("#hp-slideshow .rs-gallery-desc").attr("class", "rs-gallery-desc-outer");
						$("#hp-slideshow .rs-gallery-desc-outer").wrapInner("

");
				        		if(jQuery.trim($("#hp-slideshow .rs-gallery-images ul.rs-gallery-images-list li.active .media-link").text()) == "") {
							$("#hp-slideshow .rs-gallery-desc-inner").append('
LEARN MORE');
						} else {
							$("#hp-slideshow .rs-gallery-desc-inner").append('
LEARN MORE');
						}
						if($(".ie8").length) {
							$("#hp-slideshow .rs-gallery-caption").prepend("
");
						}
				        }
				});
			}
		});
	}

	function appView(){
		//DOC make homepage apps collapsible for small mobile devices
		if(!$("div.ui-widget.app.active").length){
			$("#hp-content div.ui-widget.app:first").addClass("active");
		}
		$("div.ui-hp div.ui-widget-header").click(function(){
			var clickedApp = $(this).parent().parent().attr("id");

			$("div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");

			$("html, body").animate({
				scrollTop: $("#" + clickedApp).offset().top
			}, 500, function(){
				$("html, body").stop(true, false);
			});
		});
	}	

