function initialize_LongerNavFix_2()
{
  var replace_Nav = new Array();

  replace_Nav[0] = ["Academic & Student","Academic & Student Affairs"];
  replace_Nav[1] = ["Extracurricular","Extracurricular Activities & Clubs"];

  //-----DO NOT EDIT BELOW THIS LINE --------//
  var navSpot = document.getElementById('Nav1').getElementsByTagName('li');
  for(var k = 0;k<replace_Nav.length;k++){
    for(var i = 0;i<navSpot.length;i++){

      var navA = navSpot[i].getElementsByTagName("A")[0];
      if(navA!=null){
        if (navA.innerHTML.toLowerCase() == replace_Nav[k][0].toLowerCase()){
          navA.innerHTML = replace_Nav[k][1];
        }
      }
    }
  }
}
var initialize_LongerNavFix_1 = function (){if (window.addEventListener){window.addEventListener("load", initialize_LongerNavFix_2, false)}else if (window.attachEvent){window.attachEvent("onload", initialize_LongerNavFix_2)}else if (document.getElementById){window.onload=initialize_LongerNavFix_2}};initialize_LongerNavFix_1();
