    	stickyCalendarFix();
    });

	$(function(){

    	csGlobalJs.OpenInNewWindowWarning();

		$(window).bind("resize", function(){ jsMediaQueries();stickyCalendarFix(); });

        var additionalHeaders = "", additionalLinks = "";

        // MENU GLOBAL ICONS
        var menuGlobalIcons = [
            ["Transportation", "https://www.csdecatur.net/Page/480"],
            ["Menu", "https://www.csdecatur.net/Page/1388"],
