                "menuButtonParent"      : "#gb-channel-list",
                "showMenuBtnText"       : "yes",
                "menuBtnText"           : "Menu",
                "showSchools"           : "yes",
                "showTranslate"         : "yes",
                "translateId"           : "none",
                "showAccount"           : "yes",
                "extraMenuOptions"      : additionalHeaders, // EXTRA MENU HEADING NAMES SEPARATED BY A ;
                "extraMenuItems"        : additionalLinks, // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
                "siteID"                : "4",
                "allLoaded"             : function(){ }
        });

        $("#gb-global-icons").creativeIcons({
                "iconNum"      : "6", // NUMBER OF ICONS
                "icons"         : [
                        ["Student Program/12.png", "Transportation", "https://www.csdecatur.net/Page/480", "_self"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
                        ["Student Program/4.png", "Menu", "https://www.csdecatur.net/Page/1388", "_self"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
                        ["Site Utility/1.png", "Calendar", "https://www.csdecatur.net/Page/2", "_self"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
