	function jsMediaQueries(){
		switch(csGetBreakPoint()) {
			case "desktop": 
				//LIMIT EVENTS ON HOMEPAGE
                $(".hp.column.one .upcomingevents .ui-articles.one li:gt(6)").css("margin-right","0px");
        		$(".hp.column.one .upcomingevents .ui-articles.one li:gt(7)").addClass("no-view");
                //$(".hp.column.one .upcomingevents .ui-articles.one li:lt(8)").addClass("view");
				$(".hp.column.one .upcomingevents .ui-articles.two li:gt(7)").hide();
			break;
			case "768":
				//LIMIT EVENTS ON HOMEPAGE
                $(".hp.column.one .upcomingevents .ui-articles.one li:gt(6)").css("margin-right","0px");
        		$(".hp.column.one .upcomingevents .ui-articles.one li:gt(7)").addClass("no-view");
                //$(".hp.column.one .upcomingevents .ui-articles.one li:lt(8)").addClass("view");
				$(".hp.column.one .upcomingevents .ui-articles.two li:gt(7)").hide();
			break;
			case "640":
				//LIMIT EVENTS ON HOMEPAGE
                $(".hp.column.one .upcomingevents .ui-articles.one li:gt(1)").css("margin-right",".68%");
                $(".hp.column.one .upcomingevents .ui-articles.one li:gt(4)").css("margin-right","0px");
                $(".hp.column.one .upcomingevents .ui-articles.one li:lt(6)").removeClass("no-view");
				$(".hp.column.one .upcomingevents .ui-articles.two li:lt(6)").show();
        		$(".hp.column.one .upcomingevents .ui-articles.one li:gt(5)").addClass("no-view");
				$(".hp.column.one .upcomingevents .ui-articles.two li:gt(5)").hide();
			break;
			case "480":
				//LIMIT EVENTS ON HOMEPAGE
                $(".hp.column.one .upcomingevents .ui-articles.one li:gt(1)").css("margin-right",".93%");
                $(".hp.column.one .upcomingevents .ui-articles.one li:gt(2)").css("margin-right","0px");
                $(".hp.column.one .upcomingevents .ui-articles.one li:lt(4)").removeClass("no-view");
				$(".hp.column.one .upcomingevents .ui-articles.two li:lt(4)").show();
        		$(".hp.column.one .upcomingevents .ui-articles.one li:gt(3)").addClass("no-view");
				$(".hp.column.one .upcomingevents .ui-articles.two li:gt(3)").hide();
			break;
			case "320":
				//LIMIT EVENTS ON HOMEPAGE
                $(".hp.column.one .upcomingevents .ui-articles.one li:gt(1)").css("margin-right","0px");
        		$(".hp.column.one .upcomingevents .ui-articles.one li:gt(2)").addClass("no-view");
				$(".hp.column.one .upcomingevents .ui-articles.two li:gt(2)").hide();
			break;
		}
	}

	function mediaRotator() {
        if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length){
            // MULTIMEDIA ROTATOR
            $("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
                    "imageWidth"              : 1500,
                    "imageHeight"             : 915,
                    "efficientLoad"           : "yes",
                    "appHeader"               : "no",
                    "titles"                  : "yes",
                    "captions"                : "yes",
                    "galleryOverlay"          : "no",
                    "linkedElement"           : ["image"], // ["image", "title", "overlay", "all"]
                    "playPause"               : "no",
                    "mainControls"            : "yes",
                    "contMoreLink"            : "no",
                    "contMoreLinkText"        : "Read More",
                    "descMoreLink"            : "yes",
                    "descMoreLinkText"        : "MORE",
                    "descVideoLink"           : "no",
                    "descVideoLinkText"       : "Watch Video",
                    "bullets"                 : "no",
                    "bulletControls"          : "no",
                    "thumbnails"              : "no",
                    "thumbnailWidth"          : 122,
                    "thumbnailHeight"         : 53,
                    "thumbnailSlideDirection" : "horizontal", // horizontal or vertical
                    "hoverPause"              : "yes",
                    "autoRotate"              : "yes",
                    "fullScreenRotator"       : "yes", // image dimensions work well at 1500x915
                    "fullScreenBreakpoints"   : ["960"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
                    "fullScreenVideo"         : "no",
                    "transitionType"          : "fade", // fade or slide
                    "transitionSpeed"         : 3,
                    "transitionDelay"         : 8,
                    "onImageLoad"             : function() { },
                    "allImagesLoaded"         : function() { },
                    "onThumbnailLoad"         : function() { },
                    "allThumbnailsLoaded"     : function() { },
                    "onTransitionStart"       : function() { },
                    "onTransitionEnd"         : function() { },
                    "watchVidClick"           : function() { },
                    "closeVidClick"           : function() { },
                    "allLoaded"               : function() { 
                    							$(".rs-gallery-container .rs-gallery-desc .rs-gallery-title").prepend('


');
                    							$(".rs-gallery-control.back").prependTo(".rs-gallery-control-new-outer-bottom");
                    							$(".rs-gallery-control.next").prependTo(".rs-gallery-control-new-outer-top");
                                                if($(".rs-gallery-container .rs-gallery-images ul.rs-gallery-images-list li").length == "1"){
                                                	$(".rs-gallery-container .rs-gallery-desc").addClass("one-image");
                                                }
                    }

            });
		}
	}

	function useChannelForSection(){
		//USE CHANNEL NAME FOR PAGE LIST NAVIGATION HEADER IF ONE NOT PRESENT
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
        }

    function searchControls() {
        $("#gb-search-btn").click(function(e){
            e.preventDefault();
            animateSearch();
        });
        $("#gb-search-btn").keydown(function(e){
            if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
            	e.preventDefault();
            	animateSearch();
            }
        });
        var searchTimer = 0;
        $("#gb-search-container").mouseenter(function() {
        	clearTimeout(searchTimer);
        });
        $("#gb-search-container").mouseleave(function() {
        	searchTimer = setTimeout(function(){
            	$("#sw-search-input").animate({
                    width: "0px"
                }, 500, function() {
                    $("#gb-search-btn").attr("aria-label", "Open search").focus();
                    $("#gb-search-container").removeClass("open");
                    $("#sw-search-input").removeClass("open");
                    $("#gb-search-form label, #gb-search-input").attr("aria-hidden", "true");
                });
            }, 1000);
        });
	}

    function animateSearch() {
        if(!$("#gb-search-container").hasClass("open")) {
            $("#gb-search-btn").attr("aria-label", "Close search");
            $("#gb-search-container").addClass("open");
            $("#sw-search-input").addClass("open");
            $("#gb-search-form label, #gb-search-input").attr("aria-hidden", "false");

            $("#sw-search-input").animate({
                width: "197px"
            }, 500, function() {
                $("#sw-search-input").focus();
            });
        } else {
            $("#sw-search-input").animate({
                width: "0px"
            }, 500, function() {
                $("#gb-search-btn").attr("aria-label", "Open search").focus();
                $("#gb-search-container").removeClass("open");
                $("#sw-search-input").removeClass("open");
                $("#gb-search-form label, #gb-search-input").attr("aria-hidden", "true");
            });
        }

        //CLICK FUNCTION FOR SEARCH DROPDOWN ALTERNATIVE OPTION
		var getWidth = $(window).width();
		if (getWidth >= 1024) {
			$("#gb-global-icons .icn.search").click(function(e) {
			e.preventDefault();
				$("#gb-search-bottom").toggle("slide",{ direction: "right" },500);
			});
		} else {
			$("#gb-global-icons .icn.search").click(function(e) {
			e.preventDefault();
				$("#gb-search-bottom").toggle("slide",{ direction: "down" },500);
			});
		}
    }    

	function dynamicContent(){

		// ********** MYSTART ********** //

        //APPEND DISTRICT HOME TO FOOTER
        $("#sw-mystart-left .sw-mystart-button.home").prependTo(".gb-footer-one-bottom");
        $(".sw-mystart-button.home a span").html('
');
        $(".sw-mystart-button.home a").attr("aria-label","District Home");

		//TO FIX THE ISSUE OF SEARCH NOT ALLOWING SPACES
        $("#sw-mystart-search").unbind();

		//APPEND SEARCH TO GB-SEARCH-BOTTOM
		$("#sw-mystart-search").appendTo("#gb-search-container");

        //ADD ROLE AND ARIA LABEL TO SEARCH BUTTON
        $("#sw-search-button").attr("role","button").attr("aria-label","Submit Search");

        //REPLACE MYSTART DROPDOWN ARROWS
		$(".sw-myaccount-chevron").text("");
		$(".sw-dropdown-arrow").text("");

        //ADD TRANSITION CLASS TO ALL MYSTART DROPDOWNS
		$(".sw-mystart-dropdown").addClass("transition");

        //CHANGE THE SCHOOLLIST TEXT
		$(".sw-mystart-dropdown.schoollist .selector").text("Our Schools");

        $("#sw-mystart-left .sw-mystart-dropdown.schoollist").prependTo("#gb-header-right");

		//ADD TRANSITION CLASS TO ALL MYSTART BUTTONS
		$(".sw-mystart-button").addClass("transition");

		//APPEND MYSTART ITEMS TO APPROPRIATE PLACES

        $(".sw-mystart-button.signin").appendTo(".gb-footer-one-bottom");
        $(".sw-mystart-button.register").appendTo(".gb-footer-one-bottom");

        //DOC ADD CUSTOM CLICK EVENT FOR SCHOOLLIST DROPDOWN
		$(".sw-mystart-dropdown").off();
		$(".sw-mystart-dropdown").click(function(){
			$(this).toggleClass("open");
			$(".sw-dropdown-arrow",this).toggleClass("open");
			$(".sw-dropdown", this).slideToggle("slow", "swing").attr("aria-hidden", ($(".sw-dropdown", this).attr("aria-hidden") == "true") ? "false" : "true");
		});

		//DOC ADD CUSTOM CLICK EVENT FOR MYACCOUNT DROPDOWN
		$("#sw-mystart-account .sw-dropdown").attr("id","cs-sw-myaccount-list").removeClass("sw-dropdown");
		$("#sw-mystart-account").off();
		$("#sw-mystart-account").click(function(){
			$(".icon-font-arrow",this).toggleClass("open");
			$("#cs-sw-myaccount-list").slideToggle("slow", "swing").attr("aria-hidden", ($("#cs-sw-myaccount-list", this).attr("aria-hidden") == "true") ? "false" : "true");
		});

        $("#gb-header-right .sw-mystart-dropdown .sw-dropdown-arrow").addClass("icon-font-arrow");

		// ********** HEADER ********** //

		//ADD LOGO
		var logoCheck = "true";
		var logoSrc = jQuery.trim("/cms/lib/GA02000365/Centricity/Template/GlobalAssets/images///logos/CitySchoolsofDecatur_logo.png");
		if(logoCheck == "true") {
			var srcSplit = logoSrc.split("/");
			var srcSplitLen = srcSplit.length;
			if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
				$("#gb-logo").append('').removeClass("hidden");
			} else {
				$("#gb-logo").append('').removeClass("hidden");
			}
		}

        //ADD SITENAME
        var siteNameOne = jQuery.trim('City Schools of Decatur');
        var siteNameTwo = jQuery.trim('');
        if((siteNameOne == "") && (siteNameTwo == "")) {
            var splitLen = 2;
            var siteName = "City Schools of Decatur";
            siteName = siteName.split(" ");
            var siteNameLength = siteName.length;
            if(siteNameLength > 2){
                siteNameEnd = jQuery.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
            } else {
                siteNameEnd = jQuery.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
            }
            siteNameBegin = jQuery.trim(siteName.toString().replace(/,/g, " "));
            $("#gb-sitename").prepend("
" + siteNameBegin + "
" + siteNameEnd + "");
        } else if((siteNameOne != "") && (siteNameTwo == "")) {
            $("#gb-sitename").prepend("
" + siteNameOne + "");
        } else if((siteNameOne == "") && (siteNameTwo != "")) {
            $("#gb-sitename").prepend("
" + siteNameTwo + "");
        } else if((siteNameOne != "") && (siteNameTwo != "")) {
            $("#gb-sitename").prepend("
" + siteNameOne + "
" + siteNameTwo + "");
        }


		// ********** CHANNEL BAR ********** //

		//ADD FIRST CLASS TO LAST CHANNEL ITEM
		$("li.sw-channel-item").first().addClass("first");
		//ADD LAST CLASS TO LAST CHANNEL ITEM
		$("li.sw-channel-item").last().addClass("last");

		//UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
		$(".sw-channel-item").unbind('hover');
		$(".sw-channel-item").hover(function(){
			var subList = $(this).children('ul');
			if ($.trim(subList.html()) !== "") {
				subList.slideDown(300, "swing");
			}
			$(this).addClass("hover");
		}, function(){
			$(".sw-channel-dropdown").slideUp(300, "swing");
			$(this).removeClass("hover");
		});

		//ADD TRANSITION CLASS TO ALL CHANNEL ITEMS
		$("ul.sw-channel-list li.sw-channel-item").addClass("transition");



		// ********** BODY ********** //

        var channelHeight = $("#gb-channel-list-outer").height();
        $("#hp-slideshow-outer").css("margin-top",channelHeight);
        $(".sp header").css("margin-top",channelHeight);
        $(window).resize(function(){
            var channelHeight = $("#gb-channel-list-outer").height();
            $("#hp-slideshow-outer").css("margin-top",channelHeight);
            $(".sp header").css("margin-top",channelHeight);
        });

		//RENAME FIRST BREADCRUMB
		$("li.ui-breadcrumb-first > a > span").text("Home");

		//REPLACE MORE-LINK TEXT
		$("a.more-link span").text("More ");

		//AUTO-FOCUS UERSNAME SIGIN FIELD
		$("#swsignin-txt-username").focus();

		//CENTER SIGNED OUT MESSAGE AND SIGNIN BUTTON
        if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
            $("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
            //ADD SIGNED OUT BREADCRUMB
            $(".ui-breadcrumbs").append("
Signed Out");
        }

        //ADD SIGN IN BREADCRUMB
        if($("#swlogin").length){
            $("#sw-content-layout-wrapper .ui-widget.app").addClass("signin");
            $(".ui-breadcrumbs").append("
Sign In");
        }

        	//REGISTER BREADCRUMB
		if($("#swageprompt-txt-birthmonth").length && $("#swageprompt-txt-birthday").length && $("#swageprompt-txt-birthyear").length) {
			$("#sw-content-layout-wrapper .ui-widget.app").addClass("register");
			$(".ui-breadcrumbs").append("
Register");
		}

		//SEARCH RESULTS BREADCRUMB
		if($("#sw-content-layout-wrapper.ui-spn #swsearch-pnl-main").length) {
			$("#sw-content-layout-wrapper").children().wrapAll('

');
			$(".ui-breadcrumbs").append("
Search Results");
		}

		//CHANGE TEXT OF CALENDAR LINK
		$("a.view-calendar-link").text("view full calendar");

		//SCROLL FUNCTION FOR BACK TO TOP BUTTON
        $('.scroll-top').on('click keypress', function(e){
          if(a11yClick(e) === true){
           	e.preventDefault();
        		$("html, body").animate({ scrollTop: $("body").offset().top}, 1500);
                $("li.sw-channel-item:first-child a").focus();
          }
        });


        //SLIDESHOW ARROW SCROLL
        var channelHeight = $("#gb-channel-list-outer").height();
		$("#gb-scroll-down").click(function(){
			$("html, body").animate({scrollTop: ($(".hp.column.one").offset().top) - channelHeight - 15}, "slow");	
		});

		// APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
        $("div.ui-widget.app .ui-widget-detail img")
            .not($("div.ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail img"))
            .not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
            .each(function() {
                if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                    $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                }
        });

        //MOVE VIEW MORE LINK INTO THE HEADER
        $(".hp.column.two .ui-widget.app.headlines").each(function(){
            if($(".more-link", this).length > 0) {	
                $("a.more-link span", this).text("View All");
                $("a.more-link", this).insertAfter(".hp.column.two .ui-widget.app.headlines .ui-widget-header h1");
            }
        });

        //DOC dont add app into accordion if it doesnt have a header
            $("#hp-content-lower div.ui-widget.app").csAppAccordion({
                "accordionBreakpoints" : [768, 640, 480, 320]
            });

            $(".sp.column.one").csAppAccordion({
                "accordionBreakpoints" : [768, 640, 480, 320]
            });

		// ********** FOOTER ********** //
		//PHONE AND FAX NUMBERS
		if("404-371-3601" != "") {
			$(".gb-footer-contact.phone").removeClass("hidden");
		}
		if("websupport@csdecatur.net" != "") {
			$(".gb-footer-email").removeClass("hidden");
		}
		//GET SCHOOLWIRES FOOTER LINKS
		var feedbackURL = $("div#sw-footer-links li:eq(0) a").attr("href");
        var feedbackText = $("div#sw-footer-links li:eq(0) a").text();
		var termsURL = $("div#sw-footer-links li:eq(2) a").attr("href");
        var termsText = $("div#sw-footer-links li:eq(2) a").text();
		var policyURL = $("div#sw-footer-links li:eq(1) a").attr("href");
        var policyText = $("div#sw-footer-links li:eq(1) a").text()
        var copyrightText = $("#sw-footer-copyright").text();
		var swLogo = $("#sw-footer-logo a img").attr("src");
		var swURL = $("#sw-footer-logo a").attr("href");

		$("a#feedback").attr("href", feedbackURL).text(feedbackText);
		$("a#terms").attr("href", termsURL).text(termsText);
		$("a#policy").attr("href", policyURL).text(policyText);
        $(".sw-footer.copyright p.copyright").text(copyrightText);
		$("#sw-logo a").attr("href", swURL);	
        $("#sw-logo a img").attr("src", swLogo);	



	}

	function searchText() {
		//CUSTOM MYSTART SEARCH PLACEHOLDER
	        //SEARCH TEXT
	        $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
		$("div#sw-mystart-search input#sw-search-input").attr("value", "Search...");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "Search...") {
				$(this).val("");
			}
		});
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("Search...");
			}
		});
	}


	function modEvents(){
        if (!$(".upcomingevents .joel-month").length) {
            $(".upcomingevents").modEvents({
                columns     : "yes",
                monthLong   : "no",
                dayWeek     : "yes"
            });
            eventsByDay(".upcomingevents .ui-articles");
        }
	}		

    function eventsByDay(container) {

    	// FOR SCOPE
            var _this = this;

        var countLi = 0;
        $(".ui-article", container).each(function(){
            if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
                var moveArticle = $(this).html();
                $(this).parent().prev().children().children().next().append(moveArticle);
                $(this).parent().remove();
            } else {
                $(".sw-calendar-block-date", this).wrapInner('');
                $(".joel-month", this).parents(".sw-calendar-block-date").prepend($(".joel-month", this));
                $(".joel-day", this).parents(".sw-calendar-block-date").prepend($(".joel-day", this));

            }

        });


        $(".upcoming-column.right .ui-article-description").each(function() {
                if($(".sw-calendar-block-time", this).length === 0) {
                    $(this).prepend('All Day');
                }
        });

        $(".view-calendar-link span").text("VIEW FULL CALENDAR");

        $(".hp.column.one .upcomingevents").each(function() {
            $(".ui-articles", this).addClass("one flex").attr("role", "tablist");
            //$(".ui-widget-detail", this).append('');
            $('').insertBefore(".ui-widget-detail .view-calendar-link", this);

            $(".ui-articles > li", this).each(function(i) {
            	var activeClass = (i == 0) ? " active" : "";
            	$(this).addClass("cs-event-" + (i + 1) + activeClass).attr({"id": "cs-event-tab-" + (i + 1), "role": "tab", "aria-controls": "cs-event-panel-" + (i + 1), "aria-label": "Upcoming Events for " + $(".joel-day", this).text() + ", " + $(".joel-month", this).text() + " " + $(".joel-date", this).text()});
                if(i == 0) {
                	$(this).attr({"tabindex": "0", "aria-selected": "true"});
                } else {
                	$(this).attr({"tabindex": "-1", "aria-selected": "false"});
                }
            	$(this).parent().parent().find(".ui-articles.two").append('
' + $(".upcoming-column.right", this).html() + '');
                $(".upcoming-column.right", this).remove();
                $("h1.ui-article-title", this).unwrap().unwrap();
            });

        });
        $(".hp.column.one .ui-widget.app.upcomingevents .ui-articles.one").on("hover focus keydown", "> li", function(e) {
        	$(".hp.column.one .ui-widget.app.upcomingevents .ui-articles.one > li.active").attr({"tabindex": "-1", "aria-selected": "false"});
        	$(".hp.column.one .ui-widget.app.upcomingevents .ui-articles > li.active").removeClass("active");
            $(this).parent().parent().find(".ui-articles.two").find("> li." + $(this).attr("class")).addClass("active");
            $(this).addClass("active").attr({"tabindex": "0", "aria-selected": "true"});

            if(e.type == "keydown") {
            	switch(e.keyCode) {
                	case csKeyCodes.up:
                    case csKeyCodes.left:
                    	e.preventDefault();

                    	if($(this).is(":first-child")) {
                            $(this).parent().find("li:last-child").attr({"tabindex": "0", "aria-selected": "true"}).focus();
                        } else {
                        	$(this).prev("li").attr({"tabindex": "0", "aria-selected": "true"}).focus();
                        }

                        $(this).attr({"tabindex": "-1", "aria-selected": "false"});
                    break;
                    case csKeyCodes.down:
                    case csKeyCodes.right:
                    	e.preventDefault();

                        if($(this).is(":last-child")) {
                            $(this).parent().find("li:first-child").attr({"tabindex": "0", "aria-selected": "true"}).focus();
                        } else {
                        	$(this).next("li").attr({"tabindex": "0", "aria-selected": "true"}).focus();
                        }

                        $(this).attr({"tabindex": "-1", "aria-selected": "false"});
                    break;
                }
            }
        });

        $(".upcomingevents").each(function() {
        	var element = $(this);
        	$(".ui-widget-header", element).append('Stay up-to-date with our events and school schedules');
        });



        $(document).on("hover keydown", ".cs-events-btn", function(e) {
                e.preventDefault();
                // REMOVE ACTIVE STATE
                $(this).removeClass("active");
                $(this).siblings().removeClass("active");
                $(this).parent().siblings(".ui-articles").find("li").removeClass("active").attr("tabindex", "-1").attr("aria-selected","false");
                // ADD ACTIVE STATE
                $(this).addClass("active");
                $(this).parent().siblings(".ui-articles").find("li").eq($(this).index()).addClass("active").attr("tabindex", "0").attr("aria-selected","true");
                $(this).parent().siblings(".ui-articles").find("li").eq($(this).index()).find("a").eq(0).focus();
         });    
    }    

	function calendarListView(){
		//AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
		if(!$("a.ui-btn-toggle.list").hasClass("active")){
			$("a.ui-btn-toggle.list").click();
		}
	}

    function buildStyleSheet(){
		var secondaryColor90 = "rgba(" + hexToRgb('#1d2731') + ", .9)";
        var accentColor80 = "rgba(" + hexToRgb('#fbb601') + ", .8)";

		var dynStyleSheet = document.createElement('style');

		if(dynStyleSheet) {
			dynStyleSheet.setAttribute('type', 'text/css');
			var head = document.getElementsByTagName('head')[0];

			if(head) {
				head.insertBefore(dynStyleSheet, head.childNodes[0]);
			}

			var dynStyles =	'#hp-slideshow:before {' +
					'	background: ' + secondaryColor90 + ';' +
                    '}' +
                    '#hp-slideshow .rs-gallery-container .rs-gallery-desc-more-link:hover:before {' +
                    '	border-color: transparent ' + accentColor80 + ' transparent transparent;' +
                    '}' +
                    '.hp.column.one div.ui-widget.app.upcomingevents .view-calendar-link:hover:before {' +
                    '	border-color: transparent ' + accentColor80 + ' transparent transparent;' +
                    '}' +
					'@media (max-width: 767px) {' +
						'#hp-slideshow .rs-gallery-container {' +
							'	background: ' + secondaryColor90 + ' !important;' +
							'}' +
					'}';

			var rules = document.createTextNode(dynStyles);

			if(dynStyleSheet.styleSheet){ // IE
				dynStyleSheet.styleSheet.cssText = dynStyles;
			} else {
				dynStyleSheet.appendChild(rules);
			}
		}
	}

	function hexToRgb(h){
	        var r = parseInt((cutHex(h)).substring(0,2),16);
	        var g = parseInt((cutHex(h)).substring(2,4),16);
	        var b = parseInt((cutHex(h)).substring(4,6),16);
	        return r + ', ' + g + ', ' + b;
	}

	function cutHex(h) {
	        return (h.charAt(0)=="#") ? h.substring(1,7):h;
	}

