    function stickyCalendarFix() {
        var channelHeight = $("#gb-channel-list-outer").height();

        if ($(window).scrollTop() <= channelHeight) {

            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                $(".wcm-controls").css("margin-top", "0px");
            }
        } else {

            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                if($(".wcm-controls").hasClass("wcm-stuck")) {
                    $(".wcm-controls").css("margin-top", channelHeight + "px");
                }
            }
        }
    }

    function a11yClick(event){
        if(event.type === 'click'){
            return true;
        }
        else if(event.type === 'keypress'){
            var code = event.charCode || event.keyCode;
            if((code === 32)|| (code === 13)){
                return true;
            }
        }
        else{
            return false;
        }
    }

    function accessibility() {

    	$("#sw-search-button").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME DOWN ARROW
                case csKeyCodes.space:
                	e.preventDefault();
                    SWGoToSearchResultsPageswsearchinput();

                break;
            }
        });
        $("#sw-search-input").attr("tabindex","-1");

        $("#sw-search-input").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME DOWN ARROW
                case csKeyCodes.tab:
                	animateSearch();

                break;
            }
        });

        $(".sw-mystart-dropdown").each(function(i) {
            // SET ATTRIBUTES
            $(this).attr("role", "button");
            $(".selector", this).attr("id", "mystart-dropdown-" + (i + 1));
            $(".sw-dropdown-list", this).attr("role", "menu").attr("aria-labelledby", $(".selector", this).attr("id"));
            $(".sw-dropdown", this).attr("aria-hidden", "true");
            $(".sw-dropdown-list li", this).attr("role", "menuitem");
        });

        $(".sw-mystart-dropdown.schoollist .sw-dropdown-list li a").attr("tabindex", "0");

        $(".sw-mystart-dropdown").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME DOWN ARROW
                case csKeyCodes.down:
                case csKeyCodes.enter:
                case csKeyCodes.space:
                    e.preventDefault();
                    $(".sw-dropdown", this).slideToggle("slow", "swing").attr("aria-hidden", ($(".sw-dropdown", this).attr("aria-hidden") == "true") ? "false" : "true");

                    // CHECK TO MAKE SURE THIS DROPDOWN ISNT ALREADY OPEN
                      if(!$(this).hasClass("open")) {
                          // TOGGLE THE DROPDOWN
                          $(this).addClass("open");
                          $(".sw-dropdown-list li:first-child a", this).focus();
                      } else {
                      		$(this).removeClass("open");
                      }
                      $(".sw-dropdown-list li a", this).attr("tabindex","0");
                    // FOCUS FIRST ITEM

                break;
            }
        });

        $(".sw-mystart-dropdown .sw-dropdown-list li a").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().is(":first-child")) {
                        // FOCUS LAST ITEM
                        $(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).parent().prev("li").find("> a").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    e.preventDefault();
                    e.stopImmediatePropagation();

                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $(this).closest(".sw-dropdown-list").find("li:first-child > a").focus();
                    } else {
                        // FOCUS NEXT ITEM
                        $(this).parent().next("li").find("> a").focus();
                    }
                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $(this).closest(".sw-dropdown-list").find("li:first-child > a").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    e.preventDefault();

                    // FOCUS LAST ITEM
                    $(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
                break;
            }
        });

        $("#cs-sw-myaccount-list li").each(function(){
            $("> a", this).attr("tabindex", "0"); 
        }); 

        $("#sw-myaccount").attr("role","button");

        $("#sw-myaccount").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME DOWN ARROW
                case csKeyCodes.down:
                case csKeyCodes.enter:
                case csKeyCodes.space:
                    e.preventDefault();


                    $(this).next("#cs-sw-myaccount-list").slideToggle("slow", "swing").attr("aria-hidden", ($("#cs-sw-myaccount-list").attr("aria-hidden") == "true") ? "false" : "true");


                    // CHECK TO MAKE SURE THIS DROPDOWN ISNT ALREADY OPEN
                 	if(!$(this).hasClass("open")) {
                          // TOGGLE THE DROPDOWN
                     	$(this).addClass("open");
                      	$(this).next("#cs-sw-myaccount-list").find("li:first-child a").focus();
                    } else {
                      	$(this).removeClass("open");
                    }
                    // FOCUS FIRST ITEM

                break;
            }
        });    

        $("#cs-sw-myaccount-list li a").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().is(":first-child")) {
                        // FOCUS LAST ITEM
                        $(this).closest("#cs-sw-myaccount-list").find("li:last-child > a").focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).parent().prev("li").find("> a").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    e.preventDefault();
                    e.stopImmediatePropagation();

                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $(this).closest("#cs-sw-myaccount-list").find("li:first-child > a").focus();
                    } else {
                        // FOCUS NEXT ITEM
                        $(this).parent().next("li").find("> a").focus();
                    }
                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $(this).closest("#cs-sw-myaccount-list").find("li:first-child > a").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    e.preventDefault();

                    // FOCUS LAST ITEM
                    $(this).closest("#cs-sw-myaccount-list").find("li:last-child > a").focus();
                break;
            }
        });

        $("#gb-channel-list .mega-channel .mega-channel-cat .mega-channel-cat-section-list li a").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    e.preventDefault();
                    e.stopImmediatePropagation();

                    // IS FIRST ITEM
                    if($(this).parent().parent().hasClass("one") && $(this).parent().is(":first-child") && $(this).parent().parent().parent().next(".mega-channel-cat.two").length) {
                        // FOCUS LAST ITEM
                        $(this).parent().parent().parent().next(".mega-channel-cat").find(".mega-channel-cat-section-list li:last-child > a").focus();
                    } else if($(this).parent().parent().hasClass("two") && $(this).parent().is(":first-child") && $("#gb-channel-list .mega-channel .mega-channel-cat.two").length) {
                    	$(this).parent().parent().parent().prev(".mega-channel-cat").find(".mega-channel-cat-section-list li:last-child > a").focus();
                    } else if($(this).parent().parent().hasClass("one") && $(this).parent().is(":first-child") && !$(this).parent().parent().parent().next(".mega-channel-cat.two").length) {   
                    	$(this).parent().parent().find("li:last-child > a").focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).parent().prev("li").find("> a").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    e.preventDefault();
                    e.stopImmediatePropagation();

                    // IS LAST ITEM
                    if($(this).parent().parent().hasClass("one") && $(this).parent().is(":last-child") && $(this).parent().parent().parent().next(".mega-channel-cat.two").length) {
                        // FOCUS LAST ITEM
                        $(this).parent().parent().parent().next(".mega-channel-cat").find(".mega-channel-cat-section-list li:first-child > a").focus();
                    } else if($(this).parent().parent().hasClass("two") && $(this).parent().is(":last-child")) {
                    	$(this).parent().parent().parent().prev(".mega-channel-cat").find(".mega-channel-cat-section-list li:first-child > a").focus();
                    } else if($(this).parent().parent().hasClass("one") && $(this).parent().is(":last-child") && !$(this).parent().parent().parent().next(".mega-channel-cat.two").length) {   
                    	$(this).parent().parent().find("li:first-child > a").focus();    
                    } else {
                        // FOCUS NEXT ITEM
                        $(this).parent().next("li").find("> a").focus();
                    }
                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $(this).closest(".mega-channel-cat-section-list").find("li:first-child > a").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    e.preventDefault();

                    // FOCUS LAST ITEM
                    $(this).closest(".mega-channel-cat-section-list").find("li:last-child > a").focus();
                break;
            }
        });

    }

	function csGetBreakPoint() {
		return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
	}

    function csOpenPageSubMenu(li) {
        if (li.prop('tagName').toLowerCase() == "li") {
            if (li.hasClass('expandable')) {  
                li.removeClass('expandable').addClass('collapsible');
            }
            if (li.find('div:first').hasClass('expandable')) {
                li.find('div:first').removeClass('expandable').addClass('collapsible');
            }
            li.find('ul:first').slideDown();

            PageNavigationStateCookie();
        }
    }

    function csClosePageSubMenu(li) {
        if (li.prop('tagName').toLowerCase() == "li") {
            li.removeClass('collapsible').addClass('expandable');
            li.find('div:first').removeClass('collapsible').addClass('expandable');	 
            li.find('ul:first').slideUp();

            PageNavigationStateCookie();
        }
    }

