      (function() {
          "use strict";
          var el = document.getElementsByTagName('html')[0],
          nojsClass = "no-js",
          jsClass = "js";

          if (el.classList) {
              el.classList.remove(nojsClass);
              el.classList.add(jsClass);
          } else {
              el.className = el.className.replace(new RegExp('(^|\\b)' + nojsClass.split(' ').join('|') + '(\\b|$)', 'gi'), ' ');
              el.className += ' ' + jsClass;
          }
      }());

