var translatorCheckInterval = setInterval(function(){
    if ($(".sw-mystart-dropdown.translate").length > 0) {
        window.clearInterval(translatorCheckInterval);
        var pageURL = window.location.href.replace(/&/g, "%26");
        pageURL = encodeURIComponent(pageURL);
        var translatorAddition = '
' + 'English to Polish' +
                                ''
        $(".sw-mystart-dropdown.translate .sw-dropdown-list li").eq(1).after(translatorAddition)
    }
},500);

