		        "secondaryColor"        : "#333333", // DEFAULT - #222A30
		        "accentColor"           : "#585858", // DEFAULT - #B5BBBF
		        "menuButtonParent"      : "#gb-channel-list",
		        "showMenuBtnText"       : "yes",
		        "menuBtnText"           : "MENU",
		        "showSchools"           : "yes",
		        "showTranslate"         : "yes",
		        "showAccount"           : "yes",
		        "extraMenuOptions"      : "Popular Links", // EXTRA MENU HEADING NAMES SEPARATED BY A ;
		        "extraMenuItems"        : "1, Calendar,/Page/2,_self;1,Menus,https://www.danville.k12.in.us/Page/546,_self;1,Menus,/Page/118,_self;1,Email,https://sites.google.com/a/danville.k12.in.us/staff/home,_blank", // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
