	});

	function pgScreenSize(){
		//var getWidth = ($("body").hasClass("msie8")) ? screen.width : window.outerWidth;
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			photoGallery();
			boxSizes();
			if(!$(".gb-mystart-right #sw-mystart-mypasskey").length) {
				$("#sw-mystart-mypasskey").before($(".sw-mystart-search"));
			}
			if(!$(".gb-mystart-right #google_translate_element").length) {
				$("#google_translate_element").appendTo($(".gb-mystart-right"));
			}
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			photoGallery();
			appView();
			if(!$(".gb-channel-list-mystart #sw-mystart-mypasskey").length) {
				$("#sw-mystart-mypasskey").appendTo($(".gb-channel-list-mystart"));
			}
			if($(".gb-mystart-right #google_translate_element").length) {
				$("#google_translate_element").appendTo($("#rs-menu"));
			}
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			photoGallery();
			appView();
			if(!$(".gb-channel-list-mystart #sw-mystart-mypasskey").length) {
				$("#sw-mystart-mypasskey").appendTo($(".gb-channel-list-mystart"));
			}
			if($(".gb-mystart-right #google_translate_element").length) {
				$("#google_translate_element").appendTo($("#rs-menu"));
			}
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			photoGallery();
			appView();
			if(!$(".gb-channel-list-mystart #sw-mystart-mypasskey").length) {
				$("#sw-mystart-mypasskey").appendTo($(".gb-channel-list-mystart"));
			}
			if($(".gb-mystart-right #google_translate_element").length) {
				$("#google_translate_element").appendTo($("#rs-menu"));
			}
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			photoGallery();
			appView();
			if(!$(".gb-channel-list-mystart #sw-mystart-mypasskey").length) {
				$("#sw-mystart-mypasskey").appendTo($(".gb-channel-list-mystart"));
			}
			if($(".gb-mystart-right #google_translate_element").length) {
				$("#google_translate_element").appendTo($("#rs-menu"));
			}
		}
	}

	function photoGallery(){
		if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length){
		        // MULTIMEDIA ROTATOR
		        $("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
		                "imageWidth"              : 1500,
		                "imageHeight"             : 915,
		                "efficientLoad"           : "no",
		                "appHeader"               : "no",
		                "titles"                  : "yes",
		                "captions"                : "yes",
		                "linkedElement"           : ["image"], // ["image", "title", "overlay", "all"]
		                "bullets"                 : "yes",
		                "hoverPause"              : "no",
		                "autoRotate"              : "yes",
		                "fullScreenRotator"       : "yes", // image dimensions work well at 1500x915
		                "fullScreenBreakpoints"   : ["960"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
		                "fullScreenVideo"         : "yes",
		                "transitionType"          : "fade", // fade or slide
		                "transitionSpeed"         : 3,
		                "transitionDelay"         : 8,
		                "onTransitionStart"       : function() { 
		                	$("#hp-slideshow .rs-gallery-desc").animate({left: -$("#hp-slideshow .rs-gallery-desc").outerWidth()}), 500;
		                },
		                "onTransitionEnd"         : function() { 
		                	$("#hp-slideshow .rs-gallery-desc").animate({left: 0}), 500;
		                },
		                "allLoaded"               : function() {
		              		$("#hp-slideshow .rs-gallery-desc").css("left", -$("#hp-slideshow .rs-gallery-desc").outerWidth());
				 	$("#hp-slideshow .rs-gallery-desc").animate({left: 0}), 500;
				 	$(".rs-gallery-container").unbind('mouseenter mouseleave');
		                 }
		        });
		}
	}

	function appView() {
		//DOC dont add app into accordion if it doesnt have a header
		$("#hp-content-wrapper div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}
		});

		//DOC make homepage apps collapsible for small mobile devices
		if(!$("#hp-content-wrapper div.ui-widget.app.active").length) {
			$("#hp-content-wrapper div.ui-widget.app").eq(0).addClass("active");
		}

		$("#hp-content-wrapper div.ui-widget-header").on("click", function(){	
			if($(this).parent().hasClass("active")) {
				$(this).parent().removeClass("active");
			} else {	
				var clickedApp = $(this).parent().parent().attr("id");
				$("div.ui-widget.app").removeClass("active");
				$(this).parent().addClass("active");

				$("html, body").animate({
					scrollTop: $("#" + clickedApp).offset().top
				}, 500, function(){
					$("html, body").stop(true, false);
				});
			}
		});
	}	

