	function jsMediaQueries() {
		switch(csGetBreakPoint()) {
			case "desktop": 
            	if(!$("#hp-slideshow .hp-slideshow.right").length){
					$(".hp-slideshow.right").insertAfter(".hp-slideshow.left");
                }
                $("#gb-mystart-right").insertAfter("#gb-mystart-left");
                $("#hp-slideshow .rs-gallery-desc").insertAfter("#hp-slideshow .rs-gallery-images");
                tabAccordionReset();
			break;
			case "768":
				$(".hp-slideshow.right").prependTo("#hp-content-top");
                $("#gb-mystart-right").insertAfter("#gb-mystart-left");
                $("#hp-slideshow .rs-gallery-desc").insertAfter("#hp-slideshow .rs-gallery-images");
                tabAccordionReset();
			break;
			case "640":
            	$(".hp-slideshow.right").prependTo("#hp-content-top");
				$("#gb-mystart-right").insertAfter("#gb-mystart-left");
                $("#hp-slideshow .rs-gallery-desc").insertAfter("#hp-slideshow .rs-gallery-images");
                tabAccordionReset();
			break;
			case "480":
            	$(".hp-slideshow.right").prependTo("#hp-content-top");
				$("#gb-mystart-right").insertAfter("#gb-mystart-outer");
                $("#hp-slideshow .rs-gallery-desc").insertAfter("#hp-slideshow .rs-gallery-container");
                tabAccordion();
			break;
			case "320":
            	$(".hp-slideshow.right").prependTo("#hp-content-top");
				$("#gb-mystart-right").insertAfter("#gb-mystart-outer");
                $("#hp-slideshow .rs-gallery-desc").insertAfter("#hp-slideshow .rs-gallery-container");
                tabAccordion();
			break;
		}
	}

	function multimediaRotator(){
    	head.ready("multimediaRotator", function(){
            if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length){
                // MULTIMEDIA ROTATOR
                $("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
                    		"imageWidth"              : 658,
                            "imageHeight"             : 394,
                            "efficientLoad"           : "yes",
                            "appHeader"               : "no",
                            "titles"                  : "yes",
                            "captions"                : "yes",
                            "galleryOverlay"          : "no",
                            "linkedElement"           : ["image", "title"], // ["image", "title", "overlay", "all"]
                            "playPause"               : "yes",
                            "mainControls"            : "yes",
                            "contMoreLink"            : "no",
                            "contMoreLinkText"        : "Read More",
                            "descMoreLink"            : "no",
                            "descMoreLinkText"        : "Read More",
                            "bullets"                 : "no",
                            "bulletControls"          : "no",
                            "thumbnails"              : "no",
                            "thumbnailWidth"          : 122,
                            "thumbnailHeight"         : 53,
                            "thumbnailSlideDirection" : "horizontal", // horizontal or vertical
                            "hoverPause"              : "yes",
                            "autoRotate"              : "yes",
                            "fullScreenRotator"       : "no", // image dimensions work well at 1500x915
                            "fullScreenBreakpoints"   : ["960", "768", "640", "480", "320"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
                            "fullScreenVideo"         : "no",
                            "transitionType"          : "fade", // fade or slide
                            "transitionSpeed"         : 3,
                            "transitionDelay"         : 8,
                            "onImageLoad"             : function() { },
                            "allImagesLoaded"         : function() { },
                            "onThumbnailLoad"         : function() { },
                            "allThumbnailsLoaded"     : function() { },
                            "onTransitionStart"       : function() { },
                            "onTransitionEnd"         : function() { },
                            "watchVidClick"           : function() { },
                            "closeVidClick"           : function() { },
                            "allLoaded"               : function() {
                            	if($("#hp-slideshow .rs-gallery-control.next").length){
                            		$("#hp-slideshow .rs-gallery-control.play-pause").insertBefore("#hp-slideshow .rs-gallery-control.next");
                                }
                            } 
                });
        	}
     	});
	}

     // GLOBAL ICONS
    function globalIcons() {
      head.ready("globalIcons", function() {

            $("#gb-global-icons").creativeIcons({
                    "iconNum"       : "8", // NUMBER OF ICONS
                    "icons"         : [
                            ["Site Utility/8.png", "School Calendar", "/Page/2", "_self"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
                            ["Student Program/43.png", "School Meals", "http://www.aacps.org/nutrition", "_self"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
